INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (1, 'Purée de cresson', NULL, 15, 30, 0, 0, 'Préparation : Nettoyer le cresson et le hacher. Toutefois, garder quelques feuilles pour la décoration. Faire cuire les pommes de terre épluchées à l''eau salée, pendant 25 minutes. 15 minutes avant la fin de la cuisson des pommes de terre, jeter le cresson dans l''eau de cuisson. Quand les pommes de terre sont cuites, les égoutter, ainsi que le cresson. Faire fondre le beurre dans la casserole encore chaude, y jetter pommes de terre et cresson, les écraser à la fourchette. Rectifier l''assaisonnement, ajouter le lait, puis le jaune d''oeuf pour lier le tout. Ajouter ensuite la crème fraîche. Battre vigoureusement et servir bien chaud.', 387, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (2, 'Purée de choux de bruxelles', NULL, 10, 20, 0, 0, 'Préparation : Nettoyer les légumes en enlevant les feuilles flétries, mais sans couper la base sinon les feuilles se détacheraient en cours de cuisson. Laver les choux et les faire cuire à l''eau bouillante salée, pendant 15 minutes, jusqu''à ce qu''ils soient bien tendres. Les égoutter et les passer en purée. Pendant la cuisson, délayer la farine dans la moitié du beurre chauffé et la laisser cuire quelques instants. Mouiller avec le lait, faire épaissir, assaisonner, ajouter un peu de muscade râpée et incorporer cette petite sauce à la purée de choux. Ajouter ensuite le reste du beurre et battre fortement. Servir cette purée en accompagnement d''un plat de viande.', 402, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (3, 'Purée de céleri-rave', NULL, 10, 20, 0, 0, 'Préparation : Eplucher les céleris et les frotter immédiatement après avec du jus de citron. Les diviser en petits morceaux. Procéder de même pour les pommes de terre. Faire cuire les deux légumes ensemble, dans de l''eau, pendant environ 20 minutes, en ajoutant sel, poivre et une pincée de muscade. Egoutter les légumes cuits et les passer en purée fine. Y incorporer la crème, le beurre et rectifier l''assaisonnement. Parsemer de persil haché.', 471, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (4, 'Pudding de légumes d''hiver', NULL, 25, 120, 0, 0, 'Préparation : Nettoyer les légumes, les laver et les faire cuire dans des casseroles séparées, dans de l''eau en pleine ébullition. Arrêter la cuisson dès qu''ils deviennent tendres (5 minutes pour les épinards, 10 à 15 pour les poireaux, 20 à 25 pour le chou coupé en morceaux). Egoutter bien les légumes et les presser pour en extraire toute l''eau. Les passer à la moulinette. Couper le lard en dés, hacher les oignons et les faire revenir ensemble dans une poêle avec le beurre. Ils ne doivent pas prendre de la couleur. Quand les oignons sont transparents, ajouter les légumes hachés et laisser cuire 10 minutes à feu très doux. Ajouter les œufs battus en omelette et la crème. Saler, poivrer et râper une pointe de muscade. Mélanger bien. Verser cette préparation dans un plat à gratin et faire cuire à four modéré (4-5 au thermostat), jusqu''à ce que la préparation ne soit plus liquide. Servir très chaud.', 795, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (5, 'Potée aux laitues', NULL, 15, 30, 0, 0, 'Préparation : Laver la laitue, enlever les premières feuilles et découper la laitue en grosses lamelles. Eplucher les pommes de terre et les cuire 25 minutes à l''eau bouillante salée. Après 15 minutes de cuisson, jeter les morceaux de laitues dans l''eau des pommes de terre. Terminer la cuisson. Lorsque les pommes de terre et la salade sont cuites, les égoutter soigneusement. Faire fondre un peu de beurre dans une casserole, ajouter les légumes et un peu de lait. Saler et poivrer. Les laisser mijoter une dizaine de minutes. Les écraser au presse-purée ou les passer au mixer. Ajouter la crème, le jaune d''œuf et le fromage. Repasser au mixer, pour obtenir une pâte assez homogène. Servir bien chaud.', 678, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (6, 'Potée de primeurs', NULL, 20, 50, 0, 0, 'Préparation : Faire fondre le beurre dans une casserole, y ajouter les oignons épluchés et émincés. Nettoyer les petits pois, gratter les carottes et les découper en rondelles. Laver les épinards et nettoyer les haricots verts. Verser tous les légumes dans le beurre fondu et les laisser ramollir pendant 7 à 8 minutes. Ajouter un peu d''eau (à la hauteur des légumes), saler et laisser cuire à feu très doux, pendant 30 minutes. A part, cuire les pommes de terre nouvelles en pelure (30 minutes dans de l''eau bouillante salée). Eplucher les pommes de terre, les découper en rondelles puis les ajouter aux légumes. Laisser encore cuire 5 à 10 minutes, dans la casserole couverte. Poivrer légèrement avant de servir.', 528, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (7, 'Pommes de terre soufflées', NULL, 10, 10, 0, 0, 'Préparation : Eplucher les pommes de terre, laver et les essuyer, puis les couper en rondelles de 3 mm environ d''épaisseur. Les sécher dans un linge. Faire chauffer deux bassines de friture dont l''une doit être chaude, l''autre brûlante. Plonger une partie des rondelles dans la friture chaude et laisser-les cuire 7 à 8 minutes environ. Les égoutter vivement et les plonger aussitôt dans la friture bouillante, quelques secondes, le temps de dorer et de gonfler. Égoutter rapidement sur papier absorbant et saupoudrer de sel. Servir sans attendre.', 1315, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (8, 'Pommes de terre râpées à l''allemande', NULL, 15, 15, 0, 0, 'Préparation : Râper l''oignon épluché et lavé. Peler les pommes de terre lavées et les râper également. Y mélanger immédiatement l''oignon râpé, les herbes hachées, le sel, le poivre et l''œuf battu, en procédant rapidement pour éviter le noircissement des pommes de terre. Faire chauffer la moitié du beurre et de l''huile dans une grande poêle et y jeter, par petits tas espacés, de 2 cuillères à soupe, le mélange de pommes de terre. La préparation s''étale à la cuisson d''où la nécessité de bien espacer les tas. Laisser cuire et dorer à feu vif, 2 minutes de chaque côté, garder au chaud et continuer la cuisson jusqu''à épuisement de la pâte, en rajoutant beurre et huile. Servir ces galettes très chaudes, avec une salade ou en accompagnement d''une viande ou poisson.', 619, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (9, 'Pommes de terre aux oignons', NULL, 20, 25, 0, 0, 'Préparation : Faire cuire les pommes de terre à l''eau salée, dans leur pelure. Les peler et les couper en tranches égales. Eplucher les oignons, les émincer en rouelles et faire blondir celles-ci en sauteuse, dans le tiers du beurre et la moitié d''huile. Les enlever. Ajouter un autre tiers de beurre et le reste d''huile dans la sauteuse et y mettre dorer très légèrement les rondelles de pommes de terre, saler et poivrer. Faire fondre la moitié du reste de beurre dans une cocotte et y alterner les couches de pommes de terre et d''oignons. Arroser avec la cuisson des légumes déglacée avec un filet d''eau et additionnée du reste de beurre. Couvrir la cocotte et faire cuire à four moyen pendant 25 minutes environ. Servir dans la cocotte.', 665, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (39, 'Navets glacés', NULL, 15, 20, 0, 0, 'Préparation : Eplucher les navets et les couper en quatre ou en six selon grosseur. Les faire cuire dans de l''eau bouillante salée, jusqu''à ce qu''ils soient tendres, puis les égoutter. Les faire revenir doucement avec le beurre, dans une cocotte. Quand ils commencent à dorer, les saupoudrer avec le sucre. Laisser mijoter doucement, en remuant souvent la casserole, jusqu''à ce que les navets soient bien glacés.', 375, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (11, 'Pommes de terre frites', NULL, 20, 20, 0, 0, 'Préparation : Eplucher les pommes de terre, les essuyer, les couper en bâtonnets égaux et les sécher dans un linge propre. Faire chauffer la friture, mais sans excès, un bâtonnet de pomme de terre plongé dedans doit légèrement grésiller. Verser dans le panier à friture la quantité de pommes de terre conforme à la dimension de la friteuse pour cuire uniformément. Après 12 à 15 minutes de cuisson, les frirtes sont blondes, légèrement fripées et cuites à cœur. Les égoutter et les transvaser dans un récipient, en attente. Recommencer l''opération avec d''autres pommes de terre, jusqu''à épuisement de celles-ci. Avant de servir, augmenter la température de la friture, qui doit être brûlante, et y plonger les pommes de terre pendant quelques secondes, le temps de les dorer et de les rendre croustillantes. Les égoutter sur du papier absorbant et saupoudrer de sel. Les pommes allumettes sont des frites coupées en fins bâtonnets, les pommes pailles étant, elles, coupées encore plus finement. On obtient les pommes gaufrettes grâce à un appareil spécial, sorte de râpe, qui leur donne cette forme. Le mode de cuisson reste le même, seuls les temps diffèrent selon la dimension des bâtonnets et gaufrettes et leur épaisseur.', 1315, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (12, 'Pommes de terre au four', NULL, 15, 70, 0, 40, 'Préparation : Laver les pommes de terre en pelure et les plonger 10 minutes dans de l''eau bouillante salée. Les égoutter. Inciser en croix, à 1 cm de profondeur, le dessus des légumes. Les entourer ensuite de papier aluminium et les faire cuire à four chaud, pendant une heure environ. Travailler le beurre avec le persil, un peu de sel et du poivre. Le rouler ensuite en boudin, de 2,5 cm de diamètre environ, dans du papier aluminium. Le mettre au réfrigérateur. Lorsque les pommes de terre sont cuites, écarter le papier aluminium pour dégager le dessus. Avec un gant de cuisine, prendre chaque pomme de terre en main et la presser légèrement, à la base, après avoir enlevé la pelure de la surface. La pulpe forme une excroissance que vous couvrirez avec une rondelle du beurre préparé et durci au réfrigérateur. Servir les pommes de terre très chaudes, dans leur enveloppe de papier aluminium.', 489, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (13, 'Pommes de terre farcies à la viande', NULL, 25, 40, 0, 0, 'Préparation : Eplucher et laver les pommes de terre. Les plonger 5 minutes dans de l''eau bouillante salée et les égoutter. Avec une cuillère, les creuser pour former une cavité, en laissant un bon centimètre d''épaisseur tout autour. Nettoyer et hacher les oignons et les faire blondir et attendrir dans la moitié du beurre. Les ajouter à la viande hachée. Déglacer la cuisson des oignons avec un verre de bouillon et, hors du feu, y mettre tremper le pain. L’écraser ensuite et l’ajouter également à la viande ainsi que le persil, l''ail en poudre. Mélanger bien le tout et assaisonner. Farcir les barquettes de pommes de terre de cette préparation. Les mettre, bien serrées, dans un plat à gratin et poser sur chacune d''elles une tranche de lard coupée en deux. Ajouter le reste de beurre et faire cuire 40 minutes à four modéré, en arrosant fréquemment avec du bouillon.', 766, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (14, 'Pommes de terre farcies', NULL, 35, 50, 0, 0, 'Préparation : N''éplucher pas les pommes de terre, les laver et les faire cuire à l''eau bouillante, jusqu''à ce qu''elles soient tendres. Égoutter, puis les éponger. Les coucher dans un plat allant au four. Couper le dessus des pommes de terre et, avec une cuillère, les évider délicatement pour pouvoir les farcir. Laver les champignons rapidement à l''eau vinaigrée, couper le pied terreux, puis les émincer. Les faire revenir rapidement dans un peu de beurre. Quand ils ont rendu toute leur eau, y ajouter le jambon coupé en dés. Saler, poivrer et laisser cuire quelques minutes à feu doux. Réduire la chair des pommes de terre en purée fine. Ajouter du beurre, la crème, du sel, du poivre et une pointe de muscade. Mélanger bien le tout. Hors du feu, incorporer les œufs en tournant rapidement. La préparation doit être onctueuse. Ajouter ensuite le jambon et les champignons, puis remplir les pommes de terre évidées avec cette purée « améliorée». Déposer une noisette de beurre sur chaque pomme de terre et mettre à four chaud pendant une dizaine de minutes. Servir brûlant.', 727, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (15, 'Pomme de terre duchesse', NULL, 35, 25, 0, 0, 'Préparation : Peler les pommes de terre et les couper en morceaux que vous ferez cuire à l''eau salée et passer en purée. Y ajouter beurre, oeuf, jaunes d''œufs, sel et poivre et mettre cette purée dans une poche. Avec la douille cannelée, presser en petits tas sur la plaque du four, beurrée et farinée. Laisser un peu durcir avant de badigeonner les tas avec du blanc d''œuf légèrement battu. Faire dorer à four chaud pendant 5 minutes environ et disposer cette garniture sur le plat de service. Ou encore, presser la purée en cordon, directement sur le plat de service (allant au four), faire dorer et garnir ensuite l''intérieur du plat avec viande ou poisson, etc.', 726, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (18, 'Pommes chips', NULL, 15, 20, 0, 30, 'Préparation : Peler les pommes de terre et les couper en fines tranches de 2 mm d''épaisseur environ. Tremper ces tranches dans de l''eau froide, pendant une 1/2 heure, puis les égoutter et bien les essuyer. Les faire frire, par petite quantités à la fois, dans la friture très chaude, environ 2 à 3 minutes. Au fur et à mesure qu''elles sont cuites et dorées et parfois même soufflées, les égoutter et les étaler sur la plaque du four tapissée de papier absorbant ou d''un linge. Les garder au chaud, sans excès. Saler et servir immédiatement.', 1315, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (550, 'Asperges recette de base', NULL, 10, 20, 0, 0, NULL, 110, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (19, 'Pomme boulangère', NULL, 25, 60, 0, 0, 'Préparation : Laver, puis éplucher soigneusement les pommes de terre. Les rincer encore à l''eau claire pour éliminer l''excès de fécule. Les essuyer avec du papier de cuisine. Les couper en rondelles assez épaisses. Mettre le beurre dans un plat à gratin. Déposer une couche de pommes de terre dans le fond du plat, saupoudrer de la moitié des aromates. Saler, poivrer. Déposer une seconde couche de pommes de terre avec le reste des aromates et l''ail écrasé, Saler, poivrer. Parsemer de petite noix de beurre sur la surface et mettre le plat dans le four chaud (thermostat 6/7). Faire cuire pendant 1 heure environ. Servir avec de la viande rouge grillée.', 553, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (22, 'Poireaux aux oeufs durs', NULL, 20, 25, 0, 0, 'Préparation : Eplucher les poireaux en tranchant l''extrémité des blancs et en coupant les 2/3 de la verdure. Enlever ensuite les feuilles extérieures et, avec un couteau pointu, fendre profondément la partie de verdure attenant au blanc afin de faciliter l''élimination de la terre ou autres impuretés, insectes, vers pouvant s''y loger. Laver les légumes à plusieurs eaux. Faire cuire les poireaux dans de l''eau bouillante salée, de 25 à 35 minutes suivant grosseur. Les égoutter à fond et les garder au chaud. Pendant ce temps, cuire les oeufs durs, 10 à 12 minutes dans de l''eau bouillante salée, et les peler. Faire fondre le beurre dans une petite casserole, sans le laisser cuire, et ajouter de l''eau de la cuisson des poireaux, le jus de citron, le sel et le poivre. Hors du feu, battre cette sauce au fouet pour la rendre mousseuse et ajouter le persil haché. Servir les poireaux entourés des oeufs durs et présenter la sauce séparément. On écrase l''oeuf dans la sauce et on y trempe les morceaux de poireaux.', 762, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (23, 'Poireaux à la crème', NULL, 0, 10, 0, 0, 'Préparation : Prélever les blancs des poireaux et les laver soigneusement. Les faire cuire à l''eau bouillante salée, pendant 10 minutes. Les égoutter et les presser doucement, pour en extraire toute l''eau. Les mettre dans une casserole, arroser avec la crème, poivrer et achever la cuisson très lentement. Servir quand les poireaux sont tendres.', 623, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (25, 'Poireaux à l''anglaise', NULL, 10, 15, 0, 0, 'Préparation : Couper les feuilles vertes et les racines chevelues des poireaux. Ne garder que les blancs et les laver à l''eau courante pour en extraire toute la terre. Les faire cuire à l''eau bouillante salée, ou mieux à la vapeur, jusqu''à ce qu''ils soient tendres. Les égoutter et les presser doucement pour en extraire toute l''eau. Faire fondre le beurre dans une casserole et y rouler les poireaux. Laisser cuire quelques instants à feu très doux. Saler et poivrer. Servir les poireaux quand ils sont bien imprégnés de beurre. Saupoudrer abondamment de persil. Jetez les feuilles vertes flétries, laver les autres et les hacher pour en faire un potage.', 575, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (26, 'Piperade', NULL, 10, 45, 0, 0, 'Préparation : Faire fondre les poivrons découpés en fines lanières dans une cocotte contenant de l''huile. Laisser cuire 30 minutes à feu doux. Dans un poêlon, faire fondre les oignons hachés et l''ail écrasé dans le reste de l''huile. Ajouter les tomates pelées, épépinées, le sel, le poivre, le thym et le laurier. Laisser cuire à petit feu, pendant 15 minutes. Mélanger les 2 préparations. Casser les oeufs entiers, les battre dans un grand saladier, avec le poivre, le sel et le lait. Faire cuire votre omelette (baveuse ou bien dorée, selon les goûts). Couvrir l''omelette des légumes. La déposer sur un grand plat rond, servir vos convives en découpant des quartiers comme s''il s''agissait d''une tarte.', 334, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (27, 'Pieds de céleri au roquefort', NULL, 20, 0, 0, 0, 'Préparation : Nettoyer les céleris. Couper les feuilles. Couper les céleris en gros dés et les verser dans un saladier. Mélanger bien le roquefort et la crème, saler et poivrer. Ajouter la moutarde, le jus de citron et l''huile. Verser cette sauce sur les dés de légumes, saupoudrer de persil et de ciboulette. Bien mélanger. Servir frais, à l''apéritif, avec des petits bâtonnets en bois. Si vous présentez ce plat en entrée: ne coupez que les feuilles très étroites des céleris. Garder les branches assez larges (elles doivent avoir 10 cm de long environ). Farcir ces barquettes avec le mélange expliqué ci-dessus et les présenter sur des feuilles de salade. C''est un amusant « petit bateau » pour palais délicats.', 431, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (551, 'Artichauts roses', NULL, 10, 25, 120, 0, NULL, 606, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (559, 'Anguilles au vert', NULL, 15, 15, 0, 0, NULL, 799, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (29, 'Petits oignons glacés', NULL, 20, 15, 0, 0, 'Préparation : Une heure avant le début de la préparation, mettre les raisins sec à tremper dans un peu d''eau tiède. Eplucher les petits oignons et les laver. Les faire revenir à feu vif dans une grande poêle, avec l''huile, jusqu''à ce qu''ils soient blonds. Ensuite, ajouter le vin blanc, les graines de coriandre, les raisins secs égouttés, le thym, le laurier, le persil, le sel et le poivre. Faire cuire à feu vif. La cuisson est terminée quand les oignons sont tendres. Le jus doit avoir réduit au 3/4. Transvaser les oignons dans le plat de service et faire refroidir. Mettre au réfrigérateur et attendre le lendemain de préférence avant de servir. Remuer souvent les oignons pendant la cuisson. Si le jus s''évapore trop rapidement, ajouter un peu d''eau. Piquer les petits oignons avec la pointe d''un couteau pour vérifier qu''ils sont cuits à point. Vous pouvez ajouter 2 ou 3 morceaux de sucre à la cuisson des oignons pour réduire l''acidité.', 799, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (31, 'Oranges en salade', NULL, 15, 0, 60, 0, 'Préparation : Eplucher les oranges, enlever les pépins et la peau blanche. Eplucher les radis et les couper en petits morceaux. Mettre le sucre dans un saladier, ajoutez le sel et délayez le tout avec les jus de citrons. Jeter les radis et les oranges découpées en morceaux dans le saladier. Bien mélanger la salade et la déposer au moins 1 heure dans le réfrigérateur avant de servir. Cette salade originale peut se servir le soir avec des roulades de jambon ou des tartines de fromage blanc salé et poivré. Idéale pour les enfants. La servir en entrée, dans des coupes en verre ou dans des 1/2 avocats évidés.', 211, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (32, 'Mirepoix', NULL, 10, 30, 0, 0, 'Préparation : Laver et nettoyer les légumes, les couper en petits dés, émincer les oignons et les échalotes. Faire fondre le beurre dans une casserole à fond épais. Y faire revenir les légumes pendant 10 minutes environ. Saupoudrer de thym et de laurier en poudre. Ajouter le jambon coupé en petits dés. Laisser mijoter à petit feu, pendant 20 minutes, les légumes doivent rester légèrement croquants. Servir comme garniture avec de la viande rouge ou blanche, rôtie ou braisée, etc.', 820, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (33, 'Macédoine chaude de légumes', NULL, 20, 30, 0, 0, 'Préparation : Nettoyer et laver tous les légumes. Ecosser les petits pois. Couper tous les légumes en petits dés. Faire bouillir une grande casserole d''eau salée, y jeter les carottes et laisser cuire, pendant 10 minutes. Ajouter thym, laurier, sucre, sel. Ajouter les navets, les haricots et les petits pois aux carottes et laisser cuire encore 20 minutes, le tout. Egoutter les légumes. Ajouter le beurre et saupoudrer de persil finement haché. Cette macédoine accompagne toutes les viande rôties et grillées.', 763, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (34, 'Laitue à l''orange', NULL, 20, 0, 0, 0, 'Préparation : Laver et essuyer les oranges. Avec un couteau de cuisine à fine lame, découper les oranges en tranches très minces. Laver la laitue, conserver les feuilles entières. Dans un grand saladier, verser l''huile, le jus des citrons, le jus d''une orange, le sel et le poivre. Mélanger bien pour en faire une sorte de vinaigrette. Dresser les feuilles de salade comme pour la reconstituer. Glisser les rondelles d''oranges entre les feuilles. Au centre de chaque tranche d''oranges, déposer une petite olive. Déposer le plat au milieu de la table. Mélanger la salade seulement au moment de servir chaque convive.', 459, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (35, 'Laitues farcies', NULL, 15, 80, 0, 0, 'Préparation : Couper les premières feuilles des laitues. Les rincer. Les faire cuire 10 minutes dans de l''eau bouillante salée. Les égoutter soigneusement. Faire cuire votre riz, jusqu''à ce que le grain devienne tendre, mais avec une certaine fermeté (environ 18 minutes). Faire revenir vos champignons dans un peu de beurre (10 minutes). Les mélanger avec les œufs durs écrasés et le riz. Farcir l''intérieur de chaque laitue. Les ficeler délicatement. Placer les laitues dans un plat à gratin beurré. Les passer au four et les faire cuire 1 heure à feu doux (thermostat 3).', 440, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (38, 'Mousse de saumon fumé', NULL, 20, 5, 60, 0, 'Préparation : Préparer les moules en les garnissant d’une bande de papier blanc de 5 cm de plus que la hauteur du moule refermée par du papier collant. Huiler le papier à l’intérieur. Hacher le saumon en purée fine. Préparer la gelée en suivant les directives du fabricant et en dissolvant le contenu du sachet dans le liquide. Faire chauffer le beurre, y délayer la farine, mouiller avec le lait et laisser s''épaissir. Ajouter le jus de citron, les jaunes d''œufs battus avec la moitié de la crème, sel et poivre, le saumon puis la moitié de la gelée. Laisser tiédir puis incorporer les blancs d''œufs battus en neige avec un peu de sel. Verser la masse dans le moule préparé et laisser prendre quelques heures. Liquéfier le reste de la gelée et en enduire, au pinceau, le haut de la mousse. Décorer avec le reste de la crème, fouettée et pressée en bâtonnets le long du bord.', 585, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (40, 'Navets en purée', NULL, 15, 25, 0, 0, 'Préparation : Eplucher les navets, les laver et les couper en quatre. Les faire cuire dans de l''eau bouillante salée, jusqu''à ce qu''ils soient bien tendres. Les égoutter, puis les passer à la moulinette, les réduire en purée très fine. Ajouter le poivre, le beurre, la crème et le persil haché. Mélanger bien. Vous devez obtenir une purée onctueuse. Rajouter un peu de beurre au besoin. Arroser avec un peu de jus de citron. Goûter et rectifier l''assaisonnement. Servir très chaud.', 424, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (41, 'Nouilles aux épinards', NULL, 15, 15, 0, 0, 'Préparation : Nettoyer les épinards. Les faire blanchir 10 minutes dans de l''eau bouillante. Les égoutter et les hacher. Dans une casserole, faire fondre le saindoux. Ajouter les épinards, le sel, le poivre, la noix de muscade. Les faire étuver, pendant 5 minutes. Ajouter la crème fraîche et le gruyère. Mélanger bien. Faire cuire les pâtes à l''eau bouillante salée, ajouter 1 cuillère à soupe d''huile (environ 10 minutes). Egoutter les pâtes et les déposer dans un grand saladier. Déposer 1 morceau de beurre par-dessus. Hors du feu, délayer le jaune d''œuf dans les épinards et les verser sur les pâtes. Saupoudrer de gruyère et servir. Ces pâtes accompagnent l''agneau grillé, les viandes blanches et les œufs « mollets ».', 1433, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (42, 'Nouilles à l''estragon', NULL, 5, 15, 0, 0, 'Préparation : Jeter les nouilles dans une grande casserole d''eau bouillante salée ajouter 1 cube de bouillon de viande. Laisser cuire à petits bouillons, pendant 10 à 12 minutes (comme indiqué sur le paquet de pates). Pendant ce temps, dans une petite casserole, faire chauffer le vin blanc. Amener à ébullition, puis y jeter les feuilles d''estragon. Couper la source de chaleur, couvrir la casserole et laisser infuser. Egoutter les nouilles, ajouter 1 noix de beurre. Mettre le reste du beurre dans l''estragon. Ajouter la crème à l''estragon et napper les pâtes avec cette sauce. Ces nouilles accompagnent parfaitement des côtelettes d''agneau grillées, des viandes rouges grillées.', 1872, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (43, 'Concombre farci au thon', NULL, 20, 15, 20, 20, 'Préparation : Couper le concombre en deux, dans sa longueur, enlever les graines, puis la chair, avec une petite cuillère, et la diviser en dés. Saupoudrer de sel l''intérieur des demi-concombres, que vous retournerez sur un plat, ainsi que les dés de la chair du concombre. Hacher l''oignon pelé et le faire blondir dans l''huile. Y ajouter les tomates, pelées et épépinées, sel, poivre, une pincée de sucre et une de basilic ou de pistou. Laisser cuire à découvert pour obtenir une fondue épaisse. Mettre à refroidir. Effeuiller le thon à la fourchette. Ecraser les œufs durs pelés. Essuyer l''intérieur du concombre avec du papier absorbant ainsi que les dés de concombre. Les mélanger avec thon, œufs écrasés et fondue de tomate refroidie, du jus de citron. Farcir les demi-concombres de cette préparation et servir bien frais, avec garniture de laitue. Le poisson en boîte peut être remplacé par du poisson frais, poché, ou encore des crevettes ou du crabe.  ', 265, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (44, 'Concombres aux crevettes', NULL, 20, 0, 0, 0, 'Préparation : Ne pas éplucher les concombres, mais les laver et les couper en tronçons. Les vider à l''aide d''une cuillère, mais ne pas en enlever trop. Dans le bol, mélanger les crevettes, la ciboulette et 4 feuilles de laitue coupées en lanières, l''œuf cuit dur coupé en morceaux, du sel, du poivre et la mayonnaise. Dresser les tronçons de concombre sur les feuilles de laitue et les garnir de la sauce aux crevettes. Servir frais, saupoudré de ciboulette.  ', 552, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (46, 'Choux de Bruxelles à la paysanne', NULL, 15, 35, 0, 0, 'Préparation : Enlever les feuilles flétries des petits choux et gratter la base, sans la couper. Laver les légumes et les cuire 20 minutes environ dans de l''eau bouillante à peine salée. Le lard étant fortement salé, mieux vaut cuire les choux en les assaisonnant à peine. Couper le lard en gros dés et les faire rissoler dans le saindoux, dans une sauteuse. Y ajouter les oignons, pelés et émincés, jusqu''à ce qu''ils commencent à dorer, puis les choux égouttés. Laisser cuire le tout, en remuant, 10 à 15 minutes environ, jusqu''à ce que les choux soient cuits et braisés. Rectifier éventuellement l''assaisonnement avec le sel et ajouter du poivre, le vinaigre, 1 pincée de muscade et les câpres. Bien mélanger.  ', 776, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (47, 'Choux de Bruxelles braisés', NULL, 15, 20, 0, 0, 'Préparation : Enlever les feuilles extérieures des petits choux et couper la base du trognon. Les faire blanchir 2 minutes dans de l''eau en pleine ébullition. Les égoutter et les mettre dans une cocotte avec le beurre, du sel et du poivre. Les faire braiser à petit feu, sans que le beurre brunisse, jusqu''à ce qu''ils soient tendres. Servir très chaud.  ', 426, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (48, 'Chou rouge au vin', NULL, 15, 120, 0, 0, 'Préparation : Hacher les oignons et couper le lard en lardons. Les faire revenir doucement, avec le beurre, dans une grande cocotte. Couper le chou en quatre. Le laver. Oter le trognon et les grosses côtes, puis couper les feuilles en lanières. Arroser avec le vin rouge. Saler (peu, à cause du lard). Poivrer. Laisser cuire à couvert pendant 1 1 / 2 heure à 2 heures. S''il restait trop de liquide dans la cocotte le chou étant cuit, le faire évaporer à feu vif. Servir très chaud.  ', 349, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (49, 'Chou rouge braisé aux pommes', NULL, 15, 90, 0, 0, 'Préparation : Couper le chou en quatre. Oter le trognon et les grosses côtes. Couper les feuilles en lanières. Mettre le beurre dans une cocotte, ajouter le chou en lanières, un doigt d''eau et le vinaigre. Saler et poivrer, puis couvrir et faire cuire à feu doux pendant 1 heure 30 minutes environ. 30 mn avant la fin de la cuisson, ajouter les pommes pelées et coupées en quatre, avec le sucre en poudre. Quand le chou est cuit, faire évaporer le liquide restant dans la casserole. Servir très chaud. Vous pouvez sucrer plus ou moins selon votre goût. Ce plat de légume accompagne parfaitement la viande de porc, la saucisse et tous les boudins chauds.  ', 292, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (50, 'Chou-fleur aux oeufs durs', NULL, 15, 25, 0, 0, 'Préparation : Oter les feuilles et couper le trognon du chou-fleur, séparer les bouquets. Les faire cuire à l''eau bouillante salée ou à la vapeur, jusqu''à ce qu''ils soient tendres. Pendant ce temps, hacher les œufs durs et le persil et faire revenir la chapelure dans la moitié du beurre. Egoutter les bouquets de chou-fleur et les ranger dans un plat allant au four. Les saupoudrer avec les œufs durs et le persil hachés. Recouvrir avec la chapelure dorée. Arroser avec le reste du beurre fondu. Faire dorer 5 minutes sous le grill. Servir immédiatement.  ', 569, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (552, 'Artichauts printaniers', NULL, 20, 35, 0, 0, NULL, 489, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (560, 'Ananas glacé gastronomique', NULL, 20, 10, 0, 0, 'Préparation :', 937, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (52, 'Chou-fleur au gratin', NULL, 20, 45, 0, 0, 'Préparation : Prélever les bouquets du chou-fleur, les laver et les faire cuire à l''eau bouillante salée. Faire fondre le beurre dans un poêlon, le saupoudrer avec la farine et laisser cuire quelques instant en mélangeant. Mouiller avec le lait et tourner jusqu''à ce que la sauce épaississe. Assaisonner avec sel, poivre, muscade et un filet de jus de citron. Hors du feu, incorporer les 2/3 du fromage râpé. Goûter et rectifier l''assaisonnement. Ajouter la crème. Egoutter les bouquets de chou-fleur et les ranger dans un plat à gratin. Les napper avec la béchamel et saupoudrer avec le reste de fromage râpé. Faire grâtiner 15 minutes à four modéré. Servir très chaud.  ', 522, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (53, 'Chou-fleur aux croutons', NULL, 10, 25, 0, 0, 'Préparation : Oter les feuilles et couper le trognon du chou-fleur. En séparer les bouquets. Les faire cuire à l''eau bouillante salée — ou mieux, à la vapeur — jusqu''à ce qu''ils soient tendres. Pendant ce temps, couper le pain de mie en petits dés et les faire rôtir à la poêle dans un peu d''huile. Surveiller la cuisson des croûtons: il ne faut pas qu''ils brunissent trop. Egoutter le chou-fleur, le déposer dans le plat de service et l''arroser de beurre fondu. Poivrer légèrement. Parsemer de petits croûtons et servir abondamment persillé.  ', 422, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (54, 'Chou-fleur à l''ancienne', NULL, 15, 30, 0, 0, 'Préparation : Laver les pommes de terre et les faire cuire dans leur pelure, à l''eau salée. Les peler et les couper en tranches. Couper le trognon du chou-fleur, enlever la verdure, les grosses côtes et le diviser en bouquets. Les laver dans de l''eau vinaigrée. Peler les oignons. Mettre les bouquets de chou-fleur dans une casserole d''eau bouillante salée et vinaigrée, y ajouter les oignons émincés et laisser cuire 15 à 20 minutes. Les égoutter. Dans un grand plat creux, alterner des couches de tranches de pommes de terre et de bouquets de chou-fleur et oignons. Garder au chaud. Préparer la sauce en délayant la farine dans le beurre chauffé. Mouiller avec le lait et un peu d''eau de cuisson des légumes, ajouter le vinaigre à l''estragon et laisser épaissir. Rectifier l''assaisonnement. Verser cette sauce sur les légumes gardés au chaud et parsemer de persil haché. Servir dans le plat.  ', 340, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (55, 'Chou blanc en purée', NULL, 20, 30, 0, 0, 'Préparation : Oter les feuilles extérieures du chou. Le couper en quatre et enlever le trognon sur toute sa hauteur. Faire blanchir le chou 5 minutes dans de l''eau salée en ébullition. Le passer ensuite sous l''eau froide courante, puis l''égoutter. Séparer les feuilles les unes des autres et achever leur cuisson à la casserole, avec les oignons hachés, le thym et le laurier. Mouiller d''eau à mi-hauteur et faire cuire à petit feu, jusqu''à ce que le chou soit tendre. Egoutter et presser bien les feuilles, pour en faire sortir toute l''eau. Passer le chou et les oignons à la moulinette. Ajouter le beurre, la crème fraîche, du sel, du poivre, un peu de muscade. Bien mélanger. La purée doit être fine et onctueuse. Réchauffer à feu très doux, en mélangeant. Servir très chaud.  ', 454, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (58, 'Champignons moutardière', NULL, 10, 10, 0, 0, 'Préparation : Laver les champignons et couper le bout des queues. Presser le citron dans une petite casserole et y mettre les champignons, saler et poivrer. Faire chauffer à feu vif d''abord, puis réduire, les champignons doivent donner toute leur eau. Ajouter une noix de beurre en cours de cuisson. Faire blondir l''ail et les échalotes hachées grossièrement, ajouter les champignons. Délayer la crème et la moutarde dans un bol, ajouter le vin blanc, rectifier l''assaisonnement, mouiller d''un peu de jus de cuisson et verser sur les champignons, remuer vivement. Verser dans le plat de service et saupoudrer de persil.  ', 611, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (60, 'Champignons forestière', NULL, 10, 5, 0, 0, 'Préparation : Laver les champignons rapidement, dans de l''eau vinaigrée, couper le bout des pieds et les émincer. Les faire sauter rapidement dans du beurre, avec la moitié d''ail finement hachée. Quand ils sont dorés, saler et poivrer, puis ajouter le persil haché avec le reste d''ail. Faire sauter quelques instant, puis servir.  ', 503, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (561, 'Ananas glacé', NULL, 20, 0, 120, 0, NULL, 917, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (63, 'Champignons crus au citron', NULL, 10, 0, 120, 0, 'Préparation : Couper le pied des champignons, puis laver ceux-ci dans de l''eau vinaigrée. Les égoutter, puis les émincer, finement, dans une terrine. Les arroser avec le jus des citrons et ajouter les échalotes finement hachées, les graines de coriandre, une grosse pincée de sel et beaucoup de poivre. Laisser mariner 1 ou 2 heures au réfrigérateur. Avant de servir, ajouter l''huile d''olive. Bien mélanger et saupoudrer abondamment de persil haché.  ', 383, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (64, 'Champignons à la crème', NULL, 10, 10, 0, 0, 'Préparation : Laver les champignons rapidement à l''eau vinaigrée. Couper le bout des pieds et les émincer ou les couper en quatre, selon grosseur. Faire fondre le beurre dans une grande poêle ou dans une sauteuse et y faire revenir les champignons à feu modéré, avec les échalotes et l''ail hachés. Quand toute l''eau des champignons s''est évaporée, et que les échalotes sont transparentes, ajouter la crème. Saler et poivrer et laisser mijoter doucement une dizaine de minutes avant de servir.  ', 622, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (66, 'Céleri en purée', NULL, 15, 45, 0, 0, 'Préparation : Eplucher les céleris et les couper en morceaux. Les faire cuire dans une grande casserole d''eau bouillante salée dans laquelle vous aurez ajouté le jus du citron. Quand le céleri cuit depuis 15 minutes, ajouter les pommes de terre épluchées et coupées en quatre. Laisser cuire 30 minutes environ: le céleri doit être très tendre. Egoutter les légumes et les passer à la moulinette. Ajouter le lait et le beurre. Poivrer. Ajouter une pointe de muscade. Travailler la purée avec une cuillère en bois, jusqu''à ce qu''elle soit onctueuse. Juste avant de servir, hors du feu, incorporer les œufs, en mélangeant bien. Saupoudrer de persil et servir.  ', 407, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (67, 'Céleri au gratin', NULL, 10, 45, 0, 0, 'Préparation : Couper les côtes et les feuilles de céleris. Garder uniquement les cœurs à la base. Les laver et les faire cuire à l''eau bouillante salée. Arrêter la cuisson dès qu''ils sont tendres et les égoutter. Faire fondre le beurre dans un poêlon. Le saupoudrer avec la farine et laisser cuire quelques instants. Mouiller avec le lait et laisser cuire en tournant, jusqu''à ce que la sauce épaississe. Assaisonner avec sel, poivre, muscade et un filet de jus de citron. Incorporer la moitié du fromage râpé. Goûter et rectifier l''assaisonnement. Couper les pieds de céleri en deux et les ranger dans un plat à gratin. Napper avec la sauce. Saupoudrer de fromage râpé et faire gratiner à four modéré pendant 30 mn environ. Servir très chaud. Servir cette préparation en accompagnement d''une viande rôtie ou grillée, ou comme plat du soir.  ', 386, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (68, 'Céleri braisé', NULL, 10, 75, 0, 0, 'Préparation : Couper les feuilles et la partie supérieure des côtes des pieds de céleri. Les laver. Les faire blanchir à l''eau bouillante salée, pendant 15 minutes. Les retirer et les égoutter. Les couper en deux et les déposer dans une casserole avec le beurre, le thym, du sel et du poivre. Couvrir et faire mijoter à feu très doux, pendant 1 heure au moins, jusqu''à ce que les cœurs de céleris soient tout à fait tendres. Secouer la casserole de temps en temps, pour bien enrober les légumes de beurre. Servir très chaud. Servir ce plat de légumes en accompagnement d''une viande blanche.  ', 290, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (69, 'Cassolettes d''échalotes', NULL, 10, 15, 0, 0, 'Préparation : Nettoyer les oignons, les échalotes et la gousse d''ail. Les couper en très petits morceaux et faire fondre ces légumes dans le beurre. Assaisonner et ajouter une poignée de cayenne. Faire griller les tranches de pain de mie et les déposer au fond des cassolettes (1 par personne). Ne pas laisser brunir les légumes et, si vous trouvez votre sauce trop liquide, la lier avec un peu de fécule. Ajouter la crème aux légumes et les verser sur les toasts. Mettre quelques minutes à four chaud. Saupoudrer de persil et servir aussitôt.  ', 593, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (403, 'Fonds d''artichauts périgourdine', NULL, 5, 0, 15, 0, 'Préparation : Mélanger les jaunes d''œufs avec la purée de foie, la crème, le sel et le poivre. Placer la farce, en dôme, sur les fonds d''artichauts et poser chacun de ceux-ci sur une feuille de laitue. Décorer avec les truffes découpées en lamelles ou les olives noires. Servir très frais.  ', 530, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (554, 'Artichauts grand-mère', NULL, 20, 75, 0, 0, NULL, 543, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (562, 'Ananas farcis de crème glacée', NULL, 30, 10, 120, 0, NULL, 559, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (70, 'Carottes rapées aux olives', NULL, 10, 10, 60, 0, 'Préparation : Gratter les carottes et les râper, les verser dans un saladier. Y ajouter le jus de citron. Faire cuire l''œuf 10 minutes pour qu''il soit dur, le passer sous l''eau froide, l''écaler, séparer le blanc du jaune. Ajouter le blanc, en petits morceaux, aux carottes. Ecraser le jaune et le mélanger à la moutarde, ajouter l''huile, sel et poivre. Dénoyauter les olives et les ajouter aux carottes. Verser la sauce par dessus et conserver au frais 1 heure. Saupoudrer de persil et de ciboulette hachés au moment de servir.  ', 513, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (72, 'Carottes en purée', NULL, 0, 25, 0, 0, 'Préparation : Gratter les carottes, les laver, les couper en 2 ou en 3, puis les faire cuire à l''eau bouillante salée, jusqu''à ce qu''elles soient bien tendres. Eplucher les pommes de terre, les couper en quatre et les faire cuire dans l''eau des carottes. Les ajouter seulement quand les carottes cuisent depuis 30 mn déjà. Egoutter bien les légumes, puis les passer à la moulinette. Vous devez obtenir une purée très fine. Ajouter le beurre, le persil haché et poivrer. Mélanger bien. La purée doit être onctueuse: au besoin, ajouter un peu de beurre. Goûter et rectifier l''assaisonnement. Décorer avec un brin de persil au milieu et servir.  ', 412, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (73, 'Haricots verts aux tomates', NULL, 20, 30, 0, 0, 'Préparation : Eplucher et effiler les haricots, les laver, les égoutter et les couper en deux. Les plonger dans de l''eau bouillante salée et les faire cuire à découvert environ 20 minutes, jusqu''à ce qu''ils soient tendres, mais sans se défaire. D''autre part, faire chauffer l''huile dans une casserole et y mettre à blondir le lard coupé en dés et l''oignon haché. Y ajouter les tomates pelées et épépinées, coupées en morceaux, l''ail haché, sel, poivre, sucre, puis les haricots égouttés. Laisser mijoter, en remuant, pendant environ 10 minutes. Parsemer de persil haché.  ', 1244, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (74, 'Gratin suisse', NULL, 15, 120, 0, 0, 'Préparation : Eplucher les pommes de terre. les laver. Les égoutter et les sécher dans un linge propre. Les couper en rondelles très fines. Les mettre dans un grand saladier. Ajouter les 3/4 du fromage, le thym, le sel, le poivre et la muscade et mélanger avec les mains pour bien répartir les épices. Ranger les pommes de terre dans un plat à gratin assez profond. L''arroser avec le bouillon, juste à hauteur de la dernière couche. Saupoudrer avec le reste de fromage râpé, parsemer de noisettes de beurre et mettre à four modéré (thermostat 5 environ). Laisser cuire lentement pendant 2 heures au moins. Servir très chaud.  ', 561, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (75, 'Gratin piémontais', NULL, 15, 120, 0, 0, 'Préparation : Faire revenir doucement les oignons hachés fins dans la moitié du beurre. Laver les pommes de terre et les couper en fines rondelles. Les mettre dans un grand saladier. Ajouter la crème, le parmesan, le sel, le poivre moulu et un peu de muscade. Mélanger avec les mains pour bien répartir le tout. Dans un plat à gratin assez profond, disposer les pommes de terre en 3 couches, séparées par deux couches d''oignons. Arroser avec le lait, à hauteur de la dernière couche. Parsemer de noisettes de beurre. Mettre à four modéré (thermostat 5) et faire cuire lentement pendant 2 heures. Servir très chaud et bien doré.  ', 512, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (76, 'Gratin dauphinois', NULL, 15, 120, 0, 0, 'Préparation : Eplucher les pommes de terre. Les laver et les essuyer dans un linge propre. Les couper en rondelles très fines. Les mettre dans un grand saladier, ajouter le sel, et le poivre moulu, un peu de muscade. Mélanger avec les mains pour que les épices soient bien réparties. Ranger en couches successives dans un plat à gratin assez profond. Parsemer chaque couche de pommes de terre de gruyère. Arroser avec le lait à hauteur des pommes de terre. Parsemer de noisettes de beurre et faire cuire à four modéré (thermostat 5 environ), pendant 2 heures. Ajouter un peu de liquide au besoin. Une 1/2 heure avant la fin de la cuisson, saupoudrer de fromage râpé et hausser un peu la température du four pour faire gratiner.  ', 518, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (77, 'Gâteau d''épinards', NULL, 15, 35, 0, 0, 'Préparation : Nettoyer les épinards. Les laver dans plusieurs eaux mais rapidement pour ne pas « noyer » les vitamines du légume. Couper les épinards sur une planche en bois, avec un bon couteau. Dans un grand saladier, verser la farine et les œufs, le beurre légèrement fondu, la crème fraîche, le fromage blanc, sel, poivre et muscade râpée. Travailler cette pâte (comme s''il s''agissait d''une pâte à tarte). Ajouter les épinards. Bien mélanger. Beurrer un plat à gratin. Répartir la pâte dans le plat. Déposer dans un four moyen (6 au thermostat). Laisser cuire pendant 35 minutes, en surveillant la cuisson. Couper la source de chaleur dès qu''une croûte dorée recouvre le gâteau. Le laisser encore quelques minutes dans le four avant de le servir chaud avec une côte de veau par exemple.  ', 955, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (78, 'Galettes de pommes de terre', NULL, 10, 20, 0, 0, 'Préparation : Laver et éplucher finement les pommes de terre. Les essuyer avec du papier de cuisine. Les couper en grosses tranches. Jeter les tranches de pommes de terre dans une grande casserole d''eau bouillante salée. Faire bouillir 1 à 2 minutes à gros bouillons. Les égoutter soigneusement. Dans une petite poêle, mettre le beurre ou le saindoux. Lorsqu''il est fondu, déposer une couche de pommes de terre, les tasser. Laisser cuire jusqu''à ce que la couche devienne « galette » à peine dorée. Saler et poivrer. Couvrir la poêle et attendre quelques minutes à feu très doux. Retourner la galette. Faire dorer l''autre face. Déposer quelques pincées de gruyère râpé sur chaque côté. Couvrir à nouveau pendant 10 minutes. Garder la galette au chaud et recommencer l''opération avec une autre couche de pommes de terre. Ces galettes accompagnent d''une façon originale toutes les viandes rouges grillées, les salades vertes.  ', 539, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (149, 'Meringue', NULL, 10, 60, 0, 0, 'Préparation : Battre les blancs d''œufs en neige très ferme, avec le sel. et y ajouter peu à peu le sucre, sans cesser de battre. La masse doit être assez ferme pour ne pas retomber. Sur la plaque du four,poser un papier huilé et fariné sans excès. Renverser la plaque pour en secouer l''excédent. A l''aide d''une cuillère à soupe, disposer sur le papier des petits tas de meringue. Mettre dans le four chaud, préchauffé, et immédiatement abaisser la température à 100° maximum (très doux). Laisser durcir les meringues pendant une petite heure, jusqu''à ce qu''elles soient croquantes tout en restant un peu moelleuse à l''intérieur.', 1348, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (80, 'Flans d''épinards aux champignons', NULL, 20, 30, 0, 0, 'Préparation : Faire égoutter à fond les épinards cuits, hachés. Couper la partie sableuse des pieds de champignons, préalablement lavés. Hacher grossièrement têtes et pieds. Peler les échalotes, les hacher finement et les faire blondir dans le beurre pendant 2 minutes. Y ajouter le hachis de champignons, sel, poivre et continuer la cuisson jusqu''à ce que les champignons aient exsudé toute leur eau. Faire chauffer le bouillon avec la fécule délayée. Battre les œufs en mousse et y incorporer, sans cesser de battre, le bouillon, puis la crème également chauffée au préalable. Ajouter ensuite les épinards, le hachis de champignons et échalotes, la muscade et rectifier l''assaisonnement. Beurrer des moules à darioles ou à flan individuels et y répartir la préparation. Mettre les moules dans un bain-marie et faire cuire au four modéré, 20 à 25 minutes environ, jusqu''à ce que ces flans soient pris. Démouler et napper d''une sauce béchamel au fromage.  ', 564, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (81, 'Fenouil à la polonaise', NULL, 15, 50, 0, 0, 'Préparation : Eplucher le fenouil, le faire cuire dans une eau salée, pendant 45 minutes. L''égoutter. Le découper en lanières. Faire cuire les œufs. Les écaler. Passer les œufs sous un jet d''eau froide. Ecraser les œufs à la fourchette, ajouter la chapelure. Dans la poêle, faire fondre le beurre. Y passer rapidement les morceaux de fenouil. Assaisonner. Ajouter la crème, puis déposer le fenouil sur un plat. Saupoudrer d''œufs et de pain émietté, de persil haché. Servir bien chaud.', 442, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (83, 'Fenouil au beurre', NULL, 10, 50, 0, 0, 'Préparation : Couper les tiges des bulbes de fenouil, recouper légèrement la base et ôter éventuellement les feuilles abîmées. Les faire blanchir à l''eau bouillante citronnée et salée, pendant 15 à 20 minutes. Les égoutter et couper chaque bulbe en deux. Les ranger dans un plat allant au four. Poivrer légèrement et arroser avec le beurre fondu. Faire cuire à four doux pendant 30 minutes environ, en arrosant souvent. Servir abondamment saupoudré de persil haché.', 510, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (84, 'éventails d''aubergines frites', NULL, 10, 15, 0, 0, 'Préparation : Laver les aubergines et les sécher. Les découper en lamelles de 3 mm d''épaisseur, dans le sens de la longueur de la partie renflée vers le bout de la tige, en vous arrêtant à 3 cm de celle-ci. En prenant soin de ne pas les briser, frotter chaque tranche avec un peu de sel, puis les fariner. Une à une, les faire frire 5 minutes en les plongeant dans l''huile très chaude. Prendre soin de bien étaler les tranches en éventail dans le panier de la friteuse. Les égoutter quelques instants sur du papier absorbant et, au fur et à mesure, les réserver au chaud (à four doux par exemple). Servir en accompagnement d''un gigot ou de côtes d''agneau.', 357, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (85, 'épinards à l''italienne', NULL, 20, 30, 0, 0, 'Préparation : Oter les tiges des épinards et éliminer les feuilles abimées. Les laver dans beaucoup d''eau. Les faire blanchir 1 minute dans de l''eau salée en ébullition. Les égoutter en les pressant bien pour en extraire toute l''eau. Faire fondre le beurre dans une cocotte et y faire fondre le lard coupé en lardons. Quand ils commencent à dorer, ajouter les épinards, la gousse d''ail écrasée, du sel et du poivre. Couvrir et laisser braiser à petit feu, pendant 1 /4 d''heure. Quand les épinards sont cuits, Les hacher grossièrement avec un couteau, puis les mélanger aux œufs battus en omelette avec le lait, le gruyère, du sel et du poivre. Verser ce mélange dans un plat à gratin, couvrir de lamelles de gruyère et mettre à four chaud pendant 20 minutes environ. Servir quand le gratin est bien doré.', 372, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (86, 'épinards au gratin', NULL, 20, 35, 0, 0, 'Préparation : Oter les tiges des épinards et éliminer les feuilles abîmées. Les laver. Les faire blanchir 1 minute dans l''eau salée en ébullition. Les égoutter en les pressant bien contre la paroi de la passoire, pour en extraire toute l''eau. Faire fondre le beurre dans un poêlon, saupoudrer avec la farine et laisser cuire quelques instants en mélangeant. Mouiller avec le lait et tourner, jusqu''à ce que la sauce épaississe. Assaisonner avec sel, poivre, muscade et un filet de jus de citron. Incorporer les 2/3 du fromage râpé à cette sauce, puis ajouter les épinards en branches. Verser le mélange dans un plat à gratin, saupoudrer avec le reste de fromage et faire gratiner à four modéré, pendant 30 minutes environ. Servir immédiatement.', 274, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (87, 'Endives braisees', NULL, 10, 40, 0, 0, 'Préparation : Oter les feuilles abîmées des endives. Avec un petit couteau pointu, creuser le pied qui est souvent amer. Les laver soigneusement pour les débarrasser de leur sable. Les essuyer. Faire fondre le beurre dans une casserole et y ajouter les légumes. Arroser avec un doigt d''eau et le jus d''un demi-citron qui combattra l''amertume. Couvrir et laisser cuire, à feu très doux 30 à 60 minutes, selon grosseur.', 338, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (229, 'Potage fermière', NULL, 15, 90, 0, 0, 'Préparation : Eplucher carottes, oignons, poireaux et les tailler en julienne. Mettre ces bâtonnets de légumes dans une marmite, avec le beurre, y ajouter les pois et laisser étuver doucement pendant 5 minutes. Couvrir avec l''eau chaude, additionnée de cubes d''extrait de viande, ajouter laurier, racine de persil, sel et poivre et laisser mijoter 50 minutes. Ajouter alors les pommes de terre épluchées et coupées en petits dés, et continuer la cuisson pendant 20 minutes. Retirer la feuille de laurier et la racine de persil et incorporer la crème et les pluches de cerfeuil.', 195, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (88, 'Courgettes aux tomates', NULL, 20, 30, 0, 60, 'Préparation : Si les courgettes sont jeunes et fraîches, ne pas les éplucher et gratter légèrement leur pelure avec le couteau éplucheur. Sinon enlever la peau verte et dure qui les entoure. Les couper en tranches de 1/2 cm d''épaisseur, les parsemer de sel, les mettre dans une passoire et les laisser égoutter, 1 à 2 heures. Peler et épépiner les tomates. Les couper en morceaux. Eplucher la gousse d''ail. Peler les échalotes, les hacher et les mettre à blondir dans l''huile. Eponger les tranches de courgettes, les enrober de farine et les faire frire rapidement dans l''huile, avec les échalotes, le temps de les dorer. Les poivrer et les égoutter. Les remplacer par les tomates. Les laisser fondre en coulis épais, avec sel, poivre, l''ail haché. Dans un plat à four huilé, mettre la moitié du coulis de tomates parsemé d''une pincée d''herbes de Provence, puis les courgettes et échalotes, le reste des tomates. Mélanger la mie de pain finement émiettée et le fromage et en parsemer la couche de tomates. Disposer des parcelles de beurre et faire cuire à four moyen 25 à 30 minutes.', 223, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (89, 'Courgettes à ma façon', NULL, 10, 60, 0, 0, 'Préparation : Dans une cocotte à fond épais, faire chauffer l''huile. Eplucher et laver les courgettes, les tomates, éplucher et couper l''oignon en dés. Mettre l''oignon coupé dans l''huile et le laisser blondir. Verser alors les courgettes coupées en dés, les tomates en morceaux, saler et poivrer, ajouter l''ail. Laisser mijoter sur feu doux pendant 1 heure.', 229, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (90, 'Carottes glacées', NULL, 10, 20, 0, 0, NULL, 425, NULL);
