INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (99, 'Pissaladière', NULL, 10, 40, 0, 0, 'Préparation : Préparer la pâte à pain et l’aplatir en forme de tarte, en façonnant de gros bords. Couper les oignons en rondelles et les faire dorer cinq minutes à l''huile, avec les clous de girofle, le sel, le poivre et la sauge. Enlever les oignons à l''écumoire, éliminer le girofle. Mettre dans la poêle les tomates pelées, épépinées, concassées et l''ail haché. Saler, poivrer et laisser réduire en purée. Sur le fond de pâte étaler les oignons refroidis, les napper de la purée de tomates également refroidie. Disposer dessus, en croisillons, les filets d''anchois et garnir d''olives. Faire cuire 25 à 30 minutes à four moyen.', 597, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (100, 'Pâté de soles aux avocats', NULL, 30, 60, 0, 0, 'Préparation : Parer les filets de soles et les broyer au mixer, ajouter les oeufs entiers, le sel, un peu de poivre blanc moulu, le beurre et mélangez en purée fine et bien homogène. Mettre la purée dans un bol et incorporer la crème, peu à peu et en battant fortement. Rectifier l''assaisonnement. Couper les avocats en deux, enlever le gros noyau et retirer la chair avec une petite cuillère ronde pour obtenir des boules égales. Les arroser de jus de citron. Beurrer une grande terrine rectangulaire et tasser la moitié de la farce de poisson. Poser dessus, au centre, les boules de chair d''avocat et couvrir avec le reste de la purée de poisson. Couvrir la terrine et faire cuire dans un bain-marie pendant 1 h environ, à four modéré. Vers la fin de la cuisson, préparer la sauce: battre les jaunes d''oeufs avec le jus de citron et le vin blanc, dans une petite casserole posée sur feu doux. Laisser épaissir, sans cesser de battre et sans atteindre l''ébullition. Incorporer peu à peu le beurre, en fouettant toujours et en surveillant l''émulsion. Au besoin, retirer de temps à autre la casserole du feu. Lorsque tout le beurre est incorporé, ajouter le persil et rectifier l''assaisonnement. Démouler le pâté, le découper en tranches. Accompagner de la sauce.', 956, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (102, 'Pâté de lapin', NULL, 60, 240, 0, 2880, 'Préparation : Faire mariner le lapin pendant 2 jours avec le vin blanc, les oignons coupés en rondelles, la gousse d''ail épluchée, les épices et le persil en branches. Quand le lapin a bien mariné, le mettre dans une casserole avec le veau, le porc et le lard. Ajouter le vin blanc et la marinade et laisser cuire pendant 3 heures à feu doux. A mi-cuisson, ajouter le cognac, le porto et l''estragon. Désosser le lapin. Cette opération doit se faire facilement, sinon laisser cuire encore un peu. Hacher toutes les viandes grossièrement et bien les mélanger en pétrissant longuement. Tasser cette préparation dans une terrine, arroser avec un peu de jus de cuisson et mettre à four moyen pendant 1 heure au bain-marie, couvercle fermé. Retourner le couvercle de la terrine, côté bombé vers le dessous, déposez un poids par dessus et laisser refroidir. Conserver au réfrigérateur pendant deux jours avant de servir avec des cornichons et des petits oignons. Garnir le dessus du pâté avec de petits bouquets de persil. Ce pâté se conserve 15 jours au moins au réfrigérateur.', 759, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (103, 'Pâté de foies de volaille', NULL, 40, 60, 0, 0, 'Préparation : Faire revenir les foies de volaille dans la poêle, dans le beurre fondu. Les passer au broyeur ou au mixer. Passer le lard dans la poêle (10 minutes environ) et le broyer également. Déposer 1 feuille de laurier dans le fond de la terrine. Mettre les viandes hachées dans une terrine, ajoutez l''oeuf entier, la crème, le cognac, poivrer et saler. Recouvrir le pâté avec les 4 feuilles de laurier. Placer le pâté dans un four à température moyenne (thermostat 5). Laisser cuire 1 heure. Sortir la terrine du four et la laisser refroidir. Quand la terrine est cuite et sortie du four, poser une planchette dessus et presser avec un poids. Laisser bien refroidir avant de démouler.', 1604, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (563, 'Ananas belle de meaux', NULL, 20, 0, 30, 0, NULL, 605, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (104, 'Pâté de foie de veau', NULL, 40, 60, 0, 0, 'Préparation : Faire revenir le foie de veau dans la poêle, dans le beurre fondu. Le passer au broyeur ou au mixer. Passer le lard dans la poêle (10 minutes environ) et le broyer également. Déposer 1 feuille de laurier dans le fond de la terrine. Mettre les viandes hachées dans une terrine, ajoutez l''oeuf entier, la crème, le cognac, poivrer et saler. Recouvrir le pâté avec les 4 feuilles de laurier. Placer le pâté dans un four à température moyenne (thermostat 5). Laisser cuire 1 heure. Sortir la terrine du four et la laisser refroidir. Quand la terrine est cuite et sortie du four, poser une planchette dessus et presser avec un poids. Laisser bien refroidir avant de démouler.', 1256, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (105, 'Pâté de foie de porc', NULL, 30, 105, 180, 0, 'Préparation : Hacher ensemble foie et lard. Emincer finement oignon et échalotes et les faire blondir 5 minutes dans le saindoux. Les ajouter au mélange foie et lard, ainsi que les oeufs, jaunes d''oeufs, persil, farine, sel et poivre et une forte pincée de 4-épices. Foncer une terrine de bardes de lard, suffisamment longues pour qu''en se rabattant elles puissent recouvrir le pâté. Mettre dessus la préparation, en tassant bien, poser les feuilles de sauge et recouvrir avec les bardes. Mettre le couvercle ou couvrir de papier aluminium et faire cuire à four moyen, au bain-marie, pendant 1 heure 30 minutes. Enlever le couvercle ou le papier aluminium, poser un poids sur le pâté pour bien le tasser. Le laisser refroidir et le mettre ensuite au réfrigérateur jusqu''au lendemain. En cours de cuisson surveiller l''eau du bain-marie et la remplacer au fur et à mesure qu''elle s''évapore.', 2143, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (106, 'Pâté de campagne', NULL, 30, 105, 180, 0, 'Préparation : Hacher le foie et le lard et les mélanger au hachis de porc. Incorporer les oeufs, le porto et le cognac, du sel et du poivre, à la viande en pétrissant bien. Garnir une terrine avec l''une des bardes de lard, puis y tasser le mélange de viandes. Recouvrir avec l''autre barde de lard, ajouter le thym et le laurier, puis fermer la terrine et la mettre à four chaud au bain-marie (dans un récipient d''eau). Laisser cuire 1 heure 30 minutes environ, puis retirer le couvercle et continuer la cuisson pendant un 1/4 d''heure. Sortir le pâté du four et le laisser refroidir, surmonté d''un poids pour le tasser. Servir froid avec des cornichons et des petits oignons. Un pâté est souvent meilleur préparé deux jours à l''avance.', 1386, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (108, 'Pain de boeuf et porc aux fines herbes', NULL, 10, 60, 0, 0, 'Préparation : Découper le lard en très petits morceaux (aux ciseaux ou au hachoir). Enlever les croûtes du pain et le mouiller avec quelques gouttes de lait tiède. Mélanger la viande, le pain, les oeufs entiers, les fines herbes et les oignons. Saler, poivrer, ajouter la muscade râpée et 1 noix de beurre. Déposer le pain (en le tassant bien) dans un plat allant au four. Faire cuire à four moyen (thermostat 5), pendant 1 heure.  ', 1190, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (109, 'Pain de boeuf sans façon', NULL, 15, 60, 0, 0, 'Préparation : Hacher finement la viande et le lard mi-gras. Mélanger le pain trempé dans les 2/3 du bouillon et presser avec l''oeuf entier et les jaunes d''oeufs mis à part, les herbes hachées et assaisonner. Beurrer une terrine, la foncer de la moitié des tranches de lard. Tasser dessus le hachis, l’arroser du reste de bouillon et le couvrir de rondelles d''oignon, de thym, de laurier, puis des tranches de lard restantes. Couvrir la terrine et faire cuire une bonne heure dans un four assez chaud, préchauffé. Démouler après refroidissement sur un lit de feuilles de laitues, garnir de demi-tomates et accompagner de salade de pommes de terre.', 1041, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (110, 'Oeufs farcis au crabe', NULL, 10, 10, 0, 0, 'Préparation : Faire cuire les oeufs durs (10 minutes dans l''eau bouillante). Passer-les sous un jet d''eau froide. Les écaler et les couper en 2 dans le sens de la longueur.  Retirer les jaunes. Dans un bol, mélanger les jaunes avec un peu de mayonnaise, le crabe égoutté, la moitié des olives dénoyautées et hachées. Remplir chaque moitié d''oeuf avec ce mélange. Dresser les oeufs ainsi farcis sur un plat garni de laitue et entourés du restant de mayonnaise. Servir très frais, avec des rondelles de citron.', 1176, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (113, 'Fromage blanc aux fines herbes', NULL, 5, 0, 0, 0, 'Préparation : Dans un grand bol, mélanger le fromage et les herbes. Saler et poivrer. Etaler le fromage sur les tartines, en une couche épaisse. Découper les radis (bien lavés et essuyés) en rondelles et les déposer sur le fromage.  ', 796, NULL);
