INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (118, 'Beignets de morue', NULL, 20, 10, 0, 0, 'Préparation : Faire dessaler la morue dans de l''eau froide courante, puis la laisser tremper dans le lait. Préparer la pâte à beignets ou à frire. Egoutter la morue, bien la sécher et la diviser en petits morceaux réguliers. Les tremper dans la pâte de votre choix et faire dorer dans la friture. Servir avec sauce ou coulis additionné de câpres.', 378, NULL);
