INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (122, 'Côte de boeuf grillée aux poivrons', NULL, 10, 30, 0, 120, 'Préparation : Mettre tous les éléments de la marinade dans un récipient et y faire macérer la viande pendant environ 2 heures, en la retournant, de temps à autre, ainsi que les poivrons fendus, vidés de leurs graines et aplatis. Egoutter viande et poivrons et les faire griller à feu vif pour saisir la viande, puis à chaleur moins vive pour que l''intérieur soit chauffé: environ 10 minutes de chaque côté, davantage si vous aimez la viande à point. Retourner de temps à autre ainsi que les poivrons. Assaisonner en fin de cuisson. Cette recette convient parfaitement à une cuisson au barbecue.  ', 625, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (123, 'Irish coffee', NULL, 10, 5, 0, 0, 'Préparation : Préparer le café fort. Battre légèrement la crème épaisse, pour la rendre plus légère et onctueuse. Faire chauffer le whisky, sans le laisser bouillir et le verser dans un verre à pied, muni d''une cuillère en métal. Déposer dessus le sucre de canne et couvrir avec le café. Verser ensuite la crème épaisse, en la faisant glisser sur le dos d''une cuillère à soupe, en formant ainsi un col de 2 à 3 cm de haut. Saupoudrer de muscade râpée et servir immédiatement, sans mélanger.', 754, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (124, 'Pâte de pommes', NULL, 60, 30, 0, 0, 'Préparation : Couper les pommes en quatre, les peler et enlever pépins et peaux dures. Diviser ces quartiers en morceaux et les faire cuire avec un peu d''eau, jusqu''à ce que les fruits soient bien tendres. Les enlever à l''écumoire. Passer les pommes en purée et remettre celle-ci sur le feu, pour la dessécher, en remuant avec la cuillère en bois. Peser cette purée et prendre le même poids de sucre. Verser ce dernier dans le jus de cuisson des pommes et faire cuire jusqu''à l’obtention d’un sirop épais. Mélanger la purée et le sirop, porter sur le feu en remuant avec la cuillère en bois, jusqu''à ce que la masse forme une pâte. Etaler cette pâte sur une plaque huilée, à 2 cm d''épaisseur, et laisser refroidir complètement. Couper en petits carrés cette pâte durcie et les passer dans du sucre cristallisé. Les ranger de préférence, dans des petits godets de papier. Si les pommes sont trop douces, ajouter à la cuisson le jus de 1 ou 2 citrons. Cette recette s''applique aux autres fruits de saison: poires, coings, prunes, etc.', 805, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (125, 'Pâte de fruits aux cerises', NULL, 40, 90, 0, 0, 'Préparation : Laver, équeuter et dénoyauter les cerises. Les mettre dans une casserole, avec 2 dl d''eau, et les faire cuire pendant 10 minutes. Les égoutter et les passer au tamis de crin. Peser la pulpe obtenue et prendre le même poids de sucre semoule. Faire cuire pulpe et sucre, en remuant avec une spatule, jusqu''à ce que la masse forme une boule et se détache des bords de la casserole. Etendre cette pâte épaisse sur la plaque du four saupoudrée de sucre très fin, et laisser sécher à l''entrée du four doux, pendant 1 heure. Laisser ensuite sécher 48 heures à l''air, puis saupoudrer de sucre très fin et couper en carrés. Vous pouvez les ranger dans une boîte en fer blanc, tapissée de papier blanc, en intercalant une feuille de papier entre chaque couche. Recette qui s''applique à tous les autres fruits à pulpe qui, traités de la même façon, vous offriront toute une gamme de pâtes de fruits frais.', 944, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (126, 'Pain de marrons au chocolat', NULL, 20, 60, 1440, 0, 'Préparation : Inciser les contours des marrons en perçant écorce et peau. Les plonger ensuite dans de l''eau et les laisser cuire pendant 15 minutes environ. Les sortir de l''eau un à un et les éplucher aussitôt. Faire bouillir le lait avec la vanille fendue. Mettre à cuire les marrons pendant 40 minutes environ jusqu''à ce qu''ils soient bien tendres. Les passer en purée fine. Ajouter à cette purée encore chaude le sucre, le chocolat râpé fondu dans un peu du lait de cuisson, le beurre, le sel et mélanger intimement pour obtenir une pâte très homogène. Beurrer un moule à cake rectangulaire, le tapisser de papier aluminium légèrement huilé et tasser dessus la pâte de marrons. Laisser durcir 24 heures dans le réfrigérateur et démouler au moment de servir. Accompagner au choix d''une sauce à la vanille.', 1098, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (127, 'Macarons', NULL, 20, 30, 0, 0, 'Préparation : Piler les amandes en y incorporant peu à peu le sucre. Ajouter ensuite la crème de riz et les blancs d''œufs très légèrement battus avec le se|. Mélanger le tout intimement pendant quelques minutes et laisser reposer la pâte au moins 5 minutes. Beurrer et fariner la plaque du four et la tapisser d''une feuille de papier de riz, côté brillant en dessous. Retravailler la pâte qui doit être consistante et, avec une poche et une douille, presser sur la feuille de papier de riz des petits tas espacés. Au centre de chacun d''eux enfoncer une demi amande mondée. Faire cuire 15 minutes à four doux puis 12 à 15 minutes à four modéré.', 1995, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (565, 'Agneau à la hongroise', NULL, 15, 50, 0, 0, NULL, 588, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (128, 'Pâté de limandes aux noix de St-Jacques', NULL, 20, 80, 0, 0, 'Préparation : Parer les filets de limandes et les broyer au mixer, ajouter les oeufs entiers, le sel, un peu de poivre blanc moulu, le beurre et mélanger en purée fine et bien homogène. Mettre la purée dans un bol et incorporer la crème, peu à peu et en battant fortement. Rectifier l''assaisonnement. Beurrer une grande terrine rectangulaire et tasser la moitié de la farce de poisson. Poser dessus, au centre, les noix de St-Jacques et couvrir avec le reste de la purée de poisson. Couvrir la terrine et faire cuire dans un bain-marie pendant 1 h environ, à four modéré. Vers la fin de la cuisson, préparer la sauce: battre les jaunes d''oeufs avec le jus de citron et le vin blanc, dans une petite casserole posée sur feu doux. Laisser épaissir, sans cesser de battre et sans atteindre l''ébullition. Incorporer peu à peu le beurre, en fouettant toujours et en surveillant l''émulsion. Au besoin, retirer de temps à autre la casserole du feu. Lorsque tout le beurre est incorporé, ajouter le persil et rectifier l''assaisonnement. Démouler le pâté, le découper en tranches. Accompagner de la sauce.', 914, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (131, 'Pain de veau et boeuf aux fines herbes', NULL, 10, 60, 0, 0, 'Préparation : Découper le lard en très petits morceaux (aux ciseaux ou au hachoir). Enlever les croûtes du pain et le mouiller avec quelques gouttes de lait tiède. Mélanger la viande, le pain, les oeufs entiers, les fines herbes et les oignons. Saler, poivrer, ajouter la muscade râpée et 1 noix de beurre. Déposer le pain (en le tassant bien) dans un plat allant au four. Faire cuire à four moyen (thermostat 5), pendant 1 heure.', 1225, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (132, 'Pain de veau aux fines herbes', NULL, 10, 60, 0, 0, 'Préparation : Découper le lard en très petits morceaux (aux ciseaux ou au hachoir). Enlever les croûtes du pain et le mouiller avec quelques gouttes de lait tiède. Mélanger la viande, le pain, les oeufs entiers, les fines herbes et les oignons. Saler, poivrer, ajouter la muscade râpée et 1 noix de beurre. Déposer le pain (en le tassant bien) dans un plat allant au four. Faire cuire à four moyen (thermostat 5), pendant 1 heure.  ', 1012, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (133, 'Pain de veau aux champignons', NULL, 10, 60, 0, 0, 'Préparation : Faire tremper le pain dans un peu de bouillon. Mettre à blondir dans le beurre les échalotes hachées menu, ajouter les champignons hachés grossièrement. Laisser cuire 2 minutes puis mouiller avec le madère, qui s''évapore aussitôt, et le jus de truffe avec les pelures. Assaisonner. Mélanger ce hachis avec la viande et le pain ramolli et presser et ajouter les oeufs battus. Rectifier l''assaisonnement. Beurrer un moule ou une terrine rectangulaire et y mettre la préparation. Faire cuire à four modéré, dans un bain- marie, pendant environ 1 heure. Démouler et server. Si le pain de veau a tendance à trop se colorer, le couvrir d''une feuille de papier aluminium.', 704, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (134, 'Pain de veau', NULL, 10, 45, 0, 0, 'Préparation : Dans une grande terrine, déchiqueter les tranches de pain, puis les arroser avec le lait. Quand le pain est bien trempé, casser les oeufs et ajouter le veau, le porc hachés, le lard coupé en fins lardons, le persil haché, les champignons en lamelles, les aromates et le sel et du poivre. Pétrir bien avec vos mains, jusqu''à ce que tout soit intimement mélangé. Vous assurer que c''est assez épicé. Déposer cette préparation dans un plat allant au four. Lui donner la forme d''un pain allongé et régulier. Mettre à four chaud (thermostat 7-8) pendant 3/4 heure environ. Le pain de veau doit être bien doré.  ', 949, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (136, 'Pain de chou-fleur', NULL, 20, 75, 0, 0, 'Préparation : Nettoyer, laver et diviser le chou-fleur en bouquets que vous ferez cuire dans de l''eau bouillante salée, additionnée du jus de citron. Passer en purée les bouquets égouttés. Hacher l''oignon épluché, le faire blondir en sauteuse dans 2/3 du beurre, ajouter la viande hachée que vous écraserez à la fourchette pendant deux minutes pour qu''elle soit bien imprégnée de beurre et d''oignon. Hors du feu, ajouter le pain préalablement trempé dans un peu de lait puis presser. Ajouter également l''oeuf entier, le jaune d''oeuf, le persil, le sel et le poivre. Graisser un moule à pain ou à cake rectangulaire avec le reste du beurre et tasser en les alternant, des couches de purée de chou et de farce. Faire cuire à four modéré et dans un bain-marie pendant une bonne heure. Démouler et napper de sauce tomate. Tout reste de viande ou de volaille, cuit ou non, peut être utilisé dans cette recette.', 732, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (138, 'Pudding de pains aux raisins', NULL, 20, 50, 0, 0, 'Préparation : Beurrer les tranches de pain et retirer éventuellement les croûtes. En tapisser le fond d''un grand moule en faïence, beurré. Parsemer de raisins, remettre une couche de pain et de raisins et ainsi de suite en terminant par le pain. Faire chauffer le lait avec sucre et muscade et le verser sur les œufs bien battus. Mélanger et transvaser le tout dans le moule. Laisser en contact pendant 30 minutes. Faire cuire le pudding à four modéré pendant 45 à 50 minutes, jusque ce qu''il soit bien pris et doré. Laisser refroidir complètement et mettre au frais.', 971, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (139, 'Pudding à l''orange', NULL, 20, 60, 0, 0, 'Préparation :', 665, NULL);
