INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (141, 'Pudding aux cerises', NULL, 10, 45, 0, 0, 'Préparation :', 1127, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (145, 'Poires au vin', NULL, 10, 20, 0, 0, 'Préparation : Faire bouillir le vin rouge avec la cannelle et le sucre. Peler les poires entières, les arroser de jus de citron et les plonger ensuite dans le vin bouillant et les laisser pocher 15 à 20 minutes selon grosseur. Laisser refroidir les poires dans leur cuisson et les servir, avec leur jus, en compotier.', 389, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (146, 'Pamplemousse gratiné aux sésames', NULL, 5, 5, 0, 0, 'Préparation : Prendre un pamplemousse rose ou jaune et le couper en deux. Découper soigneusement le pourtour de la chair pour la décoller de la peau. Le pamplemousse grillé sera ainsi plus facile à manger. Poser les deux moitié dans un ramequin pouvant aller au four. Ajouter sur chaque moitié du pamplemousse le miel et les graines de sésame. Mettre sous le grill 4 à 5 minutes. Déguster aussitôt.  ', 309, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (148, 'Meringues glacées', NULL, 10, 0, 0, 0, 'Préparation : Confectionner les meringues, ou les acheter chez le pâtissier. Intercaler 50 g de crème glacée en sandwich entre deux meringues, presser un peu. Procéder de même pour les autres meringues. Fouetter la crème avec le sucre vanillé et en garnir les meringues glacées. Servir immédiatement.', 1289, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (151, 'Marmelade de prunes et potiron', NULL, 20, 85, 0, 720, 'Préparation : Couper en petits dés la chair du potiron. Les mettre dans un grand bol, les couvrir de la moitié du sucre et laisser macérer pendant la nuit. Faire cuire ces dés de potiron macérés et, après 30 minutes d''ébullition, ajouter les prunes, lavées, dénoyautées et coupées en morceaux. Prolonger la cuisson de 30 à 40 minutes, jusqu''à ce que les fruits soient bien tendres. Ajouter alors le jus pressé des citrons, le reste du sucre et, lorsque ce dernier est fondu, ramener à ébullition et faire cuire encore 15 minutes, jusqu''à ce que la marmelade soit prise. Mettre en pots et couvrir. (3 kg de fruits et de sucre pour 8 à 10 pots)', 1042, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (152, 'Marmelade de poires', NULL, 20, 120, 1440, 0, 'Préparation : Couper les poires en quartiers, les peler, enlever pépins et membranes dures et hacher au moulin ou râper à la machine. Mettre pulpe et jus dans la bassine, avec sucre, jus de citron et vanille fendue en deux. Laisser fondre le sucre sur feu très doux, pendant 1 h, sans remuer. Prolonger la cuisson pendant une autre heure, à feu plus vif, en remuant avec la cuillère en bois. Afin d''obtenir une pâte ayant la couleur et un peu la consistance du miel. Mettre en pots et laisser refroidir 24 h avant de couvrir.', 831, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (155, 'Marmelade de fraises et rhubarbe', NULL, 15, 25, 0, 720, 'Préparation : Laver et équeuter les fraises, les couper en deux, les mettre dans un grand bol et les couvrir de 1/3 du sucre. Laver la rhubarbe, la couper en petits tronçons et les faire macérer dans le reste du sucre. Laisser en contact toute la nuit. Le lendemain, mettre les fruits, avec leur jus, dans une bassine à confiture, amener à ébullition et faire cuire à gros bouillons pendant 25 minutes. Vérifier la cuisson en versant quelques gouttes de marmelade sur une assiette très froide, elles doivent se figer. Mettre en pots et couvrir aussitôt. (3 kg de fruits et de sucre pour 8 à 10 pots)', 1029, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (157, 'Madeleines', NULL, 10, 15, 0, 0, 'Préparation : Dans une petite casserole, posée sur feu doux, faire fondre le beurre, sans le cuire. Battre longuement les œufs avec sucre, sucre vanillé, sel, parfum, jusqu''à obtention d''une mousse blanchâtre. Y incorporer peu à peu la farine,en alternant avec le beurre fondu refroidi. Beurrer et fariner les moules à madeleine ou encore des plaques creusées de cavités. Y répartir la pâte, mise à mi-hauteur. Faire cuire 5 minutes à four modéré préchauffé. Augmenter la température et continuer la cuisson à four assez chaud, pendant environ 12 minutes, jusqu''à cuisson complète. Démouler sur une grille et laisser refroidir.', 1983, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (158, 'Coeurs de palmiers', NULL, 20, 10, 30, 0, 'Préparation : Faire de la pâte feuilletée. Ne donner que 4 tours. Donner les deux derniers tours en saupoudrant le rectangle de pâte de sucre cristallisé, avant de le replier en trois. Ensuite, abaisser la pâte en un long rectangle de 4 mm d''épaisseur environ. La taille de vos cœurs de palmiers dépendra de celle du rectangle que vous abaisserez: long et étroit, les cœurs seront petits; court et large, ils seront grands. Le placer devant vous dans le sens de la hauteur, puis rouler chaque côté jusqu''au milieu. Plier les deux rouleaux obtenus en les ramenant l''un vers l''autre. Presser légèrement pour donner une forme plus ovale. Mettre au réfrigérateur pour faire durcir un peu. Après 30 minutes environ, sortir le rouleau de pâte et découper des tranches d''un 0.5 cm d''épaisseur environ. Les faire cuire à four chaud (thermostat 7-8), sur une plaque à pâtisserie non beurrée, en laissant un intervalle suffisant entre elles pour qu''elle puissent s''étendre en cuisant. Retirer du four quand les cœurs de palmiers ont une belle couleur dorée. Laisser refroidir.  ', 2007, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (241, 'Porc à la niçoise', NULL, 15, 105, 0, 0, 'Préparation : Nettoyer les légumes et les couper en tranches. Faire dorer la viande dans les 3/4 de l''huile (dans une cocotte à fond épais), 15 mn sur chaque face. Quand la viande est bien dorée, la placer dans un grand plat allant au four. Disposer autour d''elle tous les légumes découpés. Ajouter thym, laurier, sel et poivre et le reste de l''huile. Couvrir le tout avec le bouillon et laisser cuire 1 heure 15 minutes, à feu moyen (thermostat 5).  ', 541, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (163, 'Génoise', NULL, 15, 65, 0, 0, 'Préparation : Casser les œufs dans une terrine posée dans une casserole d''eau chaude, à feu doux. Ajouter le sucre et le sel et fouetter, jusqu''à ce que le mélange soit à 50 ou 60° environ (quand c''est trop chaud pour y laisser le doigt). Retirer la terrine de la casserole et continuer à fouetter, jusqu''à ce que la pâte soit froide. Incorporer alors délicatement la farine en pluie et le beurre fondu. Beurrer et fariner légèrement le moule avant d''y verser la pâte: vous démoulerez la génoise plus facilement. Verser dans un moule de 20 à 25 cm de diamètre et faire cuire à four doux pendant 45 minutes environ. Surveiller la cuisson: elle est terminée quand la lame de couteau que vous enfoncez au milieu du gâteau en ressort propre. Démouler la génoise en glissant une lame de couteau le long des parois du moule: cela facilitera aussi le démoulage. La laisser refroidir. Les garnitures de la génoise sont innombrables. La façon la plus simple et la plus rapide de la servir est de la couper en deux ou en trois dans l''épaisseur, de la tartiner copieusement de confiture ou de marmelade et de l''arroser de crème anglaise.  ', 1536, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (165, 'Gaufres quatre-quarts', NULL, 15, 20, 0, 30, 'Préparation : Tourner le beurre en crème. Y ajouter le sucre, les jaunes des œufs, la farine. Battre les blancs des œufs en neige ferme, avec le sel. Incorporer 2 à 3 cuillerées de ces blancs à la pâte précédente et mélanger au fouet. Incorporer ensuite le reste des blancs d''œufs, délicatement, en soulevant la pâte. Laisser reposer 30 minutes puis faire cuire les gaufres. Ces gaufres se conservent bien dans une boîte en fer.  ', 2103, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (169, 'Gâteau de Savoie', NULL, 15, 60, 0, 0, 'Préparation : Dans un grand bol, mettre les jaunes d''œufs et les mélanger avec la moitié du sucre fin et le sucre vanillé. Ajouter la farine en fontaine, râper le zeste de citron, l''ajouter au mélange avec une cuillère à café de jus de citron. Battre les blancs d''œufs en neige bien ferme, ajouter une pincée de sel. Ajouter aux blancs d''œufs le reste de sucre, puis le mélanger délicatement aux jaunes et à la farine. Ajouter les fruits. Verser le tout dans un moule bien beurré. Faire cuire à four doux (thermostat 3/4), pendant 1 heure. Laisser refroidir et démouler.  ', 930, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (170, 'Gâteau quatre-quarts', NULL, 20, 60, 0, 0, 'Préparation : Tourner, avec une fourchette, le beurre en crème, puis y incorporer le sucre et battre jusqu''à ce qu''il fonde. Ajouter alors les jaunes des œufs, 1 pincée de sel et, peu à peu, la farine. Terminer par les blancs d''œufs battus en neige avec 1 pincée de sel. Beurrer un moule à cake, le saupoudrer de chapelure et en secouer l''excédent. Verser la pâte dans le moule et faire cuire à four moyen pendant environ 1 heure. Vérifier la cuisson en enfonçant une lame de couteau ou une aiguille à tricoter au centre du gâteau, elle doit en ressortir sèche. Attendre quelques minutes avant de démouler sur grille.  ', 2037, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (171, 'Gâteau aux pommes', NULL, 20, 45, 0, 0, 'Préparation : Dans une grande terrine, mélanger la farine, le sucre (en gardant 4 cuillères à soupe de sucre pour décorer le plat), les œufs entiers. Ajouter le lait peu à peu, puis le thym. Eplucher les pommes, enlever les centres et les découper en fines lamelles. Dans un grand plat allant au four, faire fondre le beurre, puis verser les pommes et recouvrir le tout de pâte. Saupoudrer le dessus avec le reste du sucre fin. Faire cuire à four chaud (thermostat 6/7), pendant 45 minutes. Surveiller la cuisson, le gâteau est cuit quand il est monté et légèrement doré. Servir chaud, tiède ou froid. Cet excellent dessert peut se préparer la veille et se conserve 2 jours au réfrigérateur.  ', 741, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (172, 'Gâteau marbré', NULL, 25, 50, 0, 0, 'Préparation : Séparer les blancs des jaunes d''œufs et mélanger ceux-ci intimement au beurre, préalablement bien ramolli, et au sucre jusqu''à ce que le mélange soit blanc. Tamiser la farine avec levure et sel. Y ajouter le mélange beurre, sucre et jaunes d''œufs, puis suffisamment de lait pour obtenir une pâte molle, bien homogène. Battre les blancs d''œufs en neige, en incorporer une bonne cuillère à soupe à la pâte précédente et battre fortement. Ajouter ensuite le reste des blancs, en soulevant la pâte. Partager celle-ci en deux parties égales, et ajouter à l''une d''elles le cacao en poudre. Beurrer et fariner un moule à cake, rond ou rectangulaire, et y déposer les deux pâtes par cuillerées alternées. Faire cuire à four moyen pendant environ 50 minutes.  ', 2033, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (174, 'Cake au citron', NULL, 20, 60, 0, 0, NULL, 1992, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (175, 'Cakes au chocolat', NULL, 20, 20, 0, 0, 'Préparation : Travailler le beurre en crème, y ajouter le sucre et le sucre vanillé et, lorsque le mélange est bien blanc, le jaune d''œuf battu avec l''œuf entier et un peu de sel. Mélanger la farine avec la fécule, le cacao et le bicarbonate. Les incorporer, peu à peu, à la préparation précédente, en ajoutant suffisamment de lait pour obtenir une pâte souple, un peu molle. Répartir cette pâte dans des petits moules beurrés et farinés ou encore dans des godets de papier. Faire cuire de 15 à 18 minutes dans un four moyen. Décorer au choix les petits cakes d''un cerneau de noix ou d''un morceau d''écorce d''orange confite. Bien entendu cette pâte peut être utilisée pour un grand cake, seul le temps de cuisson diffère (environ 45 minutes).', 1931, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (176, 'Cake aux céréales', NULL, 30, 60, 0, 720, 'Préparation : La veille, faire tremper les céréales dans le lait et les raisins dans de l''eau. Les flocons d''avoine doivent avoir absorbé tout le lait, sans cependant former une masse trop liquide. Si c''était le cas, les presser sur une passoire pour éliminer l''excédent de liquide. Le lendemain, tourner le beurre en crème, avec sel et sucre, jusqu''à ce que ce dernier soit fondu. Ajouter les jaunes d''œufs, la pâte de flocons d''avoine et la farine. Mélanger intimement. Egoutter et éponger les raisins. Pour que les raisins ne s''agglomèrent pas, les rouler dans de la farine et les incorporer à la pâte ainsi que les fruits confits. Battre les blancs d''œufs en neige, avec la levure dissoute dans très peu d''eau, et les amalgamer délicatement à la pâte. Verser celle-ci dans un moule à cake beurré et fariné et faire cuire à four modéré pendant 1 h environ. Le gâteau est cuit lorsqu''une aiguille à tricoter enfoncée en son centre en ressort lisse. Si la pâte reste collée, prolonger la cuisson. Laisser tiédir avant de démouler sur grille.', 1220, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (177, 'Biscuit roulé aux abricots', NULL, 30, 15, 0, 0, NULL, 1507, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (178, 'Biscuits frits', NULL, 20, 10, 0, 0, NULL, 1666, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (180, 'Pralin', NULL, 10, 15, 0, 0, 'Préparation : Faire griller amandes au four, jusqu''à ce qu''elles soient bien sèches et que vous puissiez facilement enlever, en les frottant entre les doigts, les pellicules brunes pouvant encore les entourer. Mettre le sucre et le sucre vanillé dans une poêle et les laisser fondre sur feu doux, jusqu''à obtention d''un caramel blond. Y verser les amandes refroidies et mélanger vivement, avec la cuillère en bois, pour qu''elles soient bien enrobées de caramel et, lorsque celui-ci brunit et durcit, retirer du feu. Verser les amandes caramélisées sur une plaque huilée, laisser bien refroidir puis, selon les besoins, hacher ou piler en poudre.', 2443, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (181, 'Caramels', NULL, 10, 10, 0, 0, NULL, 2406, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (183, 'Confiture d''automne économique', NULL, 20, 0, 0, 0, 'Préparation : Presser le jus des raisins. Couper poires et pommes en quartiers, les peler et enlever cœurs et pépins. Peler les coings et les couper en deux. Détailler ces fruits en morceaux et les mettre dans une bassine à confiture, avec le jus de raisins. Laisser cuire longuement et à petit feu, en tournant avec la spatule en bois, jusqu''à ce que les fruits fondent et s''amalgament en une pâte lisse. Vérifier le degré de cuisson en déposant un peu de cette confiture sur une assiette froide. Elle doit y rester figée, sans s''écouler. Mettre en pots et couvrir. Un peu de zeste râpé et de jus de citron parfume agréablement cette confiture. Pour diminuer le temps de cuisson très long (que l''on ne peut déterminer par avance, ceci dépendant des fruits). Ajouter 500 g de sucre au jus de raisins pour 1kg de chaque fruit. La confiture sera plus moelleuse.  ', 295, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (184, 'Compote de pommes meringuée', NULL, 20, 30, 0, 0, 'Préparation : Eplucher les pommes, les couper en quatre et les épépiner. Les faire cuire dans une grande casserole, à feu doux, avec un filet d''eau. Mélanger, en écrasant les pommes, de temps en temps. Quand les pommes sont en purée, retirer la compote du feu et la verser dans un plat allant au four. Laisser tiédir. Pendant ce temps, battre les blancs en neige très ferme. Y incorporer le sucre glace. En recouvrir la compote et faire meringuer à four doux, pendant une dizaine de minutes. Servir tiède.  ', 340, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (185, 'Compote de pommes caramélisée', NULL, 20, 30, 0, 0, 'Préparation : Peler les pommes, les couper en quatre et les épépiner. Couper les quartiers en lamelles et les faire cuire doucement avec la cannelle. Ajouter la moitié du sucre et mélanger en réduisant les pommes en purée. Faire un caramel blond avec le reste du sucre et de l''eau. En mélanger la moitié à la compote. Verser celle-ci dans le plat de service et couvrir avec le reste de caramel. Servir tiède, ou froid, tel quel, ou accompagné de crème fraîche épaisse ou de crème anglaise. Pour varier la recette, la préparer avec moitié pommes, moitié poires.  ', 664, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (186, 'Compote de cerises au vin blanc', NULL, 15, 15, 0, 0, 'Préparation : Faire chauffer le vin et le sucre jusqu''à ébullition. Laver rapidement les cerises, les égoutter et les jeter dans le vin. Laisser cuire doucement 1 /4 d''heure. Hors du feu ajouter l''alcool et verser en compotier. Mettre au frais.  ', 598, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (191, 'Poulet à l''indienne', NULL, 10, 60, 0, 300, 'Préparation : Enlever la peau du poulet et le poser sur la lèchefrite du four. L''arroser de jus de citron puis de yogourt et le laisser macérer dedans pendant quelques heures, en le retournant de temps à autre. Piler les grains de coriandre et de poivre, les mélanger avec le curry et du sel et en assaisonner la volaille. L''arroser d''huile et mettre à cuire à four moyen, pendant une heure ou plus. Le yogourt est entièrement résorbé et il n''y a pas de jus. Découper le poulet en morceaux et accompagner de coulis de tomate additionné des crevettes et de riz.', 1256, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (192, 'Poulet grand mère', NULL, 15, 80, 0, 0, 'Préparation : Dans une cocotte, faire fondre doucement le lard coupé en lardons. Quand ils ont rendu un peu de leur graisse, ajouter les oignons épluchés et les faire revenir doucement. Quand lard et oignons sont un peu colorés, les retirer de la cocotte et les remplacer par le poulet vidé et bridé. Le poulet doit bien dorer sur toutes ses faces: le retourner souvent. Remettre ensuite lard et oignons dans la cocotte. Poivrer. Ajouter thym et laurier. Arroser avec un mince filet d''eau. Couvrir et laisser cuire pendant 1 heure, à feu modéré. Peler les pommes de terre et les faire cuire 10 minutes à l''eau bouillante non salée. Les ajouter au poulet 1/4 d''heure avant la fin de la cuisson. Quand le poulet est cuit, le retirer. Faire sauter à feu vif les ingrédients restant dans la cocotte, jusqu''à ce qu''ils soient bien dorés. Pendant ce temps, découper le poulet, le dresser sur le plat de service préchauffé et verser pommes de terre, lardons et petits oignons par dessus. Servir immédiatement.', 1333, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (193, 'Poulet frit à l''américaine', NULL, 10, 20, 0, 0, 'Préparation : Diviser le poulet en 8 à 10 morceaux, les essuyer. Faire chauffer la graisse dans une grande poêle profonde. Mettre dans un sac moyen en papier, farine, sel et poivre mélangés. Y placer quelques morceaux de volaille et, en tenant le sac fermé, secouer bien. Retirer les morceaux, en les tapotant pour enlever l''excédent de farine, et les ranger dans un grand plat. Procéder de même pour les autres morceaux. Faire frire ces morceaux, par petites quantités à la fois, en commençant par les pilons, puis les hauts de cuisse et en terminant par les blancs. Après 5 minutes, lorsque les morceaux sont bien dorés, les retourner et continuer la cuisson pendant 5 autres minutes. Au fur et à mesure que les morceaux sont dorés,les ranger dans une lèchefrite mise dans un four doux. Tous les morceaux étant frits et bien chauds, avant de les dresser dans un plat ou une corbeille, les éponger dans du papier absorbant. A volonté, les saupoudrer encore de sel. Servir chaud ou froid, avec une salade verte ou de chou blanc au raifort.', 1625, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (194, 'Poulet flambé au whisky', NULL, 15, 45, 0, 5, 'Préparation : Couper le poulet en quatre et le faire colorer dans une cocotte avec la moitié du beurre et l''huile. Remuer souvent. Le beurre ne doit pas brûler. Quand les morceaux sont légèrement dorés, les arroser avec le whisky. Retirer la casserole du feu, couvrir et laisser macérer pendant quelques minutes. Couper le bout terreux des champignons et laver ceux-ci rapidement dans de l''eau vinaigrée. Faire cuire à couvert dans le reste du beurre avec l''échalote hachée. Remettre la cocotte avec le poulet sur le feu. Faire chauffer rapidement quelques instants, puis flamber, en remuant bien. Ajouter les champignons avec leur cuisson, la moitié de la crème, du sel et du poivre. Couvrir et laisser cuire 30 minutes environ. Quand le poulet est cuit, déposer les morceaux sur le plat de service et les réserver au chaud. Amener rapidement le jus de cuisson à ébullition et y verser le reste de la crème dans lequel vous aurez délayé la fécule. Hors du feu, tourner jusqu''à ce que la sauce épaississe. Goûter et rectifier l''assaisonnement. Ajouter un filet de jus de citron. Arroser le plat de service avec cette sauce et servir immédiatement, accompagner de pommes allumettes ou de pommes gaufrettes.', 1474, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (195, 'Poulet farci aux pêches', NULL, 10, 75, 0, 0, 'Préparation : Préparer la farce: Tremper la mie de pain dans le bouillon. Faire blondir, pendant 3 à 4 minutes, les foies de volailles dans une poêle, avec le beurre. Les passer au tamis ou au moulin et y mélanger la mie de pain pressée, les jaunes d''œufs, sel, poivre, madère et persil haché. Farcir le poulet, l''assaisonner et le faire cuire une heure à four moyen. Le retirer, l''enduire de miel, le saupoudrer de paprika et remettre à four chaud pendant 15 minutes. Peler les pêches, les couper en deux et les dénoyauter. Lorsque la volaille est à point, la retirer du feu et du plat. La garder au chaud. Mettre les demi-pêches dans le plat de cuisson et les faire chauffer rapidement, sur les deux faces. Découper le poulet et la farce, les dresser sur le plat de service et les entourer des demi-pêches chaudes. Dégraisser la sauce, la déglacer avec un peu d''eau ou de jus de pêches et verser en saucière. Accompagner de pommes de terre gaufrettes.', 1054, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (196, 'Poulet à la créole', NULL, 30, 80, 0, 30, 'Préparation : Peler les bananes et les écraser en purée. Y ajouter la mie de pain moulue, les grains de maïs cuits, égouttés, le jaune d''œuf, le jus de citron vert, sel, poivre et 1 pincée de poivre de Cayenne. Mélanger intimement le tout. Assaisonner l''intérieur et l''extérieur du poulet et y tassez la farce. Coudre l''ouverture. Le faire rôtir dans l''huile, à four moyen, pendant 1 h 15 à 1 h 25. Retirer la chair de la noix de coco, la couper en morceaux et les faire cuire 5 minutes dans le lait bouillant.  Enlever la casserole du feu, la couvrir et laisser infuser 30 minutes. Passer ensuite au tamis en pressant bien la pulpe pour en exprimer tous les sucs. Arroser le poulet de quelques cuillerées de ce lait de coco. Lorsque la volaille est à point, éliminer la graisse de cuisson et déglacer le plat avec le lait de coco. Rectifier l''assaisonnement et ajouter le beurre.', 1391, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (197, 'Poulet créole', NULL, 15, 50, 0, 0, 'Préparation : Découper le poulet en morceaux. Faire chauffer l''huile dans une cocotte, y faire rissoler les morceaux de poulet. Ajouter les oignons épluchés et émincés, le safran, le sucre, le curry et le piment. Saler et poivrer. Laisser cuire à feu moyen, pendant 35 minutes. Ouvrir la noix de coco et recueillir le jus. Ajouter le jus à la préparation, ainsi qu''un peu de noix de coco râpée. Laisser mijoter encore 10 minutes. Servir immédiatement sur un plat chaud, accompagné de riz blanc.', 1521, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (198, 'Poulet à la comtoise', NULL, 20, 60, 0, 0, 'Préparation : Frotter le poulet avec du sel et du poivre et en mettre à l''intérieur aussi. Le mettre dans une cocotte avec un peu d''huile et le laisser prendre couleur. Quand il est doré sur toutes ses faces, couvrir et laisser cuire doucement. Nettoyer et laver les blancs de poireaux et les carottes, les couper en morceaux et les ajouter dans la cocotte. Les laisser revenir doucement. Mouiller jusqu''à mi-hauteur avec de l''eau bouillante. Saler, poivrer, ajouter un peu de muscade. Couvrir et laisser cuire à petit feu pendant 1 heure. 1 / 4 d''heure avant la fin de la cuisson, faire cuire rapidement les champignons, lavés et émincés, avec le beurre. Quand le poulet est cuit, le retirer et le réserver au chaud. Passer le bouillon avec les légumes à la moulinette, puis le remettre dans le plat et le faire réduire à feu vif. Ajouter les champignons, puis la crème dans laquelle vous aurez délayé le jaune d''oeuf et le jus du citron. Bien battre avec un fouet. Lier avec la fécule délayée dans un peu de sauce. Couper le poulet, le ranger dans un plat à gratin. Napper avec la sauce, puis saupoudrer de fromage râpé. Faire gratiner au four avant de servir, accompagné de tagliatelles au beurre.', 1275, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (404, 'Fonds d''artichauts gratinés', NULL, 5, 5, 0, 0, 'Préparation : Faire blanchir les fonds d''artichauts au préalable 1 ou 2 minutes dans de l''eau bouillante salée Réchauffer les fonds d''artichauts dans le beurre, dans une sauteuse. Faire fondre le fromage dans le vin blanc, assaisonner, ajouter la fécule délayée dans très peu d''eau et porter doucement à ébullition. Dans chaque fond d''artichaut mettre 1/2 cuillère de mousse de foie, napper de la fondue et faire gratiner vivement à four chaud.  ', 688, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (199, 'Poulet clémentine', NULL, 20, 60, 0, 0, 'Préparation : Saler et poivrer l''intérieur des poulets et y introduire un petit suisse. Frotter la peau avec un peu de sel et de poivre et les entourer d''une barde de lard. Faire fondre le beurre dans une grande cocotte et y mettre les poulets à rissoler. Quand ils ont pris couleur, mouiller avec le cognac et faire flamber. Arroser avec le vin blanc, couvrir et laisser cuire doucement. Faire de petites boulettes avec la chair à saucisse, puis les rouler dans la farine. Les ajouter à la cuisson des poulets. Quand les boulettes sont cuites, retirer les poulets de la casserole, les découper et les dresser sur le plat de service. Ecraser le pâté de foie dans la sauce et ajouter la crème. Goûter et rectifier l''assaisonnement. Laisser cuire quelques instants, puis lier la sauce avec la fécule délayée dans un peu de porto. Napper les poulets avec la sauce et servir avec des pommes croquettes.', 1465, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (200, 'Poulet au citron', NULL, 10, 45, 0, 0, 'Préparation : Beurrer un plat allant au four. Découper le poulet en morceaux et les disposer sur le piat. Les arroser de jus de citrons. Saler et poivrer. Placer le plat dans un four chaud (thermostat 7) et laisser cuire pendant 45 minutes environ, selon la grosseur des morceaux de volaille. Déglacer le jus de cuisson avec un peu d''eau. Servir chaud, accompagné de riz blanc.', 1589, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (201, 'Poulet aux chicorées de bruxelles (witloof)', NULL, 15, 90, 0, 0, 'Préparation : Faire blondir dans la moité de beurre le gros oignon et l''ail nettoyés, hachés, puis y ajouter les foies de volaille également hachés, sel et poivre, les baies de genévrier pilées. Après 1 minute de cuisson laisser tiédir. Assaisonner l''intérieur du poulet, le farcir de la préparation précédente, coudre l''ouverture et faire dorer de toutes parts en cocotte, avec le 3/4 du reste de beurre. Retirer la volaille, ajouter le reste de beurre dans la cocotte et les oignons moyens épluchés et émincés ainsi que les chicorées émincées dans le sens de la longueur, chaque légume étant coupé en 6 ou en 8 selon sa grosseur. Saler, poivrer, ajouter une pincée de muscade râpée et le jus de citron. Placer le poulet entre deux couches de chicorées, ajouter la crème, couvrir et faire cuire au four pendant 1 heure 15 minutes environ. Retirer le poulet et faire vivement réduire à feu vif le jus de cuisson des légumes. Rectifier l''assaisonnement.', 1068, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (202, 'Poulet à la bière', NULL, 15, 75, 0, 10, 'Préparation : Couper le poulet en morceaux et les faire revenir doucement avec le beurre dans une grande cocotte. Réserver les foies. Hacher les échalotes et les ajouter aux morceaux de poulets. Les laisser fondre doucement. Quand tout commence à prendre couleur, arroser avec le genièvre. Retirer du feu, couvrir et laisser macérer 10 minutes. Ensuite, mouiller avec la bière, sans couvrir entièrement. Ajouter la moitié de la crème, une pointe de couteau de poivre de Cayenne, du sel et du poivre. Faire bouillir, puis laisser cuire doucement, à couvert, pendant 1 heure. Après 30 minutes de cuisson, ajouter les champignons lavés et coupés en quatre et les foies. Quand les poulets sont cuits, dresser les morceaux sur le plat de service et réserver au chaud. Ecraser les foies cuits dans la sauce, ajouter le reste de crème et faire réduire. Si la sauce reste trop liquide, la lier avec une peu de fécule délayée dans un peu de crème. Goûter et rectifier l''assaisonnement de la sauce, puis en napper les morceaux de poulets. Saupoudrer abondamment de persil haché et servir avec des pommes de terre à l''anglaise.', 1154, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (203, 'Poulet basquaise', NULL, 15, 75, 0, 0, 'Préparation : Dans une cocotte à fond épais, faire dorer les morceaux de poulets dans l''huile d''olive. Saler et poivrer. Saupoudrer les morceaux de poulet de thym et de laurier. Dans une autre cocotte, faire revenir les poivrons, les échalotes et les oignons épluchés et émincés, les courgettes et les tomates découpées en gros morceaux, dans le reste d''huile d''olive. Saler et poivrer. Ajouter un peu de thym et de laurier en poudre. Quand les légumes sont bien fondus, les ajouter aux poulets. Couvrir la cocotte et laisser mijoter à feu très doux, pendant 1 heure. Servir le poulet sur un grand plat de service, entourés des légumes et accompagnés de riz créole.', 743, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (204, 'Poulet aux bananes', NULL, 10, 75, 0, 0, 'Préparation : Découper le poulet en morceaux. Faire fondre le beurre dans une casserole et y faire revenir les morceaux de poulets. Faire flamber les morceaux de poulets si vous utiliser du rhum. Saler, poivrer, ajouter la coriandre et le vin blanc. Laisser mijoter à feu très doux, pendant 1 heure, en couvrant la casserole. 5 minutes avant de servir, découper les bananes en rondelles et les passer rapidement dans une eau bouillante très poivrée. Les égoutter et en garnir le plat chaud, sur lequel vous aurez dressé les morceaux de poulet. Servir avec du riz créole ou pilaf.', 1337, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (205, 'Poulet aux arachides', NULL, 10, 30, 0, 0, 'Préparation : Découper la volaille en  morceaux. Les assaisonner. Faire chauffer le beurre dans une sauteuse et y mettre à dorer successivement tous les morceaux de poulet en les retirant au fur et à mesure. Les remplacer par les cacahuètes décortiquées et émondées, pilées en pâte. Mouiller avec un peu d''eau, ajouter une petite pincée de pili-pili, puis remettre les morceaux de poulet dans la sauteuse et les laisser mijoter jusqu''à cuisson complète, ce qui doit prendre environ 20 minutes. Rectifier l''assaisonnement et servir dans un plat creux. Accompagner de riz cuit au bouillon de volaille, mélangé avec des petits pois et des grains de maïs.', 1853, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (206, 'Poulet à l''ail', NULL, 10, 120, 0, 0, 'Préparation : Détailler le pain en croûtons et les faire rôtir avec un peu d''huile dans une poêle frottée avec une gousse d''ail. Saler et poivrer l''intérieur du poulet et le fourrer avec les croûtons. Le brider et le déposer dans une cocotte. L''idéal est de cuire ce poulet dans une cocotte en terre cuite. Tapisser le fond de la cocotte de brindilles de thym et arroser avec le reste d''huile d''olives. Ajouter le reste des gousses d''ail non épluchées. Fermer la cocotte et faire cuire à four chaud pendant 2 heures. Servir très chaud, avec un gratin dauphinois ou un gratin savoyard.', 1740, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (207, 'Poule au riz', NULL, 15, 120, 0, 0, 'Préparation : Remplir une grande marmite d''eau et l''amener à ébullition. Oter la graisse près du croupion de la poule et la vider. La mettre dans l''eau bouillante. Enlever l''écume grise qui se forme avec une écumoire. Ajouter les légumes nettoyés et les oignons, le thym et le laurier. Saler, poivrer et laisser cuire à petit feu, pendant 1 heure 30 minutes environ. Prélever 3/8 de bouillon dans la marmite, les verser dans une casserole et y faire cuire le riz tout doucement. Prélever encore la moitié de bouillon restant, le filtrer dans une passoire fine recouverte d''un linge propre pour le dégraisser et le faire réduire de moitié. Dans un grand poêlon, faire fondre le beurre, ajouter la farine et laisser cuire quelques instants. Mouiller avec le bouillon réduit, en mélangeant rapidement avec un fouet. Quand la sauce est bien lisse, hors du feu, ajouter la crème fraîche dans laquelle vous aurez délayé le jaune des œufs. Mélanger bien. Ajouter le jus de citron, poivrer. Goûter et rectifier l''assaisonnement. Réchauffer à feu très doux, sans faire bouillir. Découper la poule et disposer les morceaux tout autour du plat de service. Égoutter les légumes et les déposer au milieu. Napper avec un peu de sauce. Servir immédiatement avec le riz et le resté de la sauce en saucière.', 572, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (208, 'Poule au pot', NULL, 10, 165, 0, 0, 'Préparation : Hacher les foies de volaille et les mélanger avec la chair à saucisse. Saler, poivrer et farcir de ce mélange l''intérieur de la poule, préalablement assaisonné. Ficeler la volaille et la mettre dans une grande marmite. Ajouter le jarret, l''os à moelle et couvrir d''eau à hauteur. Amener à ébullition et écumer. Nettoyer les légumes et les mettre dans la marmite ainsi que l''ail et le bouquet garni. Assaisonner, couvrir et faire mijoter pendant 2 heures 30 minutes. Mettre la volaille déficelée dans un grand plat creux, avec le jarret, et l’entourer des légumes. Parsemer de persil haché. Dégraisser le bouillon de cuisson et le servir à part. Accompagner de pommes de terre bouillies.', 547, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (209, 'Potée au fenouil', NULL, 10, 100, 0, 0, 'Préparation : Faire revenir le morceau d''échine de porc, jusqu''à ce qu''il soit doré sur toutes ses faces. Pendant ce temps, couper les tiges fibreuses des bulbes de fenouil et les faire blanchir 5 minutes à l''eau bouillante. Les égoutter et les passer sous l''eau froide. Ajouter les bulbes de fenouil dans la cocotte où cuit l''échine, en même temps que le lard coupé en lardons, une branche de thym et une feuille de laurier. Saler (pas trop à cause du lard), poivrer, ajouter un verre d''eau et laisser cuire à feu doux, pendant 1 heure 30 minutes au moins. Servir très chaud, accompagné de pommes de terre à l''anglaise.', 807, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (210, 'Potée d''endives à la liégeoise', NULL, 10, 70, 0, 0, 'Préparation : Laver rapidement les endives à l''eau courante, puis les faire blanchir 5 minutes dans une casserole d''eau bouillante. Piquer la saucisse avec une fourchette et la faire revenir à feu moyen dans une grande poêle, jusqu''à ce qu''elle ait perdu une partie de sa graisse. La transvaser, sans la graisse, dans une cocotte avec les endives bien égouttées. Saler et poivrer, puis ajouter un peu de muscade. Arroser avec un verre d''eau chaude, couvrir et laisser cuire à petit feu, pendant 1 heure environ.', 729, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (211, 'Potée aux choux', NULL, 15, 180, 0, 0, 'Préparation : Nettoyer et éplucher les légumes, puis les laver. Couper les pommes de terre, navets, carotte et oignons en quatre et le chou en six. Piquer les clous de girofle dans un morceau d''oignon. Dans une grande cocotte, faire fondre carottes, navets, oignons et poireaux entiers dans un peu d''huile. Ajouter ensuite le morceau de porc, le céleri, le persil, le thym, le laurier et l''ail. Poivrer, mais ne pas saler (le lard s''en chargera). Recouvrir d''eau, puis laisser cuire tout doucement, à couvert, pendant 45 minutes. Dans une casserole à part, faire blanchir le chou pendant 5 minutes à l''eau bouillante. Puis le plonger 5 minutes dans de l''eau froide. L’égoutter, puis l’ajouter à la potée, en même temps que le morceau de lard. Laisser cuire pendant 1 heure. Ajouter alors le saucisson ou la saucisse que vous aurez, au préalable, piqué avec une fourchette et les pommes de terre. Faire mijoter 1 heure encore. Goûter et rectifier l''assaisonnement. Couper le lard et l''échiné en tranches, le saucisson ou la saucisse en morceaux. Le disposer sur le plat de service, entourés des légumes égouttés. Servir brûlant avec de la moutarde forte.', 764, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (212, 'Pot-au-feu à la flamande', NULL, 15, 150, 0, 0, 'Préparation : Faire griller les oignons ou four, sans les éplucher.  La peau brunie donnera une belle couleur au bouillon.  Remplir une grande cocotte d''eau froide, ajouter les oignons, le thym, le laurier, le bouquet de persil (queues comprises), les échalotes épluchées, le mouton et la queue de bœuf. Amener à ébullition et enlever l''écume grise à l''aide d''une écumoire. Saler, poivrer, puis ajouter tous les légumes nettoyés, le gîte de bœuf et le jarret de veau. Laisser cuire à petit feu, pendant 2 heures environ.  Au fur et à mesure que les légumes sont cuits, les enlever avec l''écumoire et les réserver dans un peu de bouillon.  Une demi-heure avant le repas, éplucher et faire cuire les pommes de terre dans un peu de bouillon que vous aurez prélevé dans la cocotte.  Juste avant de servir, réchauffer rapidement les légumes dans le bouillon. Découper la viande et la dresser sur le plat de service, entourée des légumes et des pommes de terre. Servir très chaud, accompagné de moutarde, de cornichons et, si vous aimez la moelle, de toasts à la moelle (faire cuire les os dans le bouillon une dizaine de minutes avant le repas). ', 382, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (213, 'Potage verdutestte', NULL, 10, 30, 0, 0, 'Préparation : Nettoyer les différents légumes, les laver et les hacher. Les mettre fondre dans la moitié de beurre, puis les mouiller avec le liquide chaud. Ajouter les pommes de terre épluchées et coupées en gros dés. Assaisonner et faire cuire 30 minutes. Passer et ajouter le reste du beurre, ainsi que le persil haché.', 211, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (214, 'Potage aux salsifis', NULL, 15, 95, 0, 0, 'Préparation : Si vous n''utilisez pas des salsifis surgelés, déjà nettoyés, gratter les légumes, les couper en tronçons et les laver dans de l''eau vinaigrée. Les égoutter et les éponger. Faire chauffer la moitié de beurre dans une grande casserole et y mettre à blondir, les morceaux de salsifis. Les couvrir de la farine et laisser cette dernière s''imprégner de beurre sans prendre couleur. Mouiller avec le bouillon chaud et porter à ébullition. Peler les pommes de terre, les laver, les couper en morceaux et les ajouter dans la casserole. Faire cuire à petit feu pendant 1 h 1/2, jusqu''à ce que les salsifis soient bien cuits. Passer le potage et le remettre sur le feu. Y ajouter le lait chaud, le jus de citron et rectifier l''assaisonnement. Hors du feu, incorporer le reste du beurre et le persil haché.', 263, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (215, 'Potage printanier', NULL, 15, 30, 0, 0, 'Préparation : Gratter les carottes et les découper en petits dés. Peler les navets et les découper en bâtonnets. Nettoyer les haricots et les asperges. Découper le chou-fleur en bouquets. Plonger tous les légumes (y compris les petits pois écossés) dans le consommé bouillant. Rectifier l''assaisonnement avec le sel et le poivre. Couvrir et laisser cuire à petit feu, pendant 30 minutes. Au moment de servir, parsemer de cerfeuil finement haché.', 835, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (216, 'Potage au potiron', NULL, 15, 45, 0, 0, 'Préparation : Eplucher le potiron et le couper en dés. Hacher finement les oignons. Peler les tomates, les couper en morceaux. Faire fondre le beurre dans une grande casserole et y mettre blondir les oignons hachés, pendant 2 minutes, sans qu''ils prennent couleur. Ajouter les dés de potiron, le bouillon et le lait et amener à ébullition. Incorporer les tomates, le sucre, le bouquet garni. Après 15 minutes de cuisson, ajouter la crème de riz délayée dans un peu de lait froid et continuer la cuisson pendant 5 minutes. Passer au tamis ou au moulin, remettre sur le feu, ajouter la crème, rectifier l''assaisonnement et retirer au premier bouillon. Parsemer de pluches de cerfeuil.', 236, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (217, 'Potage aux poivrons', NULL, 15, 35, 0, 0, 'Préparation : Nettoyer et couper les légumes en petits morceaux. Faire cuire 30 minutes dans 3/4 du bouillon. Passer le potage au tamis et ajouter le quart de bouillon restant, dégraisser. Enlever graines et filaments des poivrons, les couper en fines lamelles comme des allumettes. Les faire pocher trois minutes dans de l''eau bouillante salée. Égoutter et les ajouter au bouillon. Réchauffer le tout 5 minutes à feu vif, saler et poivrer.', 130, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (224, 'Potage madrilène', NULL, 10, 50, 0, 0, 'Préparation : Hacher l''oignon et le faire dorer dans la moitié de beurre, dans une marmite. Y ajouter les tomates et leur jus, le bouillon, ainsi que bouquet garni, ail et grains de poivre écrasés. Amener à ébullition et laisser cuire à couvert pendant environ 40 minutes. Passer le potage au tamis. Faire dissoudre la fécule dans le jus de citron et l''ajouter dans la marmite, ainsi que le sherry. Ramener à ébullition et rectifier l''assaisonnement. Terminer par l''addition du reste de beurre. Fouetter la crème en neige ferme, avec le cerfeuil haché, et en mettre une cuillère au centre de chaque assiette servie.', 345, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (228, 'Potage aux flocons d''avoine', NULL, 5, 35, 0, 0, 'Préparation : Faire chauffer le beurre dans une poêle et y mettre à dorer les flocons d''avoine, en remuant avec la cuillère en bois et en évitant de les laisser noircir. Mouiller avec un peu de bouillon et verser le tout dans le reste du bouillon très chaud. Amener à ébullition et laisser cuire 25 à 30 minutes à feu doux et à couvert. Rectifier l''assaisonnement. Au choix, passer ce potage ou le servir tel quel, en y ajoutant les herbes hachées.', 175, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (230, 'Potage à l''estragon', NULL, 10, 15, 0, 0, 'Préparation : Dans une grande casserole, mettre l''eau, faire bouillir et ajouter les cubes de bouillon. Nettoyer et égoutter l''estragon (garder quelques feuilles entières pour la décoration). Plonger l''estragon dans le bouillon. Laisser cuire à petit feu, pendant 10 minutes. Retirer l''estragon du bouillon. Dans une tasse d''eau, délayer la fécule de maïs et verser dans le potage. Remuer avec une cuillère en bois. Dans une soupière, verser la crème, puis le liquide bouillant par-dessus. Saupoudrer de persil finement haché et de quelques feuilles d''estragon. Servir bien chaud.', 308, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (231, 'Potage crème persillée', NULL, 10, 45, 0, 0, 'Préparation : Peler l''oignon et le hacher. Eplucher et laver les poireaux puis les détailler en tronçons. Hacher le persil; écraser la gousse d''ail. Faire blondir oignon et poireaux dans la moitié de beurre. Y mettre à fondre le persil dont vous réserverez la valeur d''une cuillère à soupe pour l''ajouter au potage en fin de cuisson. Rajouter le reste de beurre, verser la farine et la laisser cuire quelques instants, sans prendre couleur, en remuant. Mouiller avec le bouillon, ajouter l''ail et amener à ébullition. Faire cuire à petit feu pendant 30 minutes. Enlever la gousse d''ail. Ajouter la crème et rectifier l''assaisonnement en sel et en poivre. Parsemer de persil haché, réservé et, au choix, accompagner de petits croûtons frits.', 436, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (232, 'Potage crème de navets', NULL, 10, 65, 0, 0, 'Préparation : Hacher l''oignon nettoyé, émincer le blanc de poireaux et les faire blondir dans le beurre. Ajouter la farine et, quand elle a cuit quelques instants, mouiller avec le bouillon et porter à ébullition. Eplucher navets et pommes de terre, les couper en morceaux et jeter ceux-ci dans le liquide bouillant. Rectifier sel et poivre. Après environ 1 heure de cuisson, passer le potage. Le ramener à ébullition puis ajouter le jaune d''œuf battu dans la crème et parsemer de pluches de cerfeuil.', 195, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (233, 'Potage crème de cresson', NULL, 10, 15, 120, 0, 'Préparation : Effeuiller le cresson, laver les feuilles et en réserver quelques unes pour la décoration. Faire cuire le reste des feuilles dans le bouillon, pendant 15 minutes environ. Passer au mixer, ajouter le lait et la crème de riz et ramener à ébullition. Verser en soupière. Mettre au frais et, avant de servir, incorporer la crème. Rectifier l''assaisonnement et ajouter les tomates pelées, coupées en petits dés, et le persil haché. Garnir avec les feuilles de cresson réservées.', 213, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (234, 'Potage crème de concombre', NULL, 10, 35, 0, 0, 'Préparation : Peler les concombres, en couper la chair en petits dés. Dans une grande casserole, faire chauffer le beurre et y mettre à blondir les oignons hachés, sans qu''ils prennent couleur. Après 2 minutes, ajouter les dés de concombre, moins une petite partie que vous réserverez pour la garniture, puis le bouillon chaud et laisser mijoter à découvert pendant 25 minutes environ. Passer au tamis. Ramener le potage passé à ébullition et y incorporer, peu à peu et en mélangeant bien, la crème de riz délayée dans un peu d''eau ou de bouillon froid. Laisser épaissir 5 minutes. Rectifier l''assaisonnement. Verser en soupière, sur les jaunes d''œufs battus avec la crème. Ajouter alors les dés de concombre réservés et faire chauffer doucement, sans bouillir. Parsemer de ciboulette hachée.', 336, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (239, 'Potage au cerfeuil', NULL, 10, 30, 0, 0, 'Préparation : Eplucher les pommes de terre et les laver. Les couper en grosses rondelles et les faire cuire dans une casserole, jusqu''à ce qu''elles soient tendres, dans un bon litre d''eau. Pendant ce temps, laver le cerfeuil et le hacher grossièrement. L’ajouter aux pommes de terre quand celles-ci sont cuites et laisser mijoter à feu doux pendant quelques minutes. Passer le potage au mixer, jusqu''à ce que tout soit bien broyé. Ajouter la crème et le beurre. Mélanger. Goûter et rectifier l''assaisonnement. Servir très chaud.', 776, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (670, 'Pâte à frire à la bière', NULL, 15, 0, 0, 60, 'Préparation : Placer la farine dans un bol, creuser une fontaine au centre et mettre le jaune d''oeuf, le sel, le poivre. Incorporer, peu à peu, la bière légèrement tiédie. Laisser reposer au moins 1 heure. Au moment d''utiliser la pâte, ajouter le blanc d''oeuf battu en neige. Vous devez obtenir une pâte mollette pouvant bien napper les aliments qu''elle est destinée à enrober.', 1236, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (242, 'Porc au curry et à l''ananas', NULL, 10, 25, 0, 0, 'Préparation : Faire cuire le riz dans le bouillon.  Couper la viande en petits dés, les assaisonner et les faire rissoler dans la moité du beurre et l''huile, sur toutes les faces, environ 5 à 6 minutes, jusqu''à ce qu''ils soient bien cuits.  Les enlever à l''écumoire.  Mettre dans la poêle l''oignon haché menu et le curry en poudre, puis la farine et laisser bien brunir, en remuant.  Mouiller avec eau ou bouillon et laisser cuire 5 minutes.  Ajouter à cette sauce l''ananas coupé en morceaux, son jus, et les dés de viande pour les réchauffer.  Incorporer le reste du beurre au riz cuit, en rectifier l''assaisonnement et le dresser en couronne.  Au centre, verser le ragoût de porc en sauce. ', 664, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (243, 'Porc aux bananes', NULL, 10, 90, 0, 0, 'Préparation : Dans une grande cocotte, faire revenir la viande à feu vif, dans l''huile et le beurre. Ajouter les oignons coupés en rondelles épaisses. Lorsque le tout a pris une belle couleur dorée, ajouter le bouquet garni, saler et poivrer. Mouiller avec le bouillon. Couvrir et laisser cuire pendant 1 heure 15 minutes, en arrosant de temps en temps. Avant de servir, dégraisser la sauce et ajouter alors le citron et le piment. Servir le porc entouré des bananes revenues au beurre dans une poêle.', 779, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (244, 'Pommes de terre nouvelles', NULL, 10, 30, 0, 0, 'Préparation : Gratter les pommes de terre (ou les cuire avec la pelure, après les avoir bien lavées, puis les éplucher). (Dans ce cas le temps de cuisson ne doit pas dépasser 15 minutes). Couper les pommes de terre crues (ou à peine cuites). Les mettre dans une grande poêle avec une bonne partie du beurre. Saler. Remuer de temps en temps. Ajouter les petits oignons nettoyés, le saucisson en tranches et le poivron découpé en fines lanières. (Ne pas oublier de laver, d''épépiner le poivron). Ajouter le reste de beurre. Couvrir la poêle et laisser mijoter 30 minutes. Au moment de servir, saupoudrer de ciboulette.', 881, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (245, 'Poivrons farcis à la grecque', NULL, 25, 25, 0, 0, 'Préparation : Laver et essuyer les poivrons. Les couper en deux et ôter les graines. Faire cuire le riz, en le chauffant d''abord dans l''huile, puis en le mouillant à hauteur de bouillon chaud. Faire griller les aubergines, les peler, enlever les graines et écraser la chair. Mélanger le hachis de veau avec le riz cuit, les œufs, la purée d''aubergines, l''ail haché menu, l''origan, sel et poivre et en farcir les demi-poivrons. Arroser d''un peu d''huile et passer 25 minutes à four moyen, dans un plat contenant le reste de l''huile.', 425, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (246, 'Poivrons farcis', NULL, 25, 45, 0, 0, 'Préparation : Laver et vider les poivrons (enlever les graines qui collent à la chair, couper les calottes). Les couper en 2. Mélanger la viande, l''œuf, le pain trempé dans le lait, les oignons et les fines herbes. Ecraser l''ail et l''ajouter au hachis. Saler, poivrer. Farcir les poivrons en faisant un petit dôme bien bombé. Couvrir les dômes de chapelure, ajouter une noisette de beurre sur chacun d''eux. Déposer dans un plat allant au four. Glisser dans le four moyen (thermostat 5), pendant 45 minutes, en surveillant de temps en temps la cuisson.', 733, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (247, 'Poitrine de veau farcie', NULL, 25, 90, 0, 0, 'Préparation : Dans une grande terrine, émietter le pain et l’arroser avec le lait. Quand il est bien détrempé, casser l''œuf par dessus et ajouter les viandes hachées, les champignons, les oignons, le persil et l''ail hachés. Saler et poivrer, puis pétrir bien avec les petits fromages blancs. Quand tous les ingrédients sont bien amalgamés, remplir la poitrine de veau (que vous aurez fait ouvrir en portefeuille par votre boucher) avec cette farce. Recoudre la viande sans la déchirer, puis la déposer dans un plat allant au four. L’enduire largement de beurre et faire cuire 1 heure 30 minutes au moins à four chaud. Arroser de temps en temps la viande avec 2 ou 3 cuillères de bouillon. Quand la viande est cuite, la réserver au chaud et déglacer le plat avec le porto, en grattant bien le fond avec une cuillère en bois. Couper la viande en tranches et la présenter sur le plat de service, avec la sauce en saucière. Servir des carottes forestières, de l''épinard ou un gratin dauphinois en accompagnement.', 652, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (251, 'Omelette aux salsifis', NULL, 10, 15, 0, 0, 'Préparation : Faire cuire les salsifis dans un doigt d''eau bouillante salée. Dès qu''ils sont tendres, les égoutter. Faire fondre la moitié du beurre dans une grande poêle et y jeter les légumes.  Les faire sauter rapidement jusqu''à ce qu''ils prennent couleur.  Pendant ce temps, casser les oeufs dans une terrine et les battre avec le lait, du sel et du poivre. Faire cuire l''omelette à feu vif dans le reste du beurre. Quand il ne reste plus qu''un peu de liquide à la surface, la garnir avec les salsifis.  Saupoudrer abondamment de persil, puis faire glisser l''omelette en la repliant sur le plat de service. ', 925, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (253, 'Pilaf d''agneau au riz', NULL, 20, 105, 0, 0, 'Préparation : Désosser la viande et la couper en dès. Les mettre à dorer en sauteuse dans la moitié du beurre et 1/3 de l''huile. Peler et émincer les oignons. Les ajouter à la viande, les laisser prendre couleur puis mouiller avec la moitié du bouillon bien assaisonné. Faire cuire à couvert et à petit feu pendant 45 minutes, en ajoutant le girofle et le romarin ainsi que la menthe. Faire chauffer dans une cocotte le beurre et l''huile restants. Y verser le riz non lavé mais essuyé et, en remuant avec la cuillère en bois, laisser les grains blondir et s''enrober de graisse. Mouiller alors avec le reste du bouillon chaud, ajouter du poivre et la viande. Couvrir, mettre la cocotte au four moyen et laisser cuire environ 1 h, jusqu''à ce que viande et riz soient tendres à point. Verser le tout dans un grand plat et accompagner au choix de haricots verts princesses ou de tomates poêlées. En principe, le riz devrait avoir absorbé presque tout le bouillon. Si ce n''était pas le cas, retirer à la cuillère l''excédent de liquide.', 712, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (254, 'Pigeons dans le nid', NULL, 30, 40, 0, 0, 'Préparation : Laver et essuyer les pigeons intérieurement et extérieurement. Tourner en crème la moitié du beurre ramolli et le mélanger avec le sel, du poivre, l''oignon râpé, thym, estragon et persil. Saler et poivrer l''intérieur des pigeons, puis le tartiner avec le beurre assaisonné. Brider les oiseaux et pendant 10 minutes, les faire dorer sur toutes les faces dans le plat à rôtir, avec les 3/4 du reste du beurre et l''huile. Couvrir alors chacun des pigeons d''une fine barde de lard et mettre le plat dans le four chaud préchauffé, pendant 12 à 15 minutes. Réduire ensuite la température et poursuivre la cuisson à chaleur moyenne, 10 à 15 minutes supplémentaires, plus ou moins selon que vous préférez les pigeons bien cuits ou encore rosés. Arrosez souvent. Auparavant, vous aurez pelé les pommes de terre et les aurez fait bouillir dans de l''eau salée. Les passer en purée sèche sans addition de lait, en y ajoutant la moitié des jaunes d''oeufs, le beurre, sel et poivre. La façonner en forme de six nids, que vous badigeonnerez au pinceau avec le reste des jaunes d''oeufs dilués dans du lait. Poser ces nids sur la plaque du four beurrée. Débridez les pigeons cuits et gardez-les au chaud. Passer vivement les nids dans le four très chaud, pour les dorer et les chauffer en même temps. Déglacer la cuisson des pigeons avec le bouillon, amener à ébullition, dégraisser à la cuillère et faire cuire dans ce jus les airelles lavées pendant 8 minutes environ, en y ajoutant le sucre. Poser les pigeons dans leurs nids, les entourer des airelles enlevées à l''écumoire et servir le jus en saucière. Vous pouvez remplacer les bardes de lard par des tranches fines de lard salé, coupées en deux. Des myrtilles ou des raisins blancs pelés remplaceront les airelles. Celles-ci, pour gagner du temps, peuvent être cuites par avance dans un sirop.', 1064, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (255, 'Les pigeonneaux de printemps', NULL, 20, 60, 0, 0, 'Préparation : Dans une grande cocotte faire dorer les pigeonneaux avec le beurre. Découper les oignons en fines lamelles et les ajouter aux pigeonneaux. Lorsque les oignons sont devenus transparents, ajouter les carottes grattées et découpées en rondelles, les tomates coupées en quatre. Couvrir avec le bouillon, ajouter l''ail finement haché. Saler et poivrer. Laisser mijoter 1 heure à feu doux. Servir sur un plat préchauffé. Les volailles seront entourées de petits légumes.', 1125, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (256, 'Perdreaux vigneronne', NULL, 30, 30, 0, 0, 'Préparation : Saler et poivrer intérieurement et extérieurement les oiseaux préalablement plumés, vidés et flambés et dont vous aurez retiré le foie. Les couvrir chacun de 2 feuilles de vigne puis d''une barde de lard et les ficeler. Faire dorer et cuire les perdreaux en cocotte dans les 3/4 du beurre pendant environ 20 minutes. Les retirer et les garder au chaud. Peler et épépiner les raisins. Ajouter le reste de beurre dans la cocotte, ainsi que les grains de raisins. Faire chauffer 5 minutes et ajouter ensuite le cognac. Couper les perdreaux en deux. Poser chaque moitié sur un toast préalablement humecté d''un peu de jus de cuisson. Entourer des raisins enlevés à l''écumoire et déglacer la sauce si besoin est avec un peu d''eau. Verser cette sauce sur les raisins ou la servir à part. La chair des perdreaux doit être encore un peu rosée, sans excès.', 833, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (257, 'Perdrix aux choux', NULL, 40, 135, 0, 0, 'Préparation : Couper les trognons des choux, enlever les grosses côtes et les feuilles flétries puis couper les légumes en quatre. Les plonger 1 minute dans de l''eau bouillante non salée, pour les blanchir. Jeter cette eau, la remplacer par de l''eau bouillante salée et faire cuire les choux pendant 15 minutes. Egoutter et presser pour enlever l''excédent de liquide. Saler et poivrer l''intérieur et l''extérieur des perdrix, les brider et les piquer de lardons découpés dans le morceau de lard. Faire chauffer le beurre dans une cocotte, mettre à dorer le reste du lard émincé en tranches, puis les perdrix. Peler les oignons et les couper en rondelles, gratter les carottes, les diviser en bâtonnets et les mettre dans la cocotte à dorer avec les perdrix et le lard. Dès que les légumes ont pris de la couleur, les enlever ainsi que les perdrix et le lard. Les remplacer par la farine et mouiller celle-ci avec le vin et le bouillon. Ajouter le sel et le poivre et réserver cette sauce. Nettoyer la cocotte ou en prendre une autre. Remettre la moitié du lard dans la cocotte, poser dessus la moitié des choux, un peu de sauce, de légumes, puis les perdrix, côte à côte, le reste du lard, des choux, des légumes et de la sauce. Couvrir la cocotte et faire cuire au four pendant 1 h 30. Ajouter le saucisson à cuire et continuer la cuisson 30 minutes. Sortir les perdrix, les découper et les disposer sur un lit de chou au lard et aux légumes. Garnir avec le saucisson coupé en tranches.', 710, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (259, 'Pamplemousses farcis', NULL, 15, 0, 30, 0, 'Préparation : Couper les fruits en deux. Avec un couteau pointu, détacher les segments de chair des membranes qui les enserrent. Evider ensuite les demi-pamplemousses en ayant soin de ne pas percer l''écorce. Retourner ensuite les fruits sur un plat pour les égoutter. Verser les coeurs de palmiers dans une passoire et les couper en petits tronçons égaux. Les mélanger avec la chair des pamplemousses ainsi qu''avec les crevettes tranchées en deux. Préparer la sauce, délayer le jus de citron avec la même quantité de jus de pamplemousse recueilli, la sauce Tabasco. Incorporer l''huile et tourner dans le même sens pour obtenir une émulsion. Fouetter la crème et l’ajouter à cette sauce. Mélanger délicatement le tout, fruits, crevettes, coeurs de palmier et sauce, et farcir les demi-pamplemousses. Parsemer de noix hachées grossièrement et servir frais.', 565, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (260, 'Pamplemousses au crabe et à l''avocat', NULL, 15, 0, 30, 0, 'Préparation : Couper les pamplemousses en deux et détacher la chair. Oter les peaux et la détailler en cubes. Retirer la peau des avocats et détailler la chair en cubes. Arroser ces cubes d''avocats de jus de citron. Laver le coeur de laitue, garder de belles feuilles dont vous tapisserez le fond des demi-pamplemousses. Couper le reste en fines lanières. Incorporer le ketchup, le whisky et la sauce anglaise à la mayonnaise en tournant bien. Dans un saladier, mélanger la chair des pamplemousses, les lanières de laitue et le crabe et les cubes d''avocats citronnés, en ajoutant de la sauce cocktail petit à petit. Goûter. Quand vous jugez qu''il y a suffisamment de sauce, répartir la préparation dans les pamplemousses. Mettre une demi-heure au réfrigérateur avant de servir.', 629, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (261, 'Pamplemousses aux crevettes', NULL, 15, 0, 30, 0, 'Préparation : Couper les pamplemousses en deux et détacher la chair. Oter les peaux et la détailler en cubes. Laver le coeur de laitue, garder des belles feuilles dont vous tapisserez le fond des demi-pamplemousses. Couper le reste en fines lanières. Incorporer le ketchup, le whisky et la sauce anglaise à la mayonnaise en tournant bien. Dans un saladier, mélanger la chair des pamplemousses, les lanières de laitue et les crevettes, en ajoutant de la sauce cocktail petit à petit. Goûter. Quand vous jugez qu''il y a suffisamment de sauce, répartir la préparation dans les pamplemousses. Mettre une demi-heure au réfrigérateur avant de servir.', 533, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (262, 'Pamplemousses à la chinoise', NULL, 10, 0, 0, 0, 'Préparation : Laver les pamplemousses et ôter la calotte supérieure (d''un diamètre suffisamment large pour pouvoir enlever la pulpe sans abîmer le fruit). Essuyer délicatement. Retirer la chair intérieure en arrachant quartier par quartier avec la membrane blanche. Mélanger les crevettes, l''oignon finement haché, le céleri lavé et découpé en petits morceaux, la moutarde, le sel et la pincée de laurier en poudre. Ajouter la mayonnaise et mêler à la préparation quelques quartiers de pamplemousses et quelques gouttes de jus de citron, ainsi que les feuilles de laitue découpées en lanières. Remplir les pamplemousses avec cette farce et décorer avec une rondelle de tomate, ou d''oeuf dur. Déposer chacun de ces pamplemousses dans une coupe d''un diamètre nettement plus grand, mais remplie de glace pilée. Décorer avec une ombrelle « chinoise » en papier de couleur (en vente dans tous les grands magasins).', 406, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (263, 'Pain surprise de pigeons', NULL, 30, 50, 180, 1440, 'Préparation : Faire mariner les pigeons pendant 24 heures dans la marinade, en les retournant de temps à autre. Le lendemain, préparer la farce, hacher finement viandes et lard, y mélanger les oeufs entiers, le sel et le poivre. Farcir chaque oiseau d''une boulette de ce hachis, dans laquelle vous enfoncerez une baie de genévrier. Tapisser une grande terrine avec les bardes de lard en en laissant pendre à l''extérieur suffisamment pour pouvoir en recouvrir la préparation. Dans le fond de la terrine, mettre une couche de farce et disposer dessus, dans le même sens, les oiseaux farcis. Bourrer les interstices avec de la farce et en couvrir complètement les grives ou pigeons. Faire chauffer la marinade avec ses aromates et épices, la passer et ajouter la gélatine fondue. La verser sur la farce pour que le pain en soit bien imprégné. Avec le dos de la lame d''un couteau, creuser légèrement à l''endroit délimitant l''emplacement des oiseaux et marquer cette délimitation par une baie de genévrier mise au centre de la rainure. Rabattre les bardes et fermer la terrine, soit avec un couvercle approprié, soit avec du papier aluminium. Faire cuire à four assez chaud, préchauffé, et dans un bain-marie, pendant environ 50 minutes. Laisser refroidir et mettre au frais. En se servant, chacun prendra son oiseau dont l''emplacement sera bien délimité.', 885, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (264, 'Pain de chicorées de Bruxelles', NULL, 15, 90, 0, 0, 'Préparation : Nettoyer, laver les légumes et les faire cuire à la vapeur de préférence, 30 à 40 minutes (temps réduit si vous utilisez une marmite à pression ou autocuiseur). Les égoutter en pressant pour enlever le maximum d''eau puis les passer vivement dans une grande poêle où vous aurez fait chauffer 1/3 du beurre. Elles braiseront très légèrement tout en exsudant leur excédent de liquide. Hacher l''oignon épluché et le faire blondir dans 1/3 du beurre, ajouter la viande hachée, le sel et le poivre et écraser à la fourchette pendant 2 minutes pour bien opérer le mélange. Délayer la farine dans le reste de beurre chauffé, mouiller avec le lait, laisser épaissir la sauce, l’assaisonner et ajouter la muscade râpée, les oeufs battus et le mélange viande oignon. Dans un plat à gratin profond, beurré, superposer des couches de légumes assaisonnés et de viande en sauce. Faire cuire à four moyen environ 40 minutes jusqu''à ce qu''une lame de couteau piquée dans le centre du plat en ressorte sèche. Si vous préférez démouler cette préparation, nappez-la d''une sauce aux champignons par exemple ou d''une sauce blanche au fromage.', 522, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (266, 'Osso bucco', NULL, 10, 85, 0, 0, 'Préparation : Dans une grande cocotte à fond épais, faire fondre le beurre et rissoler les jarrets de veau sur les deux faces. Quand ils sont bien dorés, ajouter les oignons coupés en grosses lamelles, laisser blondir. Pendant ce temps, découper les carottes en rondelles, les tomates en morceaux, ainsi que le céleri. Ajouter les légumes dans la cocotte. Saler, poivrer. Couvrir la préparation avec un verre d''eau et le pili pili. Laisser mijoter pendant 1 heure 15 minutes sur feu doux. Vingt minutes avant la fin de la cuisson, délayer le contenu de la boîte de tomates concentrées et rectifier l''assaisonnement.', 437, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (267, 'Oranges au sirop', NULL, 25, 65, 0, 0, 'Préparation : Préparer cette recette la veille. Laver la peau des oranges en frottant bien. Les peler et réserver les plus belles écorces. Gratter la peau blanche à l''intérieur de l''écorce. Il ne doit plus vous rester que le zeste. Tailler celui-ci en fines lanières. Avec un couteau très aiguisé, peler les oranges à vif, les débarrasser de leur fine peau membraneuse. Les ranger dans une petite casserole juste de la taille. Ajouter le sucre et recouvrir d''un 1/2 lit. d''eau. Poser une serviette sur les oranges de façon à les maintenir immergées. Faire frémir doucement pendant 1 heure puis laisser refroidir dans le sirop. Mettre les zestes dans de l''eau froide et les amener à ébullition. Laisser bouillir 2 minutes, rafraîchissez sous l''eau courante et égoutter. Retirer les oranges de leur sirop et faire réduire celui-ci de moitié avec les zestes. Parfumer avec la liqueur d''orange. Mettre les oranges dans une coupe et les arroser avec le sirop. Faire rafraîchir au réfrigérateur avant de servir.', 818, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (269, 'Oranges givrées', NULL, 15, 0, 180, 0, 'Préparation : Couper les 2/3  des oranges en deux, aux 2/3 de leur hauteur. Les évider sans abîmer les écorces. Mettre celles-ci au freezer. Presser le jus de la chair des oranges évidées et ajouter le jus des oranges restantes. Vous devez obtenir environ un 1/2 lit. de jus d''orange quand celui-ci aura été sucré à votre goût. Ajouter la crème fraîche au jus d''orange et bien mélanger. Verser dans une sorbetière et mettre au freezer ou au congélateur. Quand la crème glacée a pris, sans pour autant être tout à fait dure, remplir les écorces des oranges. Remettre au froid, jusqu''à ce que la glace ait complètement prise. Sortir du réfrigérateur 20 minutes avant consommation. Vous pouvez préparer de la même façon de délicieux citrons glacés. Si vous ne possédez pas de sorbetière, verser le mélange dans un saladier, mettre au froid, puis le battre énergiquement à 3 ou 4 reprises, à 20 minutes d''intervalle.', 649, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (270, 'Omelette niçoise', NULL, 15, 15, 0, 0, 'Préparation : Peler l''oignon et l’émincer en rondelles. Trancher le poivron en deux, l’épépiner et le couper en lanières. Peler et épépiner les tomates. Peler la gousse d''ail. Faire chauffer la moitié de l''huile dans une sauteuse et y mettre à dorer les oignons et les poivrons. Ajouter ensuite les tomates concassées, la gousse d''ail écrasée, une ou deux pincées d''herbes de Provence, le sel et le poivre. Au dernier moment, ajouter la chair de thon en morceaux et les olives dénoyautées. Chauffer le reste de l''huile dans une poêle. Verser les oeufs battus avec le sel et le poivre et avec la fourchette soulever les bords en les rabattants vers le centre pour réaliser une cuisson régulière. L''omelette étant cuite, la farcir vivement avec les légumes et le thon. La plier et la glisser sur le plat de service et décorer avec les filets d''anchois.', 565, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (271, 'Omelette à la fleur d''oranger', NULL, 10, 10, 0, 0, 'Préparation : Battre les jaunes d''oeufs avec le sucre fin dans un grand bol avec un fouet. Battre les blancs d''oeufs en neige bien ferme. Incorporer les blancs en neige délicatement aux jaunes d''oeufs. Ajouter la fleur d''oranger. Piler les macarons, les ajouter au mélange. Faire fondre le beurre dans une grande poêle. Verser le mélange et le cuire en omelette sur feu très doux. Plier l''omelette en deux et garnir de cerises. Servir bien chaud.', 1484, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (272, 'Omelette espagnole', NULL, 10, 25, 0, 0, 'Préparation : Eplucher les pommes de terre, les laver, les égoutter et les essuyer. Les couper en tranches de 3 mm d''épaisseur. Peler les oignons et les émincer. Détailler la saucisse en rondelles. Verser la moitié de l''huile dans une grande sauteuse à fond épais. La laisser chauffer, sans fumer, et y jeter les rondelles de pommes de terre avec le sel et du poivre à volonté. Les laisser dorer et les retourner pour les dorer de l''autre côté. Ceci fait, ajouter les oignons et les dorer également. Au bout de 10 minutes, les légumes devraient être cuits. Les enlever à l''écumoire, en veillant à ne pas trop briser les pommes de terre et les mettre dans une passoire pour les égoutter. Faire vivement revenir et chauffer dans la sauteuse les rondelles de chorizo. Chauffer les 3/4 d''huile restant dans une grande poêle. Casser les oeufs dans un grand bol, les battre avec du sel et du poivre et y mélanger les légumes égouttés et le chorizo. Verser le tout dans la poêle en étalant avec une palette. Laisser cuire à petit feu 2 à 3 minutes, en secouant la poêle, jusqu''à formation d''une croûte dorée. Poser un grand couvercle sur la poêle, renverser l''omelette. Chauffer dans la poêle l''huile restante et faire glisser l''omelette, la face non dorée se trouvant en dessous. Laisser cuire 2 à 3 minutes et glisser l''omelette sur le plat de service. Vous pouvez aussi préparer des petites omelettes ou tortillas individuelles', 885, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (273, 'Omelette aux crevettes', NULL, 5, 15, 0, 0, 'Préparation : Battre les oeufs dans une grande terrine avec la crème, le sel et le poivre. Faire fondre le beurre dans une grande poêle et versez-y l''omelette quand il grésille. Faire cuire à feu vif. Dès que les bords de l''omelette commencent à prendre, les rabattre vers le centre de la poêle avec une spatule en bois. Continuer la cuisson jusqu''à ce que l''omelette soit pratiquement tout à fait prise. Il vous faudra peut-être réduire un peu le feu vers la fin pour éviter que le dessous ne brunisse trop. Quand il ne reste plus qu''un peu de liquide à la surface, retirer la poêle du feu. Parsemer la surface de crevettes et saupoudrer abondamment de persil haché. Décoller l''omelette de la poêle avec une spatule délicatement, pour ne pas la déchirer, puis la faire glisser sur un plat en la repliant. Servir immédiatement. Vous pouvez aussi servir l''omelette comme plat de résistance en l''accompagnant de pommes frites ou de pommes rôties à la poêle et d''un légume éventuellement. Les farces peuvent varier à l''infini: crabe en boîte, jambon, restes de poulet, fromage, pommes de terre rôties + jambon + rondelles de tomates passées au beurre, épinards, reste de béchamel, etc.', 854, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (274, 'Omelette aux champignons', NULL, 5, 25, 0, 0, 'Préparation : Nettoyer les champignons, les laver rapidement à l''eau légèrement vinaigrée. Faire fondre la moitié du beurre dans une casserole et mettre les champignons émincés. Couvrir et laisser cuire quelques minutes jusqu''à ce que les champignons aient rendu leur eau. Ensuite, ajouter la crème fraîche, saler, poivrer et laisser mijoter une dizaine de minutes à feu doux. Dans une grande terrine, casser les oeufs, salez, poivrer, ajouter le lait puis battre rapidement pendant 1 minute. Faire fondre le restant du beurre dans une grande poêle. Quand il est chaud, verser les oeufs et faire cuire à feu vif. Quand les bords de l''omelette commencent à prendre, les ramener vers le centre de la poêle avec une spatule en bois pour que le restant du liquide puisse cuire aussi. Quand il ne reste plus qu''une mince pellicule de liquide à la surface de l''omelette, la retirer du feu. Répartir les champignons sur toute la surface puis décoller délicatement l''omelette de la poêle en vous aidant d''une spatule. Faire glisser la moitié de celle-ci sur le plat de service, puis replier l''autre par dessus. Servir immédiatement.', 806, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (275, 'Oignons à la grecque', NULL, 15, 45, 0, 0, 'Préparation : Eplucher les oignons et couper le bulbe de fenouil en tous petits dés. Dans une petite casserole, faire chauffer l''huile, le vin blanc et le concentré de tomates. Ajouter les oignons, le fenouil, les aromates, le sucre, le sel et le poivre et laisser cuire doucement à couvert pendant 3/4 d''heure. Retirer le thym et le laurier et laissez refroidir. Servir dans un ravier à hors-d''oeuvre. Préparer les oignons à la grecque deux jours à l''avance, pour qu''ils puissent bien s''imprégner du parfum de la sauce.', 1006, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (276, 'Oignons farcis', NULL, 10, 40, 0, 0, 'Préparation : Eplucher les oignons. Préparer une entaille circulaire (avec la pointe d''un bon couteau) dans chaque oignon. Faire cuire les oignons pendant 15 minutes, dans de l''eau bouillante salée. Les égoutter soigneusement. Creuser les oignons en suivant la ligne de l''entaille. Enlever la chair intérieure avec une petite cuillère. Remplir l''oignon avec la farce poivrée et salée. Placer les oignons dans un plat à gratin, les saupoudrer de chapelure et les arroser de beurre fondu. Laisser cuire à feu moyen (thermostat 5) pendant 25 minutes en arrosant de beurre fondu de temps en temps.', 548, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (279, 'Oeufs sauce verte', NULL, 5, 20, 0, 0, 'Préparation : Hacher très fin les herbes et l''échalote. Verser la crème dans un poêlon et ajouter le hachis d''herbes. Saler, poivrer et laisser frémir quelques instants à petit feu. Faire cuire les oeufs de préférence dans des petites cocottes ou plats individuels, dans lesquels vous aurez fait fondre une noisette de beurre. Quand le blanc des oeufs est pris, répartir la crème aux herbes dans les cocottes et servir aussitôt.', 938, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (281, 'Oeufs pochés sur toasts', NULL, 5, 10, 0, 0, 'Préparation : Faire pocher les oeufs dans l''eau bouillante additionnée du filet de vinaigre. Faire griller les toasts. Dans une casserole, faire chauffer la crème avec une pincée de sel. Nettoyer les champignons et les découper en fines lamelles, les ajouter à la crème. Dresser un oeuf sur chaque toast et le napper de champignons à la crème. Parsemer généreusement d''estragon haché. Servir chaud.', 837, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (282, 'Oeufs pochés tartare', NULL, 10, 15, 0, 0, 'Préparation : Faire bouillir dans une casserole à fond plat un litre d''eau et trois cuillères à soupe de vinaigre. Casser un oeuf dans une tasse et le glisser vivement dans l''eau vinaigrée bouillante en rabattant le blanc sur le jaune. Procéder de même pour les autres oeufs et les laisser cuire 4 minutes dans le liquide frémissant. Enlever les oeufs à l''écumoire et les déposer sur un linge. Les parer en régularisant les bords. Préparer la sauce tartare en faisant une mayonnaise moutardée. Ajouter ensuite les autres ingrédients. Hacher la viande, mélanger quatre cuillères à soupe de la sauce, le sel et le poivre, la sauce anglaise (deux à trois cuillères à café). Tartiner de viande les toasts beurrés, poser sur chacun d''eux un oeuf poché au centre et les napper de sauce tartare. Ce plat peut être servi chaud. La viande hachée, façonnée en six galettes, peut être cuite à la poêle, déposée sur les toasts chauds et garnie des oeufs encore chauds. Napper d''une sauce béchamel additionnée des ingrédients tartares.', 1866, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (285, 'Oeufs dans le nid', NULL, 10, 25, 0, 0, 'Préparation : Casser les oeufs et séparer les blancs des jaunes et les mettre dans des récipients différents. Battre les blancs d''oeufs en neige avec le sel et un peu de poivre blanc moulu. Les étaler dans un plat à gratin beurré. Creuser dans la neige de blancs d''oeufs des cavités à espace régulier. Hacher le jambon et le répartir dans ces cavités. Poser dessus un jaune d''oeuf, sans le briser, l’assaisonner de sel et de poivre puis couvrir chaque jaune d''un peu de fromage râpé et d''une noisette de beurre. Eparpiller le reste du fromage sur les blancs d''oeufs et faire cuire à four doux environ 25 minute jusqu''à ce que les blancs d''oeufs soient pris et légèrement gratinés. Servir immédiatement.', 1069, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (286, 'Oeufs à la napolitaine', NULL, 5, 20, 0, 0, 'Préparation : Cuire les oeufs pour qu''ils deviennent bien durs (environ 10 minutes). Les laisser refroidir. Faire cuire le riz à l''eau bouillante, pendant 12 minutes. Pour donner plus de goût au riz, ajoutez un petit cube de bouillon dégraissé dans l''eau bouillante de cuisson. Mélanger le riz avec la moitié du gruyère râpé. Placer le riz dans un plat à gratin. Disposer dessus les oeufs coupés en deux, dans le sens de la longueur. Saupoudrer avec le reste de gruyère et des petits morceaux de beurre. Faire dorer à four vif, pendant 10 minutés. Servir bien chaud garni de fines herbes.', 1423, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (566, 'Agneau au curry', NULL, 10, 0, 50, 0, 'Préparation : Saler et poivrer la viande piquée d''éclats d''ail. Faire chauffer l''huile dans une sauteuse. Ajouter la viande en la couvrant des 3 oignons coupés fins. Ajouter le curry et remuer. Mouiller la viande d''un verre de bouillon de viande, couvrer et laisser mijoter 3/4 d''heure. Ajouter ensuite le yaourt et la cannelle, rectifier l''assaisonnement. On peut accompagner ce plat de riz, arrosé lui aussi par cette délicieuse sauce.', 671, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (293, 'Melon au jambon et au porto', NULL, 15, 0, 0, 0, 'Préparation : Couper chaque melon en deux et enlever toutes les graines. Au centre des cavités, verser une cuillère de porto et laisser macérer. Ciseler le jambon en fines lanières,les répartir dans les cavités et napper de crème fouettée additionnée de sel et d''une pincée de gingembre. Servir très frais.', 355, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (294, 'Melon farci à l''indienne', NULL, 10, 0, 0, 0, 'Préparation : Diviser le melon en 6 tranches égales. Enlever et jeter les graines. Ajouter à la mayonnaise le contenu du petit pot de yogourt, le curry, un peu de sel. Mélanger à cette sauce la chair de poulet coupée en petits dés, ainsi que les crevettes. Farcir les tranches de cette préparation.', 577, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (295, 'Melon au cocktail de fruits de mer', NULL, 20, 0, 0, 0, 'Préparation : Couper le melon en deux,en retirer et jeter les graines, puis découper la chair en petits dés. Préparer la sauce en ajoutant à la mayonnaise les sauces anglaise et tomate, puis le whisky. Couper en petits morceaux la chair de crabe, en tranches celle des langoustines et les mélanger, ainsi que les crevettes à la sauce cocktail. Y incorporer, pour terminer, la crème fouettée et répartir le tout dans les demi melons préalablement mis au frais.', 820, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (298, 'Maquereaux au vin blanc', NULL, 15, 10, 0, 0, 'Préparation : Si le poissonnier ne l''a déjà fait, couper têtes et queues des poissons ainsi que les nageoires. Les vider, les laver et les sécher au papier absorbant. Dans une feuille de papier blanc, mettre quelques cuillères à soupe de farine, du sel, du poivre, et y passer les poissons successivement pour les enrober de cette farine assaisonnée. Secouer pour enlever l''excédent. Faire chauffer dans une grande poêle les 3/4 du de beurre et l''huile. Y mettre à dorer les maquereaux, sur les deux faces, 5 à 7 minutes de chaque côté suivant leur grosseur. Enlever les poissons à l''écumoire et les garder au chaud. Ajouter dans la poêle le reste du beurre et les échalotes pelées et hachées finement, pendant 1 minute. Mouiller avec le vin blanc, le vinaigre et incorporer la moutarde. Rectifier l''assaisonnement et porter à courte ébullition. Verser cette sauce sur les maquereaux et parsemer de persil haché.', 814, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (567, 'Agneau en chapelure', NULL, 15, 45, 0, 0, NULL, 927, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (303, 'Lapin sauce poulette', NULL, 10, 70, 0, 0, 'Préparation : Couper le lapin en morceaux et les faire cuire très doucement dans le beurre fondu. Ils ne doivent pas prendre couleur. Ajouter les oignons hachés finement et les carottes coupées en rondelles. Les laisser fondre doucement. Quand les oignons sont transparents, saupoudrer le tout avec la farine. Mélanger bien et laisser blondir légèrement. Mouiller avec le vin blanc et ajouter de l''eau, jusqu''à ce que la viande soit couverte. Saler, poivrer, ajouter le thym, le laurier, les queues de persil. Puis laisser cuire très doucement, pendant 1 heure. 1/4 d''heure avant la fin de la cuisson, ajouter les champignons, lavés et coupés en quatre. Quand le lapin est cuit, le déposer sur le plat de service préchauffé. Hors du feu, ajouter les jaunes des œufs délayés dans la crème, en mélangeant rapidement. Laisser cuire, sans faire bouillir, pendant quelques instants, en mélangeant bien. Ajouter le jus de citron. Napper le lapin avec cette sauce et servir immédiatement.', 669, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (304, 'Lapin à la moutarde', NULL, 10, 40, 0, 0, 'Préparation : Couper le lapin en morceaux et les fariner. Les faire dorer dans une cocotte épaisse, dans laquelle vous aurez fait fondre le beurre. Quand la viande a bien pris couleur, la retirer et la remplacer par les carottes et l''oignon émincés. Faire cuire doucement, jusqu''à ce que les oignons soient transparents. Remettre la viande dans la cocotte, ajouter le thym, le laurier, les queues du bouquet de persil, puis mouiller avec le vin blanc et de l''eau jusqu''à mi-hauteur. Saler, poivrer, puis couvrir et amener à ébullition. Remettre au four très chaud, pendant 30 minutes. Quand le lapin est cuit, déposer les morceaux sur le plat de service préchauffé. Tenir au chaud. Ajouter la crème dans la cocotte et faire réduire rapidement, jusqu''à ce que la sauce ait la consistance désirée. Passer la sauce et, hors du feu, ajouter la moutarde. Goûter et rectifier l''assaisonnement. Verser sur les morceaux de lapin et servir très chaud, abondamment saupoudré de persil.', 808, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (306, 'Lapin au cidre', NULL, 15, 30, 0, 0, 'Préparation : Couper le lapin en morceaux et les faire dorer dans le beurre. Quand ils ont bien pris couleur, les retirer et les remplacer dans la cocotte par le lard coupé en lardons, les oignons et les échalotes hachés. Laisser fondre doucement puis, quand les oignons sont transparents, ajouter les champignons lavés et coupés en quatre. Quand les champignons ont rendu toute leur eau, remettre le lapin dans la cocotte, mouiller avec le cidre. Saler, poivrer, ajouter l''ail, le thym et le laurier et laisser cuire à petit feu. Lorsque la viande est cuite, déposer les morceaux sur le plat de service. Si la sauce est trop liquide, la lier avec une cuillère à café de fécule délayée dans un peu d''eau. Napper le plat de sauce et servir immédiatement.', 656, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (370, 'Citrons aux sardines', NULL, 30, 0, 120, 0, 'Préparation : Ecraser les sardines avec la crème. Poivrer largement et ajouter les fines herbes avec quelques gouttes de citron. Couper les citrons en 2. Oter leur pulpe en essayant de conserver l''écorce la plus intacte possible. Remplir les écorces avec le mélange « sardine-crème ». Servir bien frais (placer le plat au réfrigérateur, 2 heures avant de servir). Cette préparation peut également se faire avec du thon à l''huile.  ', 432, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (308, 'Laitues braisées', NULL, 10, 35, 0, 0, 'Préparation : Dans une grande casserole, faire fondre un morceau de beurre. Dès qu''il mousse, ajouter les oignons nettoyés mais entiers, le sucre, le sel. Les laisser bien dorer. Enlever les premières feuilles des laitues, ne garder que les cœurs bien fermes. Les rincer, les égoutter soigneusement. Dans une grande poêle, faire fondre le restant de beurre. Y déposer les laitues, saupoudrées d''un peu de sucre pour les faire dorer délicatement. Dès que les laitues sont ramollies (10 minutes), les mettre dans une cocotte avec les oignons dorés, le thym, le laurier. Saler et poivrer. Couvrir la casserole et faire braiser les laitues pendant 15 à 20 minutes. Egoutter les laitues et les dresser sur un plat, autour des oignons.', 477, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (309, 'Jarret aux légumes', NULL, 15, 90, 0, 0, 'Préparation : Faire chauffer beurre et huile dans une grande cocotte. Y mettre à rissoler le lard coupé en dés, puis les morceaux de jarret, salés et poivrés. Les faire dorer sur toutes les faces puis les retirer. Les remplacer par les oignons. Saupoudrer de farine et la laisser roussir. Mouiller avec le bouillon, ajouter le bouquet garni, les carottes grattées et le jarret. Rectifier l''assaisonnement. Faire cuire à couvert et à petit feu, environ 1 heure 30 minutes. Servir la viande entourée des légumes. Passer la sauce et la présenter en saucière.', 720, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (310, 'Huitres surprise', NULL, 20, 5, 0, 0, 'Préparation : Séparer les blancs des jaunes d''œufs. (Garder les blancs pour en faire un dessert). Attention de ne pas briser les jaunes. Dans un peu d''eau bouillante salée, les faire pocher, 2 minutes. Les retirer délicatement avec une écumoire et les déposer dans les coquilles d''huîtres (1 jaune par coquille). Arroser d''un peu de jus de citron, saler et poivrer fortement. Déposer la coquille sur une feuille de salade et décorer d''une rondelle de citron.', 674, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (312, 'Jambon braisé au porto', NULL, 15, 15, 0, 0, 'Préparation : Faire réchauffer le jambon à four très doux. Au préalable, l''arroser avec le porto, puis couvrir le tout avec une feuille d''aluminium. Pendant ce temps, couper le pied terreux des champignons et les laver dans de l''eau légèrement vinaigrée. Les mettre dans une casserole et les faire cuire, à couvert, avec le beurre, jusqu''à ce qu''ils aient rendu toute leur eau. Ajouter le jus du citron, la crème, du sel et du poivre. Couvrir et laisser cuire doucement 5 minutes. Hors du feu, incorporer les jaunes des œufs en tournant rapidement, puis le porto dans lequel le jambon a réchauffé. Napper les tranches de jambon avec cette sauce et laisser réchauffer à nouveau très lentement au four, couvert de la feuille d''aluminium. Servir bien chaud.', 1084, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (313, 'Jambon à la crème', NULL, 5, 10, 0, 0, 'Préparation : Couper les tranches de jambon en deux et déposer chaque demi tranche dans un petit plat individuel allant au four (un plat à œuf par exemple). Arroser le jambon de crème fraîche et poivrer. Faire réchauffer à four doux, chaque plat étant couvert d''un petit morceau de papier aluminium. Quand la crème commence à frémir, saupoudrer chaque plat de fromage râpé, puis faire gratiner à four chaud, jusqu''à ce que le fromage prenne un peu de couleur. Servir très chaud.', 1509, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (314, 'Jambon grillé à l''ananas', NULL, 5, 5, 0, 0, 'Préparation : Passer rapidement chaque tranche de jambon dans l''huile, puis dans la chapelure. Poser les tranches sur le grill et les faire dorer sur chaque face (1 à 2 minutes de chaque côté). Découper l''ananas en rondelles. Poser une rondelle sur chaque tranche de jambon frit. Servir très chaud avec des pommes « chips ».', 1222, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (316, 'Morue boulangère', NULL, 10, 50, 0, 0, 'Préparation : Beurrer un plat allant au four et y déposer la morue dessalée, poivrer, ajouter le bouquet garni et les 3/4 du beurre fondu dans un petit poêlon à part. Déposer le plat dans un four à température moyenne (thermostat 5/6) et laisser cuire 15 à 20 minutes. Gratter les pommes de terre et éplucher les petits oignons. Faire revenir les petits oignons dans le reste du beurre. Ajouter les pommes de terre découpées en fines rondelles, laisser rôtir 20 à 25 minutes. Ajouter les pommes de terre et les oignons dorés à la morue, dans le four et laisser encore cuire à four très doux (thermostat 3), pendant 10 minutes. Au moment de servir, saupoudrer de persil haché.', 588, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (568, 'Agneau à ma façon', NULL, 20, 45, 0, 0, NULL, 581, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (317, 'Morue provençale', NULL, 15, 45, 0, 0, 'Préparation : Faire dessaler la morue. Faire pocher la morue à l''eau chaude: porter une grande quantité d''eau à ébullition. Laisser le poisson « pocher » pendant 10 minutes. L’enlever avec une écumoire. Pendant ce temps, faire revenir l''oignon épluché et découpé en lamelles dans l''huile chaude, et dans une grande poêle. Laver les tomates, les essuyer, les découper en gros morceaux, les ajouter aux oignons. Ajouter le persil haché, le sel, le poivre, les gousses d''ail. Laisser mijoter, pendant 25 minutes environ, sans couvrir. Ajouter le poisson découpé en gros morceaux et les olives dénoyautées. Laisser mijoter 10 minutes en couvrant la poêle. Servir très chaud avec du pain, des pommes de terre nouvelles ou des pâtes au beurre.', 290, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (318, 'Moules à la bruxelloise', NULL, 10, 10, 0, 0, 'Préparation : Laver les moules dans plusieurs eaux. Eplucher et émincer céleris et oignons et les laver ainsi que la racine de persil. Les égoutter et les faire étuver dans le beurre, dans une grande marmite, avec sel, poivre en grains et poivre moulu, pendant 10 minutes. Y ajouter la moitié des moules égouttées, le jus de citron, sel et poivre. Poser le couvercle et secouer la casserole pour uniformiser la cuisson. Laisser cuire environ 5 bonnes minutes. Retirer les moules ouvertes et les garder au chaud dans un grand saladier. Ecarter les moules fermées, qui ne s''ouvrent pas malgré une prolongation de cuisson. Mettre dans la casserole le reste des moules, assaisonner de nouveau et faire cuire de la même façon. Servir les moules, avec leur jus, dans deux grandes soupières et les parsemer de persil haché. Accompagner de pommes de terres frites.', 428, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (319, 'Moules au beurre d''escargot', NULL, 20, 20, 0, 0, 'Préparation : Laver les moules dans plusieurs eaux. Emincer céleri et oignon épluchés et lavés et les faire étuver 10 minutes, avec le persil, dans le beurre. Saler, poivrer, ajouter les moules égouttées, le vin blanc, secouer la casserole et retirer du feu dès que les moules s''ouvrent. Les décoquiller et conserver les plus grosses coquilles. Hacher menu échalotes et gousses d''ail, les piler en pâte et y ajouter persil et estragon hachés. Travailler en crème le beurre prévu pour le beurre d’escargot, avec poivre et sel, puis y incorporer la préparation précédente. Dans chaque grosse coquille, mettre un soupçon de beurre d''escargot, puis une moule et la couvrir de beurre. Ranger les moules farcies sur une plaque de four et passer 4 minutes à four très chaud préchauffé. Accompagner de toasts beurrés.', 652, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (321, 'Moules au gratin', NULL, 10, 20, 0, 0, 'Préparation : Gratter les moules et ôter les parties fibreuses qui adhèrent aux coquilles. Les laver rapidement à 2 ou 3 reprises, sans les laisser tremper dans l''eau, Les faire cuire à feu vif dans une grande casserole avec le vin blanc, le jus du citron, une tasse d''eau, l''oignon haché et le céleri coupé en morceaux. Saler et poivrer. Quand toutes les coquilles sont ouvertes, les moules sont cuites. Les retirer et les détacher de leur coquille. Les réserver au chaud, dans un peu de jus de cuisson. Filtrer le reste du jus. Hacher les échalotes et les faire fondre doucement dans les 2/3 du beurre. Ajouter les champignons lavés et émincés. Les laisser cuire à couvert, jusqu''à ce qu''ils aient rendu leur eau. Les saupoudrer avec la farine, mélanger, puis arroser avec le jus de cuisson des moules. Faites épaissir en tournant. Ajoutez la crème. Goûtez et rectifiez l''assaisonnement. Mettre les moules égouttées dans cette sauce, puis verser la préparation dans un plat à gratin. Saupoudrer avec le fromage râpé, puis avec un peu de chapelure. Parsemer de copeaux de beurre et faire gratiner à four chaud, jusqu''à ce que la croûte soit dorée.', 556, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (322, 'Moules marinières', NULL, 10, 10, 0, 0, 'Préparation : Gratter les moules soigneusement et les débarrasser des parties fibreuses qui s''attachent aux coquilles. Les laver rapidement, sans les laisser tremper, à 2 ou 3 reprises. Hacher les oignons et le cœur de céleri ou couper les branches de céleri en dés, en laissant même une partie des feuilles. Faire fondre le beurre dans une grande casserole et y faire revenir doucement le céleri et les oignons. Ceux-ci ne doivent pas prendre couleur. Ajouter les moules. Saler et poivrer et arroser avec le jus des citrons. Faire cuire à feu vif en secouant de temps en temps. Quand elles sont bien ouvertes, saupoudrer de persil haché et servir. Accompagner des pommes de terre frites.', 416, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (323, 'Moules poulette', NULL, 10, 15, 0, 0, 'Préparation : Gratter les moules et ôter les parties fibreuses qui adhèrent aux coquilles. Les laver rapidement à 2 ou 3 reprises. Ne les laisser pas tremper dans l''eau: elles absorberaient à nouveau le sable dont le lavage les a débarrassées. Dans une grande casserole, faire fondre la moitié du beurre et y faire revenir doucement les oignons hachés et le céleri coupé en petits morceaux. Saler et mouiller avec le vin. Ajouter les moules et les faire cuire, en secouant de temps en temps la casserole, jusqu''à ce qu''elles soient toutes ouvertes. Ôter la coquille supérieure de chaque moule et ranger l''autre partie sur le plat de service, maintenir au chaud au dessus d''une casserole d''eau bouillante ou à four doux (dans le fond duquel vous aurez déposé un récipient d''eau). Faire fondre le reste du beurre dans un poêlon, ajouter la farine et laisser légèrement blondir le mélange en tournant. Mouiller avec le jus de cuisson filtré et la moitié de la crème. Faire épaissir en tournant. Hors du feu, ajouter les jaunes des œufs délayés dans le reste de crème en mélangeant rapidement. Ajouter un filet de jus de citron. Goûter et rectifier l''assaisonnement Verser sur les moules. Saupoudrer abondamment de persil haché et servir accompagné de pommes de terre frites.', 550, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (334, 'Oeufs à la dijonnaise', NULL, 20, 10, 0, 0, 'Préparation : Hacher finement les échalotes et les mettre dans une casserole avec le vin blanc et l''estragon. Amener à ébullition et faire réduire de moitié. Ajouter la crème et laisser à nouveau réduire de moitié. Faire cuire les œufs mollets: les plonger 5 minutes dans de l''eau en ébullition, puis les mettre quelques instants sous l''eau froide courante. Les écaler délicatement sans les abîmer. Hors du feu, incorporer le beurre en parcelles et la moutarde, à la sauce. Saler et poivrer, puis battre bien avec un fouet. Mettre les œufs coupés en deux dans des ramequins ou des petits plats individuels. Napper de sauce et saupoudrer de persil haché. Servir immédiatement.', 1080, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (324, 'Moussaka', NULL, 30, 80, 0, 30, 'Préparation : Couper les aubergines en rondelles, les mettre dans un grand saladier et les saupoudrer de sel. Laisser dégorger pendant une 1/2 heure. Egoutter les aubergines et les faire dorer à feu vif, dans une grande poêle, avec un peu d''huile. Quand elles sont colorées, les retirer de la poêle et les faire égoutter sur du papier absorbant. A leur place, dans la poêle, faire fondre doucement les oignons et l''ail hachés avec une pincée de feuilles de thym et la feuille de laurier. Ajouter un peu d''huile au besoin. Ajouter la viande et la faire revenir rapidement jusqu''à ce qu''elle commence à prendre couleur. Couvrir, puis laisser cuire à petit feu, pendant une dizaine de minutes. Dans un poêlon, vider le contenu de la boîte de tomates pelées, ajouter origan, basilic et ciboulette et faire réduire à feu vif, jusqu''à ce que la sauce ait la consistance d''une purée. Saler et poivrer abondamment. Transvaser la viande hachée, sans son jus de cuisson, dans le poêlon de sauce de tomates et mélanger. Dans un plat à gratin, superposer couches d''aubergines et couches de viande. Terminer par une couche d''aubergines. Saupoudrer de gruyère. Couvrir avec un morceau de papier aluminium et mettre à four modéré (thermostat 5), pendant 1 heure. Servir très chaud avec des pommes de terre persillées.', 381, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (325, 'Mousse d''oeufs', NULL, 10, 10, 0, 0, 'Préparation : Cuire les œufs durs, les peler. Les couper en deux et enlever les jaunes. Mettre les jaunes dans un bol, ajouter le sel, la crème fraîche, le beurre ramolli et le cognac. Mélanger bien pour en faire une pâte légère et onctueuse et en farcir les blancs d''œufs.', 1794, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (326, 'Mousse de saumon', NULL, 15, 0, 0, 0, 'Préparation : Ecraser (à la fourchette) le contenu de la boîte de saumon, bien égoutté. Ajouter le jus de citron, l''huile, la crème, saler et poivrer. Mélanger bien pour en faire un mélange homogène. Déposer 1 feuille de salade dans chaque coupe individuelle, remplir de mousse de saumon. Garnir avec des rondelles de citrons et des quartiers de tomates non épluchées.', 997, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (327, 'Noix de veau grand-mère', NULL, 15, 70, 0, 0, 'Préparation : Faire dorer la viande en cocotte, dans le beurre. Enlever la noix et la remplacer par le lard coupé en dés et les petits oignons. Les laisser rissoler pendant 5 minutes. Remettre la viande dans la cocotte, l''assaisonner, couvrir et laisser mijoter pendant 40 minutes, en ajoutant un peu de vin blanc. Ajouter ensuite les tomates pelées et épépinées, estragon et persil, le reste du vin et continuer la cuisson pendant 20 minutes, jusqu''à ce que la viande soit tendre. Enlever viande et légumes à l''écumoire et les dresser sur un plat. Lier la sauce avec la crème et la servir à part.', 628, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (328, 'Noix de veau aux primeurs', NULL, 15, 155, 0, 0, 'Préparation : Laver, éplucher et couper les légumes en fine julienne. Les faire revenir dans un peu de beurre. Quand ils commencent à être dorés, ajouter l''huile et laisser fondre les légumes, pendant 15 minutes. Passer les tranches de viande dans la farine. Les faire dorer dans une cocotte avec le reste de beurre. Saler et poivrer. Ajouter les légumes dans la cocotte et le bouquet garni, l''ail écrasé. Mouiller avec la bière. Couvrir la cocotte et laisser cuire 2 heures à feu très doux. 20 minutes avant la fin de la cuisson, faire cuire le riz dans de l''eau bouillante salée. Servir la viande recouverte des légumes et ce qui reste de liquide en saucière.', 551, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (329, 'Oeufs brouillés aux girolles', NULL, 10, 30, 0, 0, 'Préparation : Couper les pieds terreux des champignons et les laver rapidement dans plusieurs eaux. Les égoutter, puis les éponger dans 2 feuilles de papier de cuisine. Faire fondre un peu de beurre dans la poêle. Y jeter les champignons, en remuant bien. Les laisser cuire à feu vif, pendant 10 minutes, pour faire évaporer l''eau. Saler et poivrer. Lorsque le beurre a été absorbé par les champignons, diminuer la source de chaleur, les faire rissoler très doucement, pendant 15 minutes environ. Faire fondre le reste du beurre dans un poêlon à fond épais. Verser les œufs dans cette casserole, en les agitant vivement avec un fouet. Les œufs doivent cuire doucement et avoir la consistance d''une crème épaisse. Ajouter la crème fraîche, remuer bien, afin que les œufs ne collent pas dans le fond de la casserole. Verser les œufs sur un plat, les recouvrir de girolles, saupoudrer de persil haché. Servir avec des toasts ou des pommes de terre rissolées.', 895, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (330, 'Oeufs brouillés à la niçoise', NULL, 15, 10, 0, 0, 'Préparation : Nettoyer et couper en cubes les courgettes et les poivrons en fines lamelles. Les passez dans la farine. Faire chauffer l''huile dans une cocotte, y mettre les légumes et l''ail, saler et poivrer. Laisser mijoter à feu doux, pendant 10 à 12 minutes. Casser les œufs dans un bol, les brouiller sans les battre, saler et poivrer. Faire fondre le beurre dans une sauteuse, verser les œufs, mélanger bien, ajouter la crème, mélanger pour« laisser prendre » sur une très faible source de chaleur. Verser les œufs brouillés dans un plat chaud et verser par dessus les courgettes et les poivrons.', 675, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (332, 'Oeufs cocotte à la crème', NULL, 10, 8, 0, 0, 'Préparation : Répartir la moitié de la crème fraîche dans les ramequins ou petites cocottes à œufs. Casser un œuf dans chacun. Poivrer, saler, arroser avec le reste de la crème. Saupoudrer de fromage râpé. Mettre à four doux et laisser cuire, jusqu''à ce que le blanc soit cuit. Attention! le jaune ne doit pas devenir dur. Servir immédiatement.', 1294, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (333, 'Oeufs en cocotte gratiné', NULL, 20, 10, 0, 0, 'Préparation : Laver les champignons, couper le bout sableux des queues puis hacher grossièrement têtes et queues. Peler les échalotes, les hacher finement et les faire légèrement blondir dans la moitié du beurre. Y ajouter les champignons hachés, sel et poivre, faire cuire 2 minutes puis mouiller avec le madère et la crème. Mélanger et rectifier l''assaisonnement. Dans le fond des ramequins beurrés, mettre 1 cuillère à soupe du hachis de champignons à la crème. Casser un œuf dessus. Saler et poivrer légèrement. Mélanger fromage et chapelure et en parsemer les œufs. Arroser avec le reste du beurre fondu et faire cuire à four modéré, dans un bain-marie, 6 à 8 minutes environ, jusqu''à ce que le blanc de l''œuf soit pris, le jaune restant mollet.', 1202, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (342, 'Homard grillé sauce hollandaise', NULL, 15, 20, 0, 0, 'Préparation : Faire bouillir une grande casserole d''eau salée et y plonger les homards pendant 5 minutes. Les couper en deux dans le sens de la longueur et arroser la chair de beurre fondu assaisonné avec une pointe de poivre de cayenne et un peu de sel. Briser les pinces sans les décortiquer. Déposer les homards sur la grille du four et les passer quelques minutes sous le grill. Pendant ce temps, préparer une sauce hollandaise. Mettre le beurre, le vinaigre, les jaunes d''œufs, la moitié du jus de citron, le sel et le poivre dans un poêlon. Poser le poêlon sur un poêlon plus petit, rempli d''eau bouillante, et sans cesser de tourner, faire cuire la sauce au bain-marie, jusqu''à ce qu''elle épaississe. Retirer de temps en temps le poêlon de la source de chaleur pour éviter que la sauce tourne. Goûter et rectifier l''assaisonnement. Servir le homard avec la sauce en saucière.', 823, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (343, 'Côtes de veau flambées au cognac', NULL, 20, 25, 0, 0, 'Préparation : Faire dorer les côtes de veau avec la moitié du beurre, dans une grande poêle. Quand elles sont bien dorées, les transposer dans une grande sauteuse. Laver les champignons à l''eau vinaigrée, couper le bout des pieds et les émincer. Les faire cuire rapidement, à couvert, dans le reste du beurre. Arroser les côtes de veau avec le cognac, faire chauffer, puis flamber en les retournant avec une spatule en bois. Ajouter les champignons et leur jus de cuisson, puis la moitié de la crème. Amener à ébullition, puis laisser réduire quelques instants. Saler et poivrer. Délayer la fécule dans le reste de crème et verser dans la sauce. Laisser cuire à feu doux quelques minutes. Servir très chaud avec des pommes de terre ou des épinards en branches beurrés.  ', 856, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (344, 'Côtes de veau bonne femme', NULL, 20, 75, 0, 0, 'Préparation : Saler et poivrer les morceaux de viande, puis les fariner légèrement. Les secouer pour en faire tomber l''excédent. Les faire dorer dans une grande poêle avec les 2/3 du beurre. Les retirer et les déposer dans une grande cocotte. Les remplacer dans la poêle par le lard, coupé en fins lardons et les petits oignons épluchés. Les faire revenir, jusqu''à ce qu''ils soient bien dorés. Ajouter oignons et lardons dans la cocotte, en même temps que les pommes de terre épluchées et lavées. Poivrer, mais ne pas saler (à cause du lard). Ajouter le reste de beurre et un filet d''eau. Couvrir et laisser mijoter très doucement pendant 1 heure, en mélangeant de temps en temps. Servir très chaud, abondamment saupoudré de persil haché.  ', 976, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (345, 'Côtes de porc sauce moutarde', NULL, 20, 25, 0, 0, 'Préparation : Faire chauffer l''huile dans une grande poêle et y faire revenir les côtelettes à feu vif. Surveiller la cuisson: la viande doit prendre une belle couleur, mais ne pas attacher. Quand elles sont dorées, baisser le feu et continuer la cuisson à feu doux, pendant 15 minutes au moins. Pendant ce temps; hacher finement les échalotes et les faire cuire doucement dans la moitié du beurre. Quand elles sont transparentes, ajouter le reste du beurre, puis saupoudrer avec la farine. Faire cuire quelques instants en mélangeant bien, jusqu''à ce qu''il n''y ait plus de grumeaux. Mouiller avec la crème et tourner jusqu''à ce que la sauce épaississe. Ajouter la moutarde, poivrer, saler et arroser d''un filet de jus de citron. Hors du feu, ajouter le jaune de l''œuf, en tournant rapidement. Goûter et rectifier, l''assaisonnement qui doit être très relevé. Mettre les côtelettes sur le plat de service et les napper avec la sauce. Servir immédiatement.  ', 1160, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (570, 'Abricots farcis aux crevettes', NULL, 15, 0, 15, 0, NULL, 292, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (346, 'Côtes de porc en papillotes', NULL, 20, 30, 0, 0, 'Préparation : Faire dorer les côtelettes à la poêle, dans un peu d''huile. Quand elles ont pris couleur, saler et poivrer, puis les retirer de la poêle et les déposer sur une assiette. Hacher les champignons et les échalotes et les faire cuire dans la graisse de cuisson des côtelettes. Saler et poivrer, puis couvrir et laisser mijoter quelques minutes. Terminer la cuisson des champignons à feu vif, pour faire évaporer leur eau. Découper 1 grand rectangle de papier aluminium par personne. Sur chacun, déposer une tranche de jambon, un peu de hachis de champignons, puis la côtelette et par dessus le reste de hachis. Déposer une noix de beurre. Refermer la tranche de jambon par dessus. Avant de refermer les papillottes, sans trop serrer, verser une cuillère à soupe de crème. Mettre à four moyen (thermostat 5-6), pendant 25 minutes environ. Servir dans les papillotes. On peut préparer les papillotes à l''avance et terminer de les cuire à la dernière minute.  ', 975, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (347, 'Côtes de porc à la flamande', NULL, 10, 20, 0, 0, 'Préparation : Faire chauffer la moitié du beurre dans une grande poêle. Y mettre les côtes de porc. Les faire dorer rapidement, sans faire brûler le beurre, puis réduire le feu et terminer la cuisson à allure moyenne, pendant 5 minutes au moins de chaque côté. Pendant que la viande cuit, éplucher les pommes et les émincer en lamelles fines. Quand les côtes de porc sont à point, les retirer de la poêle et les garder au chaud. Les saler et les poivrer. Faire fondre le reste du beurre dans la poêle, y mettre les pommes et les faire cuire rapidement, pendant quelques minutes, en les soulevant avec une spatule en bois. Servir la viande entourée des fruits.  ', 682, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (349, 'Côtes de porc farcies', NULL, 15, 20, 0, 0, 'Préparation : Inciser les côtes préalablement désossées, sans les séparer complètement, en portefeuille. Insérer dans la fente une demi-tranche de jambon et une de fromage, parées à la dimension de la côte pour ne pas déborder. Refermer les côtes, en les pressant, les tartiner ensuite de moutarde et les passer successivement dans farine, œuf battu avec l''huile et chapelure. Presser pour que cette dernière adhère bien. Laisser durcir. Faire dorer les côtes dans la poêle, avec beurre et huile, sur les deux faces, puis continuer la cuisson sur feu plus doux, afin que la viande puisse cuire à l''intérieur et le fromage fondre sur le jambon environ 5 à 6 minutes par côté. Servir avec des pâtes et de la sauce tomate, ou encore des pommes frites et de la salade.  ', 1147, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (350, 'Côtes de porc aux cornichons', NULL, 20, 30, 0, 0, 'Préparation : Hacher grossièrement les oignons et les faire cuire doucement dans un peu d''huile. Quand ils sont blonds, ajouter la farine et faire cuire quelques instants en mélangeant bien. Arroser avec le vin blanc et un verre d''eau, puis amener à ébullition, sans cesser de tourner. Réduire le feu et laisser mijoter lentement, pendant que la viande cuit. Faire saisir les côtelettes à feu vif dans un fond d''huile. Quand elles sont dorées des deux côtés, terminer la cuisson à feu doux pendant un bon 15 mn. Oter les côtelettes de la poêle et les réserver au chaud. Déglacer la poêle avec un filet d''eau chaude et ajouter le jus obtenu à la sauce. Mélanger, puis ajouter les cornichons coupés en rondelles, la moutarde, du sel et du poivre. Gôuter et rectifier l''assaisonnement. Napper les côtelettes avec la sauce, garnir chaque morceau de viande d''un petit cornichon entier et servir immédiatement.  ', 701, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (354, 'Côte de boeuf bouquetière', NULL, 20, 60, 0, 0, 'Préparation : Eplucher tous les légumes et les tailler en bâtonnets épais. Séparer les bouquets du chou fleur. Déposer la côte de bœuf sur la grille de la lèchefrite, l''arroser avec 1/3 du beurre fondu et la faire cuire à four très chaud (préchauffé 10 minutes à l''avance), pendant 1/4 d''heure par livre environ. Faire cuire carottes et navets dans deux casseroles séparées, contenant chacune un peu de beurre. Les faire rissoler quelques instants, puis les saupoudrer de sucre. Saler et poivrer. Mouiller avec de l''eau froide à hauteur des légumes et laisser cuire doucement, jusqu''à ce que toute l''eau se soit évaporée. Faire sauter les pommes de terre dans une casserole, avec du beurre et de l''huile. Assaisonner légèrement. Faire cuire le chou-fleur dans une casserole d''eau bouillante salée. L''égoutter dès qu''il est tendre, puis le réserver au chaud, arrosé avec le reste du beurre. Quand la viande est cuite, la déposer sur le plat de service, entourée de tous les légumes. Déglacer rapidement la lèchefrite à feu vif, avec un filet d''eau et servir le jus en saucière. Saupoudrer les légumes de persil haché. Pour une viande très saignante, diminuer le temps de cuisson (10 minutes par 500 g).  ', 649, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (355, 'Côtes d''agneau Champvallon', NULL, 15, 55, 0, 0, 'Préparation : Eplucher les pommes de terre et les oignons et les couper en rondelles très fines. Faire fondre le beurre dans une grande poêle et y faire revenir rapidement les côtes d''agneau, jusqu''à ce qu''elles soient bien dorées des deux côtés. Les retirer de la poêle et les remplacer par les pommes de terre et oignons. Les faire bien colorer. Tapisser le fond d''une cocotte avec la moitié des pommes de terre et des oignons, poser la viande par dessus et recouvrir du reste de légumes. Saler et poivrer. Ajouter une feuille de laurier et une branche de thym. Arroser avec le bouillon chaud à mi-hauteur. Couvrir et faire mijoter doucement pendant 3/4 d''heure environ. Servir très chaud.  ', 527, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (356, 'Coquille saint-jacques au sherry', NULL, 10, 15, 0, 0, 'Préparation : Si vous disposez de coquilles fraîches, les passer au four chaud pour qu''elles s''entrouvrent et retirer noix et corail que vous lavez. En cas de produit surgelé, faire dégeler noix et corail. Hacher l''oignon et le mettre dans une sauteuse avec persil, thym, vin, sherry, sel et poivre. Amener à ébullition puis ajouter noix et corail. Laisser cuire 10 minutes à petit frémissement et à couvert. Laver les champignons, les émincer et les faire sauter dans la moitié du beurre et le jus de citron. Y ajouter la farine, bien mélanger et mouiller avec le jus de cuisson passé de vin et sherry. Laisser épaissir. Disposer noix (coupées en deux dans l''épaisseur) et corail dans des coquilles, couvrir de champignons et de sauce bien assaisonnée, parsemer de chapelure et de parcelles de beurre et faire gratiner à four chaud.  ', 635, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (358, 'Coquilles saint-jacques à la parisienne', NULL, 15, 25, 0, 0, 'Préparation : Si vous utilisez des coquilles fraîches, les mettre dans le four très chaud et les retirer dès qu''elles s''ouvrent. Enlever noix et coraiI et jeter le reste. Laver noix et corail et les faire vivement sauter 2 à 3 minutes dans la moitié du beurre. Les enlever et les remplacer dans la poêle ou la sauteuse par les champignons lavés et émincés que vous ferez également sauter 2 à 3 minutes. Les retirer à l''écumoire. Ajouter une noix de beurre, puis la farine et laisser cuire quelques instants avant de mouiller avec lait, vin blanc, puis crème. Assaisonner. Mettre dans cette sauce les noix de St Jacques coupées en deux ou en trois horizontalement, les coraiIs ainsi que les champignons. Faire chauffer quelques minutes et ajouter un filet de jus de citron. Répartir cette préparation dans des coquilles bien lavées. Saupoudrer d''un peu de mie de pain émiettée, mélanger avec du fromage rapé, parsemer de parcelles de beurre et faire gratiner à four moyen 15 à 20 minutes.  ', 843, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (359, 'Coquilles saint-jacques meunière', NULL, 10, 10, 0, 0, 'Préparation : Laisser les coquilles dégeler pendant 1 heure environ, puis les rouler dans la farine. Faire chauffer l''huile dans une grande poêle et y jeter les coquilles farinées. Les faire sauter à feu moyen, en les retournant souvent, jusqu''à ce qu''elles soient bien dorées. Faire fondre le beurre dans un poêlon, très doucement. L''écumer, puis le transvaser dans une autre poêle, en ayant soin de ne pas verser les impuretés blanches en même temps. Egoutter les coquilles sur du papier absorbant, puis les déposer dans la poêle contenant le beurre fondu Laisser cuire doucement, pendant 5 minutes. Hacher le persil et la gousse d''ail et en mélanger les 2/3 aux coquilles. Saler, poivrer et faire cuire à feu vif, pendant quelques instants. Servir immédiatement, saupoudré du reste du persil.  ', 1252, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (361, 'Coquilles d''oeufs durs aux crevettes', NULL, 15, 5, 0, 0, 'Préparation : Peler les œufs et les couper en morceaux. Faire chauffer beurre et moutarde dans une petite casserole, y ajouter la crème, sel et poivre et laisser épaissir. Hors du feu, ajouter les œufs hachés, les crevettes, le persil et le fromage. Rectifier l''assaisonnement. Verser cette préparation dans des coquilles, parsemer d''un peu de mie de pain émiettée et de noisettes de beurre et passer 5 minutes sous le grill du four.  ', 1151, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (364, 'Concombres au crabe', NULL, 20, 0, 120, 30, 'Préparation : Peler les concombres et les découper en dés, saupoudrer de sel et les laisser reposer 30 minutes (pour les faire dégorger). Décortiquer le crabe, l''émietter dans un ravier. Découper le jambon en petits morceaux. Le mélanger au crabe. Verser l''eau sortie des concombres, les égoutter soigneusement. Ajouter les dés de concombres au mélange, arroser avec le cognac. Placer le plat dans le réfrigérateur, pendant 2 heures. Préparer votre mayonnaise, y ajouter 1 filet de sauce anglaise. Dans des coupes individuelles, placer les feuilles de laitue, puis le mélange de crabe-jambon. Déposer une noix de mayonnaise par-dessus. Ne sortez les coupes du réfrigérateur qu''au moment de servir. Garnir d''une rondelle de citron ou 1 quartier de tomate bien ferme, et quelques câpres.  ', 580, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (372, 'Choucroute', NULL, 30, 330, 0, 0, 'Préparation : La veille, faire cuire le jambonneau dans de l''eau bouillante non salée, pendant 1 heure environ. Mettre le lard à tremper dans de l''eau froide. Laver la choucroute à l''eau froide. Puis, la blanchir 30 secondes dans une grande casserole d''eau bouillante. L''égoutter. Hacher les oignons et, dans une grande marmite, les faire fondre doucement, dans un peu d''huile ou de saindoux. Quand les oignons sont transparents, ajouter la choucroute et les baies de genévrier. Faire revenir doucement la choucroute, jusqu''à ce qu''elle soit chaude, puis mouiller avec les 3/4 du vin. Introduire le jambonneau, l''échiné de porc et le lard égoutté au milieu de la choucroute. Poivrer et saler, puis couvrir et faire mijoter très doucement, pendant 3 heures. Ajouter les saucisses de Francfort et laisser cuire encore 30 mn. Servir avec des pommes de terre à l''anglaise et de la moutarde forte. Préparer la choucroute la veille, elle n''en sera que meilleure. La faire réchauffer à feu très doux pendant 1 heure et demi.  ', 828, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (386, 'Hachis à la portugaise', NULL, 20, 25, 0, 0, 'Préparation : Couper le lard en petits dés que vous ferez rissoler dans les 3/4 de l''huile. Y ajouter les oignons pelés et hachés et laisser blondir pendant 5 minutes. Ajouter alors la viande hachée, bien mélanger jusqu''à ce qu''elle soit bien imprégnée d''huile. Incorporer le riz cuit, le persil haché, rectifier l''assaisonnement et verser le tout dans un plat à gratin huilé. Napper du coulis de tomate relevé d''ail haché et d''une pincée de poivre de Cayenne. Passer 20 minutes à four moyen.  ', 957, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (377, 'Céleris-raves farcis', NULL, 20, 45, 0, 0, 'Préparation : Eplucher les céleris et les plonger 15 minutes dans de l''eau bouillante salée et vinaigrée, afin de les attendrir et faciliter leur découpage. Les passer sous l''eau froide et les égoutter. Les couper en tranches épaisses, (3 par céleri) et prises dans le centre du légume. Creuser ces tranches, à 2 cm des bords et du fond. Détailler la chair en petits dés. Peler l''oignon et le hacher. Gratter la carotte et la détailler en julienne. Faire dorer ces légumes dans le beurre et l''huile et y ajouter les dés de céleris, le jus de citron, le veau haché, sel et poivre. Remuer et laisser cuire quelques minutes. Farcir les tranches évidées avec cette préparation. Les ranger dans un plat à gratin ou la lèchefrite huilée et contenant 1 cm d''eau. Les parsemer de fromage râpé et le reste du beurre divisé en parcelles. Faire gratiner à four modéré, environ 25 à 30 minutes, jusqu''à ce que les tranches de céleris soient tendres sans se défaire. Si les céleris sont petits et tendres, en prendre plutôt 3 que vous couperez en 2 sans longue cuisson préalable. La farce peut varier et comporter des restes de viandes, de volaille, de légumes tels que des épinards et même de purée de pommes de terre.  ', 349, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (378, 'Carré de porc à l''ananas', NULL, 10, 60, 0, 0, 'Préparation : Enduire le carré de porc avec le sucre, 1 pincée de sel et 1 tour de moulin à poivre. Le placer dans un plat allant au four, avec un peu de matière grasse. Le mettre à four vif pour le faire colorer, pendant quelques minutes (thermostat 7). Réduire la source de chaleur (thermostat 5) et arroser le carré de porc avec le rhum. Continuer la cuisson pendant 1 heure. Eplucher les pommes, les couper en morceaux. Les passer dans la poêle avec un peu de beurre et le morceau de sucre. Dès que les pommes sont tendres, arrêter la cuisson. Faire dorer les bananes épluchées dans un peu de beurre. Faire dorer l''ananas, découpé en tranches, également dans la poêle, avec 1 noix de beurre. Disposer le carré de porc sur un grand plat de service, entouré de morceaux de pommes, d''ananas et de bananes. Au moment de garnir le carré de porc, goûter les pommes cuites et, si nécessaire, ajouter 1 pincée de sucre et 1 pincée de cannelle. Servir avec un riz blanc.  ', 772, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (380, 'Homard aux fines herbes', NULL, 20, 20, 0, 0, 'Préparation : Faire fondre doucement les carottes râpées, les oignons et les échalotes hachés dans la moitié d''huile d''olives et la moitié du beurre. Utiliser une grande cocotte. Passer les homards quelques instants sous l''eau courante chaude pour les étourdir un peu. Couper les homards vivants dans le sens de la longueur, en commençant par fendre la tête. Faire cette « opération » avec un bon couteau, sur une planche en bois munie d''une rigole pour récupérer le jus. Détacher les pinces et les briser au milieu. Vider les têtes et jeter l''estomac plein de sable, mais réserver les parties crémeuses et verdâtres à part, récupérer le jus dans une tasse. Au fur et à mesure, jeter les pinces et moitiés de homards dans la cocotte où fondent les légumes. Ajouter à nouveau le reste de beurre et d''huile et faire revenir les homards à feu vif, jusqu''à ce que leurs carcasses soient bien rouges. Arroser avec le cognac, chauffer et faire flamber. Mouiller avec le vin blanc. Ajouter les tomates pelées écrasées et la moitié des herbes hachées. Saler, poivrer et mettre une pointe de couteau de poivre de cayenne. Couvrir et laisser cuire à feu vif 20 minutes. Retirer les homards et les déposer sur le plat de service préalablement chauffé. Couvrir avec une feuille d''aluminium. Mélanger les parties crémeuses et le jus des homards avec la crème. Vider dans la sauce en même temps que le reste des herbes. Faire cuire quelques instants. Goûter et rectifier l''assaisonnement. Servir avec du riz créole bien beurré.  ', 574, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (383, 'Harengs au céleri', NULL, 15, 45, 0, 0, 'Préparation : Laver le pied de céleri à grande eau. Le couper en petits morceaux. Le faire cuire dans de l''eau bouillante salée, pendant 20 à 25 minutes. L''égoutter soigneusement, puis le passer au mixer ou au presse-légumes, pour obtenir une pâte épaisse. Beurrer un plat à gratin et le tapisser de la purée de céleri. Disposer les harengs sur cette purée. Les parsemer de petits morceaux de beurre. Faire cuire 20 minutes à four chaud (thermostat 6/7). Servir avec des pommes de terre nouvelles cuites avec leur pelure, que l''on épluchera au moment de consommer les harengs.  ', 703, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (384, 'Hamburgers aux oignons', NULL, 10, 10, 0, 0, 'Préparation : Mélanger la viande hachée avec le pain ou la chapelure préalablement mouillée avec du bouillon, l''oignon et le persil haché. Assaisonner. Façonner en galettes plates et faire vivement dorer dans les 2/3 du beurre et la moitié de l''huile. Couper les oignons en minces rondelles et les faire dorer dans le reste de beurre et d''huile. Servir les hamburgers sur ce nid d''oignons frits.  ', 1274, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (387, 'Hachis parmentier', NULL, 20, 60, 0, 0, 'Préparation : Peler les pommes de terre et les faire cuire à l''eau bouillante salée. Dans une terrine, faire tremper le pain dans un peu de lait. Ajouter une brindille de thym effeuillée, l''oignon et le persil hachés, le sel et le poivre. Mettre la moitié des œufs par dessus. Quand le pain est détrempé, ajouter la viande hachée et pétrir, jusqu''à ce que tout soit bien mélangé. Mettre cette préparation dans une casserole avec un doigt d''eau et laisser cuire à couvert, en mélangeant de temps en temps, jusqu''à ce que le hachis soit cuit. Quand les pommes de terre sont cuites, les réduire en purée. Ajouter le beurre, du poivre, un soupçon de muscade et bien mélanger. Si besoin est, ajouter un peu de lait. La purée doit être onctueuse. Terminer en incorporant les œufs restants. Beurrer un plat à gratin assez profond, puis en tapisser le fond avec la moitié de la purée. Répartir le hachis de viande par dessus, sans ajouter le jus de cuisson. Recouvrir avec le reste de purée. Parsemer de petits copeaux de beurre et faire réchauffer à four chaud pendant 20 à 30 minutes  ', 679, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (402, 'Fonds d''artichauts parisiens', NULL, 5, 0, 15, 0, 'Préparation : Mélanger les jaunes d''œufs avec la mousse de foie, la crème, le sel et le poivre. Ajouter alors 1 pointe de tabasco et 1 filet de cognac pour donner un goût plus prononcé à la préparation. Placer la farce, en dôme, sur les fonds d''artichauts et poser chacun de ceux-ci sur une feuille de laitue. Décorer avec les olives noires découpées en lamelles. Servir très frais.  ', 417, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (394, 'Gigot boulangère', NULL, 20, 135, 0, 360, 'Préparation : Sortir le gigot du réfrigérateur 6h à l''avance. Le faire macérer dans l''huile que vous aurez aromatisée avec une gousse d''ail écrasée, 1 feuille de laurier broyée, 1 branche de thym effeuillée, du sel et du poivre. Eplucher les pommes de terre et les couper en fines rondelles. Les laver et les éponger. Les mettre dans une grande terrine, saler, poivrer, puis mélanger avec les mains pour que sel et poivre soient bien répartis. Beurrer un grand plat à gratin et y ranger les pommes de terre en superposant 2 couches. Parsemer de morceaux de beurre et arroser les pommes de terre avec de l''eau pour les couvrir à peine. Mettre à four chaud (thermostat 7-8), pendant 1 heure 30 minutes au moins. Les pommes de terre sont cuites quand l''eau a pratiquement disparu. Piquer le gigot d''éclats d''ail; le frotter avec du sel et du poivre, puis l''enduire avec le reste du beurre Le déposer sur le plat de pommes de terre cuites et remettre au four très chaud. Il doit cuire pendant 1/4 d''heure par 1/2 kg. Quand le gigot est bien doré, le retourner. La cuisson est terminée, ouvrir la porte du four et l''éteindre. Laisser la viande gonfler pendant quelques minutes avant de servir.  ', 620, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (395, 'Gibelotte de lapin à la moutarde', NULL, 15, 30, 0, 0, 'Préparation : Découper les lapins en 12 ou 14 morceaux et les assaisonner. Les faire dorer dans les 2/3 du beurre, environ 2 à 3 minutes de chaque côté. Y ajouter les échalotes hachées, les laisser blondir puis mouiller avec le vin blanc, que vous laisserez réduire. Ajouter thym, genièvre et la crème battue avec la moutarde. Couvrir et continuer la cuisson pendant environ 15 à 20 minutes. Rectifier l''assaisonnement. Disposer les morceaux de lapin dans un plat creux. Lier la cuisson avec les foies dénervés et passés en purée fine. Vérifier encore une fois l''assaisonnement et passer au chinois. Napper les morceaux de lapin de cette sauce.  ', 639, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (397, 'Fricassée de veau', NULL, 10, 70, 0, 0, 'Préparation : Couper la viande en cubes et la faire revenir rapidement dans un peu d''huile. Quand la viande est colorée, la retirer et la remplacer par les oignons et l''échalote finement hachés. Les faire fondre doucement. Remettre la viande dans la cocotte, puis saupoudrer avec la farine. Bien mélanger, puis mouiller avec le vin blanc et la crème. Ajouter de l''eau à hauteur de la viande. Saler, poivrer, puis ajouter la gousse d''ail écrasée et le thym. Faire cuire à petit feu, pendant 1 heure, en mélangeant de temps en temps. Ne pas laisser la viande attacher. Servir très chaud, abondamment saupoudré de persil haché, avec des pommes de terre à l''anglaise.  ', 907, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (398, 'Fricassée de rognons', NULL, 15, 20, 0, 0, 'Préparation : Avec un couteau bien aiguisé, couper les rognons en deux et ôter la partie blanche du milieu. Couper le bout terreux du pied des champignons, les laver rapidement à l''eau vinaigrée, puis les couper en quatre. Faire fondre la moitié du beurre dans une casserole et y mettre les champignons. Couvrir et laisser cuire jusqu''à ce qu''ils aient rendu toute leur eau. Piquer les chipolatas avec une fourchette et les faire cuire dans une poêle sans graisse. Démarrer la cuisson lentement pour qu''elles ne brûlent pas, puis l''activer, quand les saucisses ont déjà rendu un peu de graisse. Quand les chipolatas sont bien dorées, les retirer et mettre les rognons à leur place. Les faire revenir rapidement, jusqu''à ce qu''ils soient cuits. Les retirer et les remplacer par les pommes de terre cuites, coupées en rondelles. Les faire dorer rapidement. Les retirer. Réserver chipolatas, rognons et pommes de terre au chaud. Déglacer la poêle avec le cognac, en grattant bien avec une spatule en bois. Ajouter le jus de cuisson des champignons et faire bouillir quelques instants. Disposer les rognons, les saucisses coupées en deux ou en trois, les champignons et les pommes de terre dans le plat de service. Hors du feu, ajouter le reste du beurre à la sauce, en fouettant bien. En napper le plat et servir abondamment saupoudré de persil haché.  ', 609, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (399, 'Fricandeau de veau à l''oseille', NULL, 10, 130, 0, 0, 'Préparation : Assaisonner la viande, la couvrir avec la barde de lard et la ficeler. Couper carotte et oignon en rondelles ou en julienne. Les mettre dans le plat à rôtir, avec le beurre, poser la viande dessus et faire dorer à four moyen préchauffé. 10 minutes plus tard, ajouter le bouillon chaud, le bouquet garni et laisser cuire doucement pendant environ 2 heures, en arrosant fréquemment. Laver les feuilles d''oseille, les hacher et les mettre à fondre dans la moitié du beurre destiné à la sauce pendant 5 minutes. Y ajouter la crème, sel et poivre et garder au chaud. Enlever la viande et lier la sauce avec le reste de beurre manié de farine. Verser en saucière. Découper le veau en tranches, l''entourer de la crème d''oseille et, à volonté, accompagner de pommes sautées.  ', 716, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (400, 'Friands au roquefort', NULL, 10, 15, 0, 0, 'Préparation : La veille, mettre votre pâte à dégeler dans le bas du réfrigérateur. La sortir une heure avant de commencer la préparation. Avec un rouleau, l''abaisser à 2 ou 3 mm d''épaisseur, en ayant soin de travailler sur une table propre et farinée. La découper en carrés réguliers d''environ 12 cm de côté. Sur chaque carré, déposer une portion de roquefort, en prenant soin de le répartir sur toute la largeur de la pâte. Plier chaque morceau de pâte en deux et coller les bords en appuyant avec le bout des doigts mouillés pour les souder. Quadriller le dessus de chaque friand avec la pointe d''un couteau. Battre l''œuf et en badigeonner le dessus des friands pour qu''ils prennent une belle couleur dorée à la cuisson. Beurrer et fariner légèrement une plaque à pâtisserie ou, à défaut, une feuille d''aluminium épais. Déposer les feuilletés dessus et mettre à four chaud (thermostat 7 ou 8 ), pendant 15 minutes environ Servir tiède.  ', 1864, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (401, 'Fondue au fromage', NULL, 10, 15, 0, 0, 'Préparation : Couper le pain en cubes réguliers. Eplucher la gousse d''ail et en frotter le caquelon, jusqu''à ce qu''elle soit tout à fait usée. Faire chauffer le vin dans le caquelon à fondue. Quand il commence à bouillir, y jeter les fromages râpés d''un seul coup. Chauffer le mélange à feu doux, en tournant bien, jusqu''à ce qu''il forme une pâte liquide. Poivrer et ajouter la muscade, puis le kirsch. Amener à ébullition. Mettre le caquelon à table sur un petit réchaud à alcool et régler la flamme de sorte que la fondue continue à frémir. Présenter en même temps un panier avec les morceaux de pain. Chaque convive se servira, piquera le pain au bout d''une longue fourchette et le trempera dans la fondue. Baisser la flamme au fur et à mesure que la fondue diminue. Pour terminer, partager la croûte gratinée qui s''est formée dans le fond du caquelon.  ', 1556, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (405, 'Filets de limande à la moutarde', NULL, 5, 5, 0, 0, 'Préparation : Poser les filets sur la grille au-dessus de la lèche-frite et, avec un pinceau, les enduire de beurre fondu. Les assaisonner et les couvrir de chapelure sur laquelle vous verserez quelques gouttes d''huile. Faire dorer 5 minutes dans le four préchauffé, sous la rampe du grill. Pendant ce temps, dans une petite casserole, délayer la moutarde avec le jus de citron. Y incorporer le reste du beurre, en battant avec le fouet sur chaleur douce, puis la crème et terminer par les herbes hachées. Rectifier au besoin l''assaisonnement. Dresser les filets sur un plat chauffé et les napper de la sauce bien mousseuse. On peut appliquer cette recette à d''autres poissons: merlan, dorade, fines tranches de cabillaud.  ', 813, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (406, 'Fondants de gruyère', NULL, 20, 10, 30, 0, 'Préparation : Mélanger à la main fromage râpé et farine. Faire bouillir le lait, avec la gousse d''ail écrasée et la pincée de muscade. Fouetter en mousse les jaunes d''œufs, avec sel et poivre, et verser dessus, peu à peu et sans cesser de battre, le lait bouillant dont vous aurez retiré la gousse d''ail. Remettre sur le feu et laisser épaissir, sans bouillir. Hors du feu, ajouter le mélange fromage râpé et farine, bien mélanger et rectifier l''assaisonnement. Laisser tiédir. Incorporer à cette masse épaisse les blancs d''œufs battus en neige et l''étaler sur un plat huilé, à environ 3 cm d''épaisseur. Laisser refroidir et durcir au frais. Lorsque la pâte est bien ferme, la découper en croquettes de 6 cm de long et 3 cm de large, ou encore en boulettes ou en carrés. Paner ces croquettes en les passant successivement dans la farine, blancs d''œufs légèrement battus et chapelure. Laisser à nouveau durcir au frais, quelques heures. Faire frire dans la friture bouillante, puis réduire un peu la chaleur pour permettre au fromage de chauffer sans que sa carapace ne devienne trop foncée. Egoutter sur papier absorbant et servir avec du persil frit.  ', 899, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (407, 'Foie de veau à la vénitienne', NULL, 10, 20, 0, 0, 'Préparation : Découper votre foie en tranches, puis en petits morceaux de 2 à 3 cm de long environ. Saupoudrer de farine. Faire fondre le beurre dans une grande poêle, y jeter les morceaux de foie. Ajouter les échalotes épluchées et finement émincées. Ajouter le vin blanc, saler et poivrer. Laisser cuire à petit feu, pendant 20 minutes, en remuant souvent, afin que le foie ne colle pas dans la poêle. Servir le foie très chaud.  ', 916, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (408, 'Foie de veau à l''italienne', NULL, 10, 5, 0, 0, 'Préparation : Couper les tranches en morceaux gros comme un doigt et les faire revenir dans la moitié du beurre, jusqu''à ce qu''ils soient juste saisis. Les retirer de la poêle et les réserver au chaud. A leur place, dans la poêle, faire fondre les échalotes finement hachées. Quand elles sont transparentes, les arroser avec le madère et faire réduire à feu vif quelques instants. Remettre les morceaux de foie dans la poêle. Saler et poivrer et les faire revenir à feu vif. Quand ils sont cuits, hors du feu, ajouter le reste du beurre et le laisser fondre en mélangeant. Servir immédiatement avec du riz ou des pâtes bien beurrées.  ', 899, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (409, 'Foie de veau à l''indienne', NULL, 15, 5, 0, 0, 'Préparation : Dans une grande cocotte, faire blondir l''huile. Eplucher et émincer les oignons. Les jeter dans l''huile. Ajouter les tomates découpées en tranches. Ajouter le yaourt, l''ail écrasé, le curry (dilué dans un fond d''eau), le safran (également dilué dans un fond d''eau) et enfin les grains de coriandre. Saler, remuer la préparation avec une cuillère en bois. Laisser mijoter sur feu très doux, jusqu''à la fin de la cuisson de la viande. Dans une poêle, verser un peu d''huile et faire frire les tranches de foie. Les faire bien dorer (5 minutes sur chaque face). Poivrer. Dresser les tranches sur un plat de service. Recouvrir la viande avec la sauce. Servir avec du riz bien ferme.  ', 469, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (410, 'Foie de veau en cocotte', NULL, 10, 60, 0, 0, 'Préparation : Découper le lard en petits lardons et piquer le foie de ces bâtonnets. Saler, poivrer généreusement et faire dorer en cocotte, dans la moitié du beurre. Peler les oignons, les émincer et, lorsque le foie est bien doré de toutes parts, le retirer et le remplacer par les oignons. Dès que ceux-ci ont roussi, remettre le foie dans la cocotte, mouiller avec bouillon et vin et ajouter clou de girofle et bouquet garni. Faire cuire à petit feu et à couvert pendant 1 heure environ. Vérifier la cuisson en enfonçant dans le foie une aiguille à tricoter, le jus qui s''écoule de la fente doit être blanchâtre et non rosé. Retirer le foie. Rectifier l''assaisonnement du jus de cuisson et le lier avec la fécule dissoute dans le vinaigre. Ajouter le reste du beurre. Accompagner de purée de pommes de terre.  ', 789, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (411, 'Foie de veau au bacon', NULL, 10, 10, 0, 0, 'Préparation : Faire cuire les tranches de foie de veau à la poêle, avec le beurre. Mener la cuisson à feu modéré, sans que le beurre brunisse. Quand les tranches de foie ont bien doré des deux côtés, les retirer et les déposer sur le plat de service. Couvrir celui-ci avec une feuille de papier aluminium et le garder à four doux. Faire revenir rapidement les tranches de bacon dans la poêle où a cuit le foie. Quand elles se recroquevillent sur elles-mêmes, les retirer et en déposer, une sur chaque tranche de foie. Déglacer la poêle avec 1 cuillère à soupe d''eau en grattant bien le fond avec une cuillère en bois. Verser cette sauce sur la viande et servir immédiatement avec des pommes à l''anglaise abondamment persillées.  ', 1201, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (412, 'Filets de sole aux petits oignons', NULL, 10, 40, 0, 0, 'Préparation : Nettoyer les légumes et les découper en petits dés. Les faire revenir dans un peu de beurre (3 à 4 minutes), puis ajouter le vin, petit à petit. Laisser cuire 15 minutes. Egoutter les légumes, mais garder le liquide dans lequel vous ferez dissoudre le bouillon-cube. Porter ce bouillon à ébullition et faire pocher les filets de sole (environ 4 minutes). Saler et poivrer les filets, les plier en 2 et les disposer sur un plat beurré. Entourer les filets, des légumes et placer le plat dans le four chaud, pendant 20 minutes (thermostat 7). Prélever 1 grand verre de bouillon. Faire fondre le restant du beurre dans un poêlon. Ajouter la crème et fouetter bien pour en faire une sauce homogène. Ajouter le jus de citron, le cerfeuil et le persil hachés. Bien mélanger. Se sert en saucière.  ', 557, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (413, 'Filets de sole à l''orange', NULL, 10, 10, 0, 0, 'Préparation : Faire lever les filets des soles, les assaisonner, les fariner très légèrement, en secouant l''excédent. Les faire frire à la poêle, dans les 2/3 du beurre. Les garder au chaud. Déglacer la cuisson avec le jus d''un quart des oranges et la cuillère à soupe de jus de citron. Eplucher les oranges restantes à vif, en coupant la pelure, à même la chair, avec un couteau tranchant. Diviser les fruits en tranches et passer celles-ci vivement dans la cuisson déglacée, pour les chauffer. Pendant ce temps, travailler en pâte le reste de beurre et la farine, en les écrasant à la fourchette, et les incorporer peu à peu à la cuisson bouillante à épaissir. Napper de cette sauce les filets de sole et les garnir chacun d''une tranche d''orange, les autres étant placés autour du plat. Servir avec des pommes de terre mignonnettes.  ', 549, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (417, 'Filets de dorade au four', NULL, 15, 10, 0, 0, 'Préparation : Peler les échalotes, les hacher menu et les faire blondir dans 1/3 du beurre. Beurrer un plat de service en métal, poser dessus les filets de poisson, les assaisonner, y parsemer les échalotes, puis un peu de chapelure et arroser avec le reste du beurre fondu. Faire griller et gratiner sous la rampe du gril du four pendant environ 5 minutes. Mettre dans une petite casserole le vin blanc, le jus de citron, le jaune d''œuf et la moutarde. Les battre et les laisser s''épaissir sur feu doux, en remuant sans cesse, sans atteindre l''ébullition qui coagulerait l''œuf. Y incorporer le beurre, par petits morceaux à la fois et en battant vigoureusement au fouet, sans trop chauffer pour ne pas compromettre l''émulsion. Lorsque le beurre est entièrement assimilé, ajouter la crème, peu à peu et sans cesser de battre. Assaisonner et presser un filet de jus de citron. Napper de cette sauce les filets sortis du four, saupoudrer de persil haché et accompagner de pommes de terre, ou de riz au beurre, de tranches de tomates sautées à l''huile avec ail et persil.  ', 755, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (418, 'Filets de dorade aux crevettes', NULL, 15, 10, 0, 0, 'Préparation : Passer les filets sous l''eau froide et les essuyer avec du papier absorbant ou un linge. Les paner en les passant successivement dans la farine mélangée avec sel et poivre, les œufs battus avec l''huile et la chapelure. Laisser durcir. Faire frire dans la moitié du beurre et l''huile, environ 3 minutes de chaque côté. Garder au chaud. Dans une casserole, chauffer le reste du beurre et y mettre à revenir vivement les crevettes. Y ajouter la crème et un filet de jus de citron. Verser cette préparation sur les filets et garnir le plat de quartiers de citron.  ', 1004, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (439, 'Entrecôte maitre d''hôtel', NULL, 10, 10, 0, 0, 'Préparation : Huiler la viande et la faire griller sur les deux faces. Assaisonner après cuisson. Travailler le beurre avec le persil, le jus de citron et le cognac. Le mettre à durcir au réfrigérateur et, au moment de servir, le répartir en noisettes sur la viande grillée. Laver le cresson et en garnir le plat.', 794, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (425, 'Escalopes de veau viennoise', NULL, 10, 15, 0, 0, 'Préparation : La veille, déposer la moitié de beurre pas trop dur sur un carré d''aluminium. Refermer celui-ci et donner au paquet la forme d''un cylindre. Mettre au frigo. Il vous suffira de déballer et de tailler en rondelles régulières. Passer les escalopes, une à une, dans la farine. Secouer, pour faire tomber l''excédent. Les tremper ensuite dans une assiette creuse où vous aurez battu les œufs avec du sel et beaucoup de poivre. Terminer en passant les escalopes dans la chapelure. Bien appuyer pour la faire adhérer. Au fur et à mesure qu''elles sont prêtes, déposer-les dans une poêle où vous aurez fait fondre un peu de beurre. Faire cuire à feu modéré, jusqu''à ce que les escalopes aient une belle couleur dorée uniforme. Pendant ce temps, tailler les citrons en fines rondelles et découper des rondelles de beurre à l''emporte-pièce. Quand les escalopes sont cuites, les servir surmontées chacune d''une rondelle de citron avec une rondelle de beurre et un anchois roulé. Accompagner de pommes de terre frites et d''un plat d''épinards en branches beurrés, ou de petits pois très fins.', 973, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (426, 'Escalopes de veau à la vaudoise', NULL, 10, 20, 0, 0, 'Préparation : Enrober les escalopes de farine additionnée de sel, poivre et marjolaine. Couper le bout sableux des queues des champignons et laver ceux-ci. Hacher les pieds et émincer les têtes des champignons. Arroser de jus de citron. Faire cuire et dorer les escalopes dans la moitié de beurre et l''huile. Procéder à la cuisson des champignons dans le reste du beurre et les laisser sauter 3 minutes avant d''ajouter le vin blanc, sel, poivre, le persil haché. Continuer la cuisson pendant 2 minutes. Incorporer alors le jambon coupé en fines lanières. Disposer les escalopes sur le plat chauffé et recouvrer-les avec les champignons et le jambon enlevés à l''écumoire et vivement égouttés. Déglacer la cuisson de la viande avec le bouillon et y ajouter celle des champignons. Napper les escalopes garnies avec cette sauce. Accompagner de petits pois étuvés et de pommes de terre gaufrettes.', 826, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (427, 'Escalopes de veau sauce câpres', NULL, 10, 20, 0, 0, 'Préparation : Faire dorer les escalopes dans un peu d''huile, à feu vif. Quand elles sont bien colorées des deux côtés, les retirer et les tenir au chaud, à four doux par exemple. Jeter la graisse de la poêle. Déglacer la poêle avec un filet de vinaigre et un peu d''eau. Gratter bien avec une cuillère en bois pour délayer les sucs caramélisés. Ensuite, ajouter les câpres et le beurre. Réduire le feu et faire fondre le beurre. Terminer, hors du feu, en battant bien à la fourchette. Arroser les escalopes avec cette sauce et saupoudrer abondamment de persil avant de servir. Accompagner de pommes allumettes et de champignons sautés à l''ail, ou de chou-fleur à l''anglaise.', 1028, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (606, 'Mousse de fruits', NULL, 15, 0, 0, 0, 'Préparation : Peler les oranges à vif, en tranchant la pelure au couteau, à même la chair. Couper les fruits en tranches et enlever pépins et cœurs de membranes dures. Couper les pommes en quatre, les peler, retirer également pépins et membranes. Peler les bananes et les couper en tronçons. Mettre tous ces fruits dans le bol du mixer et les broyer en purée fine, en y ajoutant sucre, crème battue et liqueur. Disposer cette crème mousseuse dans des coupes et garnir avec un biscuit.', 611, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (428, 'Escalopes de veau en portefeuille', NULL, 10, 15, 0, 0, 'Préparation : Déposer une tranche de fromage sur chaque escalope. Replier les escalopes autour du fromage, puis les emballer dans une tranche de jambon. Passer chaque paquet ainsi formé dans la farine, puis dans les œufs battus avec poivre et sel et, enfin, dans la chapelure. Bien appuyer pour faire adhérer la chapelure. Faire cuire les escalopes à la poêle avec le beurre, à feu modéré. Les retourner pour qu''elles dorent uniformément des deux côtés. Servir surmonté d''une rondelle de citron, avec un quartier de citron supplémentaire sur chaque assiette. Accompagner de brocoli au naturel ou d''épinards à la crème.', 1138, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (429, 'Escalopes de veau aux champignons', NULL, 10, 20, 0, 0, 'Préparation : Peler les échalotes, les hacher finement et les faire légèrement blondir, en sauteuse, dans 1/3 du beurre. Y ajouter les champignons lavés et émincés, le vin blanc et laisser cuire jusqu''à évaporation de ce dernier. Ajouter le porto, la crème, sel et poivre et continuer la cuisson jusqu''à réduction du liquide et épaississement de la préparation. Y ajouter l''oseille hachée, fondue dans 1/3 de beurre, et le jambon cuit, haché. Faire sauter les escalopes à la poêle, dans le reste de beurre et la cuillerée d''huile, avec sel et poivre. Les dresser sur le plat de service chauffé et les napper de la crème aux champignons et jambon bien chaude.', 738, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (431, 'Escalopes à la milanaise', NULL, 10, 15, 0, 0, 'Préparation : Ciseler les escalopes en pratiquant des entailles le long de leur contour. Les passer ensuite dans de la farine, assaisonnée de sel et de poivre, puis dans les œufs battus et enfin dans de la chapelure mélangée avec le fromage. Bien presser et laisser durcir. Faire chauffer beurre et huile et y mettre à dorer les tranches de viande, sur les deux faces. Accompagner de pâtes au beurre et d''un ravier de fromage râpé.', 1087, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (432, 'Escalopes à la hongroise', NULL, 10, 15, 0, 0, 'Préparation : Faire blondir dans la moitié de l''huile les oignons pelés et hachés, les poivrons lavés, épépinés, émincés en lanières, les tomates pelées, épépinées et coupées en dés. Mouiller avec le bouillon et laisser cuire jusqu''à ce que le poivron soit tendre. Assaisonner, ajouter le paprika et lier avec la fécule délayée dans une cuillère d''eau. Simultanément, faire cuire les haricots, à découvert, dans de l''eau bouillante salée. Passer les escalopes dans de la farine assaisonnée et les faire frire dans le restant d''huile, de 2 à 4 minutes de chaque côté selon l''épaisseur des tranches. Ajouter les haricots égouttés à la sauce.', 822, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (433, 'Escalopes de foie aux oignons', NULL, 10, 10, 0, 0, 'Préparation : Passer les tranches de foie dans de la farine additionnée de sel et de poivre. Hacher les oignons et les faire vivement dorer dans les 3/4 de beurre et faire cuire rapidement les tranches de foie, 2 minutes de chaque côté environ. Enlever la viande. Déglacer la cuisson avec le bouillon, le porter à ébullition et y ajouter jus de citron, les oignons et les tranches de foie. Si la sauce vous semble être trop liquide, la lier avec un peu de fécule délayée dans de l''eau. On peut y ajouter de la purée de tomate pour en corser le goût. Saupoudrer de persil haché.', 654, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (434, 'épaule de mouton au citron', NULL, 10, 60, 0, 0, 'Préparation : Détailler la viande en cubes de grosseur moyenne, puis la faire revenir à feu vif dans l''huile très chaude. Les morceaux doivent bien dorer. Retirer la viande de la poêle et la remplacer par les oignons finement hachés et la gousse d''ail écrasée. Quand ils sont transparents, ajouter le paprika. Mélanger, puis remettre la viande dans la casserole. Arroser avec le jus de citron. Saler et poivrer, puis laisser mijoter à couvert pendant 1 heure au moins. Servir très chaud, abondamment saupoudré de persil haché, avec des pommes de terre à l''anglaise.', 785, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (435, 'épaule d''agneau rôtie', NULL, 5, 40, 0, 0, 'Préparation : Allumer votre four sur feu vif. Eplucher les gousses d''ail. Piquer l''épaule d''agneau de morceaux de ces gousses. Enduire le morceau de viande de beurre. Enfourner la viande et laisser cuire 20 minutes par livre, en ayant soin, de temps à autre, de verser le beurre fondu au fond du plat, sur la viande. Lorsque la viande est cuite, placer l''épaule, découpée ou non, sur un grand plat de service.', 760, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (436, 'Entrecôte printanière', NULL, 20, 100, 0, 0, 'Préparation : Nettoyer les légumes et les faire cuire dans de l''eau bouillante salée pendant 30 minutes, dans des casseroles séparées (le chou-fleur pas plus de 15 minutes). Bien égoutter les légumes et les maintenir au chaud. Faire fondre 1 /3 du beurre dans une grande poêle assez profonde. Badigeonner l''entrecôte avec le beurre fondu, la faire griller sur un grill électrique ou dans le four, 7 à 8 minutes de chaque côté. Verser un peu de bouillon dans la grande poêle et y remettre la viande 30 minutes (pour une viande saignante), 45 minutes pour une viande plus cuite. 15 minutes avant de servir: faire fondre le restant du beurre dans les différentes casseroles à légumes et y faire légèrement revenir carottes et haricots. Saler, poivrer et saupoudrer d''oignon en poudre. Faire sauter les pommes de terre dans un mélange de beurre et d''huile, pendant 10 à 15 minutes. Déposer la pièce de viande sur un grand plat chauffé, l''entourer des légumes.', 775, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (437, 'Entrecôte aux petits oignons', NULL, 20, 30, 0, 0, 'Préparation : Eplucher les oignons, sans les couper. Les déposer dans une petite casserole, ajouter l''eau, 1 noix de beurre et le sucre. Couvrir et laisser cuire à feu vif, en surveillant la cuisson. Dès que le liquide prend une consistance de caramel, arrêter la cuisson. Nettoyer les champignons. Découper le lard en petits lardons et les faire fondre dans une poêle. Ajouter les champignons. Pendant ce temps, faire cuire vos pommes de terre dans de l''eau bouillante salée (environ 25 minutes). Les égoutter et les passer 5 à 10 minutes dans un peu de beurre fondu. Mélanger oignons et champignons. Faire griller votre entrecôte (10 à 12 minutes de chaque côté). Déposer la viande sur le lit de pommes de terre, l''entourer des légumes. Saupoudrer le tout de persil haché.', 730, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (438, 'Entrecôte marchand de vin', NULL, 10, 30, 0, 0, 'Préparation : Huiler la viande et la poivrer. La placer sur le grill préchauffé et la faire griller 12 minutes de chaque côté (15 minutes pour une entrecôte à point). Eplucher et hacher finement les échalotes. Les faire cuire rapidement, dans la poêle, avec le beurre et le vin (10 minutes environ). Elles doivent être réduites de moitié. Dans un bol, malaxer le restant du beurre avec les échalotes et le persil. Ajouter le jus de citron. Saler, poivrer. Verser ce mélange sur la viande et servir très chaud.', 884, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (440, 'Entrecôte à l''estragon', NULL, 15, 20, 0, 0, 'Préparation : Nettoyer et hacher l''estragon et les échalotes. Déposer les 3/4 de l''estragon dans un petit poêlon, avec le vinaigre. Saler. Faire réduire le mélange de moitié. Poivrer la viande. Dans une grande poêle, faire fondre le beurre, y placer la viande et la faire cuire 10 à 15 minutes de chaque côté. Pendant ce temps, ajouter un peu de beurre à la sauce à l''estragon. La battre jusqu''à ce qu''elle devienne mousseuse. Dresser la viande sur un plat et verser la sauce par-dessus. Saupoudrer de persil.', 1027, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (441, 'Entrecôte à la bordelaise', NULL, 10, 15, 0, 0, 'Préparation : Faire chauffer l''huile dans une poêle. Quand elle est bien chaude, y mettre la viande et faire saisir à feu très vif. Quand une belle croûte dorée s''est formée des deux côtés, terminer la cuisson à feu modéré. Saler, poivrer abondamment. Retirer la viande et la déposer sur le plat de service. La maintenir au chaud, sous un couvercle par exemple. Hacher les échalotes et les faire revenir dans une cuillère à soupe de beurre. Quand elles commencent à blondir, arroser avec le vin blanc et faire réduire à feu vif, en grattant bien le fond de la poêle avec une cuillère en bois pour délayer les sucs de viande caramélisés. Hors du feu, ajouter le reste du beurre et bien fouetter. Napper l''entrecôte avec cette sauce et servir immédiatement avec des pommes de terre frites et des champignons sautés.', 901, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (442, 'Endives en ragout', NULL, 20, 95, 0, 0, 'Préparation : Laver les endives, les égoutter, puis les couper en quatre ou en six dans le sens de la longueur. Eplucher les pommes de terre et les oignons et les couper en fines rondelles. Couper le lard en lardons et le faire fondre avec le beurre dans une grande cocotte. Ajouter les endives et faire braiser 5 minutes à couvert. Puis ajouter les pommes de terre et les oignons. Saler et poivrer, puis arroser avec de l''eau bouillante à ras des pommes de terre. Laisser cuire une 1 1/2 heure à tout petit feu, jusqu''à ce que l''eau ait été presque entièrement absorbée. Les endives sont parfois très amères. Goûter une feuille crue et, si c''est le cas, ajouter 1 ou 2 morceaux de sucre dans la cocotte, en même temps que les légumes.', 617, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (443, 'Endives farcies', NULL, 20, 65, 0, 0, 'Préparation : Laver les endives à l''eau courante et les faire blanchir 5 minutes dans une grande casserole d''eau bouillante. Les égoutter. Dans une terrine, émietter le pain et l''arroser avec le lait. Quand il est bien détrempé, casser l''œuf dessus, puis ajouter les viandes hachées, le lard coupé en petits dés, l''oignon haché fin et le thym. Saler, poivrer, râper une pointe de muscade, puis pétrir le tout longuement. Ecarter avec précaution les feuilles des endives bien égouttées. Farcir avec le mélange de viande, puis rabattre les feuilles et maintenir l''ensemble avec de la ficelle. Ranger les endives farcies dans un plat allant au four, les arroser avec le beurre fondu et faire cuire à four moyen (thermostat 5-6), pendant 1 heure environ. Arroser souvent pendant la cuisson. Servir très chaud avec des pommes de terre vapeur.', 810, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (444, 'émincés de veau au citron', NULL, 15, 20, 0, 0, 'Préparation : Couper la noix en escalopes très fines, puis encore en lanières de 3 ou 4 cm de long sur 2 cm de large. Faire fondre le beurre dans une grande poêle et y faire revenir la viande, sans qu''elle prenne couleur. Dès que la viande a perdu sa couleur rosée, la retirer et la réserver au chaud. Laver les champignons, couper le bout des pieds, puis les émincer. Hacher les oignons très fin. Faire fondre champignons et oignons dans la poêle où a cuit la viande. Quand l''eau des champignons s''est évaporée, remettre la viande dans la poêle. Arroser avec le jus des citrons et faire réduire de moitié, à feu modéré. Ajouter la crème, saler et poivrer. Couvrir et laisser cuire à petit feu pendant 5 minutes. Hors du feu, ajouter les jaunes des œufs, délayés dans quelques cuillères de sauce. Mélanger rapidement. Saupoudrer abondamment de persil haché et servir avec des pommes de terre frites et un plat d''épinards en branches bien beurré.', 647, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (446, 'écrevisses à la bordelaise', NULL, 20, 35, 0, 0, 'Préparation : Laver, puis râper les carottes. Hacher l''oignon, les échalotes et les queues de persil. Faire fondre la moitié du beurre dans une casserole et y mettre les carottes, l''oignon et l''échalote, les queues de persil hachées, le thym et l''estragon. Couvrer et laisser étuver très doucement, pendant 15 minutes environ. Pendant ce temps, châtrer les écrevisses, c''est-à-dire leur ôter l''intestin. Pour y arriver, tirer d''un coup sec la nageoire centrale de la queue: le boyau noir qui y est attaché viendra tout seul. Faire chauffer le reste du beurre et l''huile dans une grande casserole et y jeter les écrevisses. Les faire revenir en les remuant jusqu''à ce qu''elles soient bien rouges. Ajouter le cognac et flamber en continuant à les remuer. Ajouter ensuite les légumes étuvés, les tomates pelées concassées et la moitié du persil haché. Mouiller avec le vin blanc, saler, poivrer et ajouter le poivre de cayenne. Porter à ébullition, couvrir et laisser cuire 5 à 10 minutes, selon la grosseur des écrevisses. Retirer les écrevisses de la casserole et les garder au chaud. Laisser mijoter doucement la sauce pendant une dizaine de minutes, puis, hors du feu, ajouter le reste du beurre en parcelles, en mélangeant bien au fouet, pour lier la sauce. Verser cette sauce sur le plat d''écrevisses, saupoudrer avec le reste de persil haché et servir immédiatement.', 620, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (447, 'Dorade au vin blanc', NULL, 20, 30, 0, 0, 'Préparation : Demander à votre poissonnier de vider et d''écailler le poisson, sans enlever la tête. Saler et poivrer l''intérieur du poisson, puis le huiler. Le coucher dans un plat allant au four, et l''arroser avec le reste d''huile. Peler l''oignon, le couper en rondelles et en garnir le poisson. Vider le contenu de la boîte de tomates tout autour. Saler et poivrer, puis arroser avec le vin blanc. Faire cuire à four très chaud (thermostat 7-8), pendant 30 minutes environ en arrosant souvent. Avant de servir, saupoudrer avec le persil haché, parsemer de morceaux de beurre et arroser avec le jus du citron. Puis remettre dans le four éteint pendant une dizaine de minutes pour laisser gonfler le poisson. Servir avec des pommes de terre blanches persillées.', 491, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (555, 'Artichauts au fromage blanc', NULL, 10, 20, 120, 0, 'Préparation : Enlever les premières feuilles des artichauts. Découper les pointes des autres feuilles et ôter le foin. Faire tremper vos artichauts dans une eau citronnée, pendant 5 minutes. Faire bouillir une casserole d''eau salée avec la farine. Y plonger les artichauts et les laisser cuire pendant 20 minutes. Les égoutter soigneusement. Pendant ce temps, fouetter la crème fraîche et le fromage blanc dans un bol. Ajouter oignons hachés, fines herbes hachées, sel et poivre. Remplir tous les fonds d''artichauts avec le mélange. Les déposer deux heures dans le réfrigérateur avant de servir.  ', 436, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (448, 'Dorade au four', NULL, 15, 35, 0, 0, 'Préparation :  Chauffer votre four à feu vif. Enduire la dorade, laissée entière, de beurre. Introduire les éclats d''ail à l''aide de la lame d''un couteau, saler et poivrer. Dans un plat allant au four, placer délicatement le poisson sur quelques noix de beurre. Pendant le premier temps de cuisson, c''est-à-dire 1/4 d''heure à feu vif, éplucher les tomates (saisies à l''eau bouillante pour faciliter l''épluchage). Placer ensuite les oignons coupés en grosses lamelles ainsi que les tomates coupées en tranches dans le plat où se trouve la daurade bien dorée. En profiter pour l''arroser du beurre répandu dans le fond du plat. Verser le contenu du verre de vin blanc sec. Continuer la cuisson au four plus doux, pendant 20 minutes. Lorsqu''elle est cuite, placer la dorade avec précaution, sans la défaire, sur un grand plat de service, l''entourer des rondelles de tomates. Napper la sauce sur le poisson.', 432, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (449, 'Dorade au citron', NULL, 10, 30, 0, 0, 'Préparation : Hacher ensemble le persil, les échalotes et la gousse d''ail. Saler, poivrer et farcir le poisson de ce hachis. Couper les citrons en fines rondelles. Coucher la dorade dans un plat, bien beurré, allant au four. Avec un couteau bien tranchant, faire des incisions parallèles, en allant jusqu''à l''arête du poisson, en biais. Glisser une rondelles de citron dans chaque fente. Saler,poivrer, ajouter la feuille de thym, puis parsemer de copeaux de beurre, avant de mettre à four chaud pendant 20 à 30 minutes. Servir avec des pommes de terre blanches persillées.', 465, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (450, 'Dorade aux champignons', NULL, 20, 55, 0, 0, 'Préparation : Nettoyer les poissons. Saler et poivrer l''intérieur des poissons. Enlever le pied terreux des champignons, les essuyer avec un linge humide et les hacher grossièrement. Faire fondre le beurre dans une poêle. Y jeter les échalotes émincées, saler et laisser cuire quelques minutes à feu vif, avec les champignons. Pendant ce temps, faire cuire le riz dans de l''eau bouillante salée, pendant 10 minutes. Egoutter le riz. Ajouter l''œuf cru battu, les champignons, le persil. Bien mélanger. Remplir les poissons avec cette farce, délicatement, pour ne pas briser la chair. Refermer les poissons avec des piques en bois. Déposer les poissons dans un plat à gratin beurré, les arroser de vin blanc, écraser le pain et le répandre dans le plat. Garnir les poissons de noisettes de beurre et passer le plat au four chaud (thermostat 6/7), pendant 45 minutes.', 542, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (452, 'Dinde de Noël farcie aux marrons', NULL, 60, 90, 0, 0, 'Préparation : Demander au boucher ou au marchand de volaille de désosser complètement la dinde et de vous donner les os. Préparer la farce dans une grande terrine. Emietter le pain et l''arroser avec le lait. Quand il est bien détrempé, ajouter les viandes hachées, les œufs, le cognac, le porto, les champignons et le persil grossièrement hachés, du sel et du poivre. Bien pétrir, à pleines mains, jusqu''à ce que le mélange soit parfaitement homogène. Ajouter alors les marrons égouttés et les incorporer à la farce, sans trop les briser. Mettre la dinde, côté peau en dessous, à plat sur la table. Répartir les morceaux de chair détachés, là où la chair est moins épaisse. Badigeonner toute la surface de blanc d''œuf. Placer la farce au milieu de la dinde. Rabattre la peau et la chair par dessus et recoudre avec une grosse aiguille et du fil de cuisine (à défaut de ficelles, le fil à coudre, en double épaisseur, peut très bien faire l''affaire). Essayer de rendre à la dinde sa forme primitive. Mettre la dinde farcie dans un grand plat allant au four, avec les os concassés, les carottes et les oignons en rondelles, du thym et du laurier. Frotter la peau avec du sel et du poivre, puis arroser de beurre fondu. Faire cuire à four très chaud (thermostat 7 ou 8), pendant 1 1/2 heure au moins. Quand la dinde est cuite, la retirer et la déposer sur le plat de service. Réserver au chaud. Verser 1/2 lit. d''eau dans le plat et faire réduire de moitié, à feu vif, en grattant bien le fond du plat avec une cuillère en bois. Passer la sauce au chinois, puis la lier avec une cuillère à café de fécule délayée dans un peu de porto. Servir la dinde avec des marrons ou des pommes croquettes ou des pommes dauphines et présenter la sauce en saucière. Présenter en même temps un ravier de compote de pommes et un ravier de compote d''airelles.', 689, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (453, 'Darnes de saumon à l''oseille', NULL, 10, 10, 0, 0, 'Préparation : Laver et essorer l''oseille, la hacher finement. Nettoyer le poisson en enlevant la peau et les arêtes. Déposer les darnes sur des feuilles d''aluminium, en ajoutant sur chacune d''elles un peu d''oseille, saler et poivrer, ajouter quelques graines de fenouil. Placer sur les darnes une noix de beurre et une rondelle de citron. Fermer les papillotes (papier d''aluminium en laissant un bon espace vide) Les placer dans un plat allant au four et laisser cuire, au four, pendant 10 minutes. Un beurre maître d''hôtel (beurre manié avec persil, sel, poivre et jus de citron) servi très frais, agrémentera ces délicieuses darnes.', 716, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (471, 'Crème de concombres', NULL, 15, 25, 0, 0, 'Préparation : Eplucher finement les concombres, ôter les graines avec la pointe d''un couteau. Les découper en lamelles très minces, sur une planche en bois. Dans une casserole moyenne, faire fondre le beurre, sans le laisser brunir. Y déposer les légumes et les faire sauter, jusqu''à ce qu''ils deviennent transparents. Ajouter l''oseille et le cerfeuil, le poivre et le sel. Mouiller avec le bouillon. Amener à ébullition. Laisser cuire à petit feu, pendant 15 minutes. Si vous désirez lier le potage avec les jaunes d''œufs, il faut le laisser tiédir, puis battre les jaunes dans un petit bol. Verser les œufs lentement dans la crème de concombres, en les mélangeant bien avec un fouet.', 313, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (556, 'Artichauts braisés', NULL, 15, 50, 0, 0, NULL, 515, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (455, 'Crustacés en cocktail', NULL, 30, 0, 0, 0, 'Préparation : Laver la laitue. En garder des belles feuilles pour garnir le fond des coupes individuelles. Découper le cœur en lanières fines, avec des ciseaux. Oter l''écorce des avocats et leur noyau. Les couper-les en tranches fines. Les citronner pour éviter qu''ils ne noircissent. Incorporer le ketchup et le whisky à la mayonnaise. Goûter et rectifier l''assaisonnement, qui doit être très relevé. Ecaler les œufs durs et les couper en quatre. Dans un grand saladier, mettre le crabe égoutté (ou dégelé), le homard, les crevettes, les morceaux d''avocats, la laitue en lanières. Ajouter la moitié du bol de sauce cocktail et mélanger délicatement. Rajouter de la sauce si nécessaire. Presser le jus d''un citron et mélanger encore. Répartir cette préparation dans les coupes et garnir avec 2 quartiers d''œuf dur. Arroser les coupes avec le restant de mayonnaise. Servir frais. On peut faire le même genre de préparation avec du saumon rose en boîte et des crevettes.', 1041, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (456, 'Croutons d''épinards aux ufs', NULL, 10, 20, 0, 0, 'Préparation : Faire dégeler les épinards dans un doigt d''eau bouillante salée Quand ils sont tout à fait dégelés, les égoutter à fond, en les pressant contre les parois de la passoire avec une spatule en bois. Les remettre dans la casserole avec les 2/3 de beurre et les laisser cuire doucement, pendant quelques minutes. Pendant ce temps, faire cuire les œufs 10 minutes dans de l''eau en ébullition. Les passer sous l''eau froide courante, puis les écaler. Tartiner les tranches de pain avec le reste du beurre et les faire rôtir à la poêle, à feu modéré. Bien surveiller pour qu''elles ne brûlent pas. Elles doivent être dorées uniformément des deux côtés. Couper les œufs durs en morceaux, puis les écraser finement à la fourchette. Les Incorporer aux épinards et faire réchauffer quelques instants en tournant. Saler et poivrer, râper une pointe de muscade, puis goûter et, au besoin, rectifier l''assaisonnement. Répartir la préparation sur les croûtons rôtis et servir.', 939, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (459, 'Croquettes de sardines', NULL, 10, 10, 0, 60, 'Préparation : Egoutter les sardines, enlever peaux et arêtes et écraser la chair en pâte, avec le beurre. Hacher au moulin les œufs durs, pelés, et le pain rassis. Les mouiller avec le lait jusqu''à ce qu''il imprègne entièrement le pain. Mélanger cette purée avec la pâte de sardines et beurre, ainsi que le persil haché, le jus de citron, l''œuf entier et le jaune, sel, poivre et poivre de Cayenne. Façonner en croquettes que vous panerez dans une préparation de farine, œuf et huile, chapelure. Laisser durcir et faire frire dans la friture très chaude. Egoutter et accompagner de quartiers de citron et de persil.', 951, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (460, 'Croquettes de jambon', NULL, 10, 10, 0, 60, 'Préparation : Hacher au moulin le jambon, le porc et la mie de pain. Ajouter l''œuf entier et le jaune d''œuf, battus avec la moutarde, la sauce anglaise et le persil. Assaisonner. Façonner ce hachis en petites croquettes en forme de gros bouchons, les paner dans la farine, et l''œuf battu avec l''huile et la chapelure. Les laisser bien durcir au frais avant de les frire dans la friture très chaude. Egoutter les croquettes et les saupoudrer de paprika.', 1214, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (462, 'Croquettes aux crevettes', NULL, 20, 20, 0, 60, 'Préparation : Faire fondre le beurre, puis y incorporer la farine, en mélangeant avec une spatule en bois. Ajouter le lait et tourner jusqu''à ce que la sauce épaississe. Laisser mijoter quelques instant. Vous devez obtenir une sauce très épaisse et bien lisse. Hors du feu, incorporer le fromage. Ajouter le jus d''un citron, poivrer et saler (attention! pas trop: le fromage est déjà salé). Amalgamer les crevettes à cette préparation. Vous avez maintenant une espèce de pâte épaisse. Goûter et rectifier éventuellement l''assaisonnement. Verser cette préparation dans un plat rectangulaire, huilé au préalable. Mettre au réfrigérateur, jusqu''à ce que la pâte soit solide. Démouler la pâte sur une planche à pâtisserie farinée. La découper en rectangle que vous roulerez entre les paumes de vos mains, pour obtenir de grosses croquettes en forme de boudins. Passer les croquettes dans la farine, puis dans l''œuf battu et, enfin, dans la chapelure. Faire frire dans une friture à 180°, jusqu''à belle couleur dorée. Servir avec des morceaux de citron. On peut remplacer les crevettes par un reste de poulet coupé en dés, ou de poisson. Ou simplement doubler la quantité de fromage râpé.', 781, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (547, 'Asperges aux fines herbes', NULL, 20, 20, 0, 0, NULL, 805, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (557, 'Artichauts a la barigoule', NULL, 10, 80, 0, 0, NULL, 512, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (463, 'Crêpes au roquefort', NULL, 20, 20, 0, 0, 'Préparation : Verser la farine dans un grand saladier et faire un puits au milieu, dans lequel vous casserez les œufs. Travailler le mélange farine-œufs avec une grande cuillère en bois, en ajoutant le lait, petit à petit. Quand le mélange est homogène, ajouter la bière et mélanger encore. Faire fondre lentement le beurre non salé. Ecraser finement le roquefort avec une fourchette. Il doit former une pâte que vous délayerez soigneusement avec le beurre fondu. Verser le fromage dans la pâte à crêpes. Si elle devient trop épaisse, ajouter un peu de lait. Surtout ne pas saler votre pâte, à cause du Roquefort, déjà très salé. Bien battre la pâte, puis, faire cuire les crêpes dans une petite poêle légèrement beurrée. Cuire ces crêpes dans une poêle à revêtement anti-adhésif de préférence, cela évitant un apport de graisse supplémentaire, les crêpes étant déjà très riches en elles-mêmes. Au fur et à mesure, les beurrer avec le beurre salé et les réserver au chaud.', 1244, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (464, 'Crêpes au jambon et aux champignons', NULL, 20, 20, 0, 60, 'Préparation : Mettre la farine dans une terrine et y creuser un puit. Casser les œufs au milieu, ajouter le beurre fondu au préalable et une pincée de sel. Mélanger avec une cuillère en bois, en délayant au fur et à mesure avec le lait froid. Vous devez obtenir une pâte lisse et assez fluide. Au besoin, battre au fouet. Laisser reposer cette pâte pendant une heure, protégée par un linge propre. Pendant ce temps, éplucher les échalotes et les hacher finement. Faire fondre la moitié du beurre dans une casserole, puis y faire revenir les échalotes à feu très doux. Quand elles sont transparentes, ajouter les champignons lavés et émincés. Couvrir et laisser cuire, à couvert, jusqu''à ce que les champignons aient rendu toute leur eau. Transvaser le tout sur une assiette. Dans la même casserole, faire fondre le reste du beurre, puis le saupoudrer de farine et bien mélanger. Arroser avec le lait et faire épaissir, sans cesser de tourner. Ajouter champignons et jambon coupé en dés dans cette sauce et assaisonner avec sel, poivre et une pointe de muscade. Réserver au chaud pendant la cuisson des crêpes dans une grande poêle. Sur chacune des crêpes, verser un peu de la préparation jambon-champignons. Les rouler et les ranger dans un plat allant au four. Faire réchauffer 5 minutes à four moyen avant de servir.', 834, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (465, 'Crêpes aux fruits de mer', NULL, 30, 10, 0, 120, 'Préparation : Préparer la pâte à crêpes en mélangeant farine, œuf battu, sel, lait, zeste, le beurre et l''huile. Laisser reposer au moins 2 h. Faire cuire les crêpes et les garder au chaud. Chauffer la moitié du beurre, y délayer la farine et mouiller ce roux avec le lait, la crème et le jus des boîtes de crabe et de saumon. Laisser épaissir quelques minutes puis ajouter les chairs de crabe et de saumon effeuillées, les crevettes. Rectifier l''assaisonnement. Au centre de chaque crêpe déposer une grosse cuillerée de farce. Rabattre dessus deux côtés de la crêpe, en vis-à-vis, puis les deux côtés opposés. La farce est enfermée complètement. Faire dorer dans le reste de beurre et d''huile, le côté pliures en dessous pour bien souder celles-ci, l''autre côté ensuite. Saupoudrer de persil haché et accompagner de quartiers de citrons. Les crêpes peuvent aussi être roulées et mises dans un plat à gratin puis nappées de sauce au fromage et gratinées.', 1220, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (466, 'Crêpes fourrées aux petites saucisses', NULL, 20, 10, 0, 0, 'Préparation : Mettre tous les ingrédients de la pâte dans une grande terrine et mélanger avec le mixer. C''est le moyen le plus rapide pour qu''il ne reste pas de grumeaux. Faire pocher les saucisses de Francfort 5 minutes dans de l''eau frémissante, pour les réchauffer. Faire des crêpes aussi fines que possible. Sur chacune d''elles, déposer une saucisse tartinée de moutarde et les rouler. Les ranger dans un plat allant au four. Saupoudrer de fromage râpé et faire légèrement gratiner.', 1162, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (467, 'Crêpes farcies rissolées', NULL, 20, 30, 0, 60, 'Préparation : Préparer la pâte en mélangeant farine, œufs et lait, en y ajoutant sel, poivre, une bonne pincée de zeste râpé de citron, le beurre fondu ou l''huile. Laisser reposer 1 ou 2 heures. Enfoncer une fourchette à mi-hauteur du morceau de lard et le frotter dans une petite poêle chauffée. Y verser la pâte et confectionner une vingtaine de petites crêpes. D''autre part, hacher oignons et ail et les faire blondir dans le beurre. Y ajouter la farine, bien remuer, puis la viande hachée, le vin ou l''eau, sel et poivre, un soupçon de pili-pili et la menthe hachée. Laisser cuire doucement, en remuant, pendant au moins 30 minutes. Rectifier l''assaisonnement et laisser refroidir. Au centre de chaque petite crêpe mettre une cuillerée de farce à la viande. Rabattre dessus d''abord la partie de la crêpe devant vous, puis la partie lui faisant face, la farce étant ainsi cachée, rabattre ensuite les deux autres parties de la crêpe, jusqu''au centre. Vous obtenez ainsi des petits paquets. Faire dorer dans une grande poêle contenant de l''huile très chaude, en commençant par le côté des pliures. Retourner à mi-cuisson, égoutter sur papier absorbant et servir immédiatement, avec un coulis de tomate, ou encore telles quelles, piquées d''un petit bâtonnet.', 775, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (469, 'Crème de fromages', NULL, 10, 10, 0, 0, 'Préparation : Enlever les croûtes des fromages. Ne garder que l''intérieur. Détailler les fromages en morceaux. Dans un grand poêlon, faire fondre le beurre, ajouter les morceaux de fromage et les laisser fondre sur un feu très doux, en tournant sans cesse, pour qu''ils n''attachent pas. Dans un bol, mélanger le lait et la farine et verser lentement sur les fromages fondus, toujours en tournant. Augmenter légèrement la source de chaleur pour faire prendre de la consistance à la pâte. Saler et poivrer. Laisser cuire 2 minutes, puis verser la pâte dans une terrine chaude. Saupoudrer d''ail. Servir avec les tranches de pain grillées.', 1188, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (548, 'Asperges à la crème', NULL, 15, 20, 0, 0, NULL, 243, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (473, 'Crème d''asperges', NULL, 20, 30, 0, 0, 'Préparation : Dans une grande casserole, faire bouillir de l''eau salée. Pendant ce temps, éplucher les asperges. Les mettre à cuire dans l''eau bouillante 30 minutes. Préparer, pendant cette cuisson une béchamel : - faire fondre 30 g de beurre dans une casserole, saupoudrer de farine en tournant à la cuillère en bois. - Hors du feu, ajouter le lait. - Replacer le tout sur le feu doux, en continuant à remuer jusqu''à épaississement. Lorsque les asperges sont cuites, prélever un bol de leur jus de cuisson, et les égoutter. Ajouter le liquide prélevé à la béchamel et mélanger. Détacher les pointes des asperges, les garder au chaud, passer le reste au mixer. Ajouter cette purée à la béchamel, saler et poivrer, remettre sur le feu doux. Dans une poêle beurrée, faire dorer les tranches de pain. Ajouter à la crème d''asperges, la crème fraîche, ainsi que les pointes d''asperges. Mélanger doucement. Verser le tout dans votre soupière. Ajouter dessus le pain doré persillé.', 593, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (475, 'Couronne de viande', NULL, 10, 45, 0, 0, 'Préparation : Faire tremper le pain dans un peu de bouillon. Hacher finement les échalotes pelées. Mélanger intimement les viandes hachées avec le pain trempé et pressé, les échalotes, les œufs, sel et poivre. Beurrer un moule en forme de couronne et le saupoudrer de chapelure fine. En secouer l''excédent. Tasser le hachis dans le moule et faire cuire à four moyen, 45 minutes environ. Si, en fin de cuisson, la surface du hachis a tendance à trop brunir, couvrir le moule d''une feuille de papier aluminium. Démouler sur le plat de service, autour d''un mont de purée de céleris-raves et de pommes de terre. Accompagner de sauce tomate ou de sauce brune aux oignons, de sauce aux champignons au choix.', 919, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (477, 'Côtelettes paysannes', NULL, 10, 20, 0, 0, 'Préparation : Oter les feuilles flétries du chou et le laver à l''eau vinaigrée. Le faire blanchir 10 minutes dans de l''eau bouillante salée. L''égoutter. Quand il est tiède, le couper en quatre. Oter les grosses côtes dures et détailler le reste en lanières. Faire cuire le chou dans un peu d''huile, à feu moyen, en remuant souvent. Saler et poivrer. Pendant ce temps, faire dorer les côtes de porc à la poêle, avec le reste d''huile. Terminer la cuisson, 5 minutes de chaque côté, à feu moyen. Quand le chou est cuit, le mettre dans le plat de service et déposer les côtelettes par dessus. Jeter la graisse de cuisson des côtelettes, puis déglacer la poêle avec un verre d''eau, à feu très vif, en grattant bien avec une cuillère en bois, pour dissoudre tous les sucs caramélisés. Ajouter le beurre hors du feu, en mélangeant bien, pour velouter la sauce. Verser sur le plat et servir, avec des pommes de terre à l''anglaise, en accompagnement.', 748, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (478, 'Côtelettes d''agneau aux courgettes', NULL, 0, 45, 0, 0, 'Préparation : Mettre au chaud le plat qui servira à présenter vos côtelettes. Plonger les tomates dans l''eau bouillante et les éplucher. Laver le cresson. Eplucher les courgettes, les couper en dés et les faire revenir dans une casserole au fond de laquelle vous aurez mis un peu d''huile. Y ajouter l''oignon coupé fin, ainsi que les tomates. Saler et poivrer. Laisser mijoter le tout sur feu doux pendant 3/4 d''heure. Griller les côtelettes badigeonnées d''huile, salées, poivrées, parsemées de thym et laurier. Lorsque la viande est grillée (4 minutes de chaque côté), disposer la viande sur le plat chaud. Garnir le plat de cresson. Dans un plat creux, disposer les courgettes. Ce plat délicieux peut s''accompagner de pommes dauphines ou de pâtes. Vos côtelettes peuvent également être grillées au barbecue, dans ce cas demander au boucher de couper des côtelettes assez minces.', 576, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (479, 'Côtes de veau aux petits oignons', NULL, 20, 0, 0, 0, 'Préparation : Nettoyer les oignons en ayant soin de conserver la partie verte. Nettoyer les échalotes. Couper les oignons, les échalotes et les champignons en menus morceaux. Dans une poêle beurrée, placer les champignons, les échalotes, ensuite les oignons, ajouter alors une pincée d''herbes hachées. Laisser cuire sur feu doux, jusqu''à ce que l''eau des champignons ait disparu presque entièrement. Dans une autre poêle, faire dorer les côtes de veau salées et poivrées. Lorsque les 2 préparations sont terminées, ajouter la crème fraîche aux champignons. Placer la viande sur un plat de service, napper de sauce, saupoudrer de persil et de ciboulette.', 807, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (480, 'Côtes de veau aux laitues', NULL, 10, 45, 0, 0, 'Préparation : Enlever les grosses feuilles extérieures des laitues et laver celles-ci dans plusieurs eaux. Les plonger 4 minutes dans de l''eau bouillante salée et les égoutter à fond. Les couper en deux, dans la hauteur, et les ficeler. Peler, hacher l''échalote et la faire blondir dans la moitié du beurre, en cocotte. Y ajouter les demi-laitues, salées et poivrées et mouiller de vin blanc. Faire cuire à four moyen et à couvert pendant environ 40 minutes. 15 minutes avant la fin de la cuisson des laitues, assaisonner les côtes de veau et les dorer dans le reste du beurre, 7 minutes de chaque côté. Déglacer la poêle avec la crème et ajouter une pincée de poivre de Cayenne. Napper la viande de cette sauce et l''entourer des laitues braisées, parsemées de persil haché.', 721, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (481, 'Côtes de veau à l''italienne', NULL, 20, 20, 0, 0, 'Préparation : Faire revenir les échalotes hachés dans un peu d''huile d''olives. Quand elles commencent à blondir, ajouter le contenu de la boîte de tomates pelées, le thym, le basilic, la gousse d''ail épluchée et entière, le sucre, du sel et du poivre. Amener à ébullition, puis laisser mijoter à feu doux, jusqu''à ce que les tomates soient réduites en purée. Faire dorer les côtes de veau dans une grande poêle, avec le beurre. Quand elles ont bien colorées, les déposer dans un plat allant au four, dans lequel vous aurez étendu préalablement la sauce tomate. Déglacer la cuisson des côtelettes avec un filet d''eau et en arroser la viande. Surmonter chaque côté d''une tranche de jambon, puis une tranche de fromage. Saupoudrer de parmesan râpé avant de mettre à four chaud pour faire gratiner. Servir avec des pommes de terre frites ou des pâtes au beurre.', 839, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (482, 'Carbonades à la flamande', NULL, 20, 135, 0, 0, NULL, 598, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (483, 'Cannellonis aux légumes verts', NULL, 20, 20, 0, 0, 'Préparation : Faire cuire les pâtes comme il est indiqué sur leur emballage, mais les garder un peu fermes. Nettoyer les légumes, les couper grossièrement et les pocher à l''eau bouillante salée, quelques minutes. Les égoutter soigneusement et les mélanger avec les œufs et le fromage de chèvre, saler et poivrer, ajouter un peu de thym et de laurier en poudre. Disposer un peu de légumes sur chaque pâte et les mettre dans un plat à gratin. Saupoudrer chaque couche de gruyère râpé et d''une noix de beurre. Ajouter le reste du chèvre et passer à four chaud 10 minutes.', 1226, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (485, 'Canetons aux navets', NULL, 15, 45, 0, 0, 'Préparation : Eplucher les navets et les oignons.  Les faire revenir dans le beurre (garder les oignons entiers, mais découper les navets en 2 ou en 4 selon leur grosseur). Les garder au chaud. Faire dorer les canetons dans l''huile, dans une cocotte. Fermer cette dernière et laisser cuire à feu moyen pendant 45 minutes. Toutefois, après les 20 premières minutes de cuisson, ajouter les légumes. Saler et poivrer Après la cuisson, déglacer la sauce avec un petit verre d''eau, et la servir à part. Présenter les canetons entourés des légumes.', 995, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (486, 'Canard rôti aux navets', NULL, 20, 40, 0, 0, 'Préparation : Faire fondre doucement, sans matière grasse, le lard coupé en lardons. Les retirer dès qu''ils commencent à blondir. Les remplacer dans la poêle par les foies des canards. Les faire dorer sur toutes les faces, puis les retirer. Laver et émincer les champignons et les faire cuire dans la poêle où ont cuits lardons et foies, en ajoutant une noix de beurre. Répartir tous les ingrédients précédents et les olives dénoyautées, pour en farcir chaque canard. Les recoudre hermétiquement. Les frotter de poivre et de sel. Les enduire avec une noix de beurre et les mettre à four très chaud (thermostat 8). Pendant la cuisson, les retourner de temps en temps pour leur permettre de bien dorer. Pendant ce temps, éplucher les navets et les faire blanchir 15 minutes dans de l''eau bouillante salée. Les égoutter, les essuyer, puis les faire revenir avec le reste du beurre. Après quelques minutes, les saupoudrer avec le sucre. Faire cuire à petit feu, à découvert. Quand ils sont cuits, découper les canards et les servir garnis de leur farce, entourés des navets glacés, avec le jus de cuisson en saucière.', 1264, NULL);
