INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (492, 'Cailles rôties à la liégeoise', NULL, 10, 20, 0, 0, 'Préparation : Faire fondre le beurre dans une cocotte et y ajouter l''huile. Y faire dorer les cailles, à feu modéré, en les retournant souvent. Bien surveiller la cuisson, le beurre ne doit pas noircir. Saler, poivrer et ajouter les baies de genévrier. Laisser cuire 20 minutes environ. Pendant ce temps, découper des canapés dans le pain de mie, en les taillant en ronds ou en losanges. Les faire toaster juste avant de passer à table. Déposer un canapé dans chaque assiette et une caille par dessus. Arroser avec la sauce. Servir très chaud. On peut accompagner ce plat de cailles de pommes allumettes et d''une garniture de champignons à la forestière.', 1276, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (493, 'Cailles à la boulangère', NULL, 30, 120, 0, 0, 'Préparation : Hacher très fin la feuille de laurier, les oignons et une des gousses d''ail. Incorporer ce hachis d''aromates au beurre en malaxant bien. Ajouter le sel et le poivre. Eplucher les pommes de terre et les détailler en rondelles très fines. Les laver, puis les essuyer dans un linge propre, délicatement, sans les briser. Mettre les pommes de terre dans un grand saladier et les saupoudrer de sel et de poivre. Mélanger avec les mains pour bien répartir les épices. Frotter un plat à gratin avec la gousse d''ail, jusqu''à ce que celle-ci soit tout à fait usée. Y ranger ensuite les pommes de terre en deux couches régulières. Mouiller avec de l''eau chaude, à ras des pommes de terre et mettre à four chaud (thermostat 6) après avoir parsemé le plat de noisettes de beurre aromatisé (en réserver 1 noisette par cailles). Quand le gratin de pommes de terre cuit depuis environ 1 heure 1/2, poser les cailles, dans lesquelles vous aurez introduit une noisette de beurre et que vous aurez bardées et ficelées, sur une grille au dessus du plat de pommes de terre. Les laisser cuire 1/2 heure environ en les retournant de temps en temps pour qu''elles dorent uniformément. Quand les cailles sont cuites, les déposer directement sur les pommes de terre et servir.', 769, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (494, 'Cabillaud en rôti', NULL, 10, 40, 0, 0, NULL, 495, NULL);
