INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (498, 'Bûche de noël aux marrons', NULL, 20, 0, 720, 0, 'Préparation : Faire fondre le chocolat dans une casserole à feu très doux. Ajouter la purée de marrons, le beurre ramolli, les 3/4 de sucre glace et bien travailler le mélange jusqu''à ce qu''il soit tout à fait homogène. Verser la pâte obtenue sur un long rectangle de papier aluminium. Rouler dans le sens de la longueur, en donnant la forme d''une bûche. Mettre au réfrigérateur pendant 12 heures au moins. Avant de servir, démouler la bûche sur un plat, lisser la pâte avec une spatule trempée dans l''eau très chaude. Gratter ensuite avec les dents d''une fourchette pour imiter l''écorce. Garnir avec des feuilles de houx ou des petits sujets en massepain trouvés cher le pâtissier. Saupoudrer avec le restant de sucre-glace. A table, découper en tranches fines. Pour les pressés, faire prendre la bûche au freezer ou au congélateur.', 1506, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (499, 'Brochettes de porc à l''ananas', NULL, 20, 10, 0, 120, NULL, 1416, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (500, 'Brochettes mixed grill', NULL, 20, 10, 0, 60, 'Préparation : Découper les noisettes des côtelettes. Séparer le bœuf en cubes égaux. Diviser le rognon en morceaux égaux, ainsi que le foie. Verser l''huile dans un plat creux, y mettre les feuilles de laurier, les grains de poivre écrasés et faire mariner les viandes dans cette huile, en remuant, pendant au moins 1 heure. Tremper ensuite les champignons dans la marinade, ainsi que les tomates coupées en deux et épépinées et le poivron également épépiné, coupé en 6 morceaux. Sans oublier les oignons coupés en rondelles. Répartir sur les brochettes les différents éléments, soit champignon, foie, roulade de lard, bœuf, poivron, mouton, oignon, rognon, laurier, chipolata, oignon, bœuf, tomate, lard, champignon. Faire griller sur toutes les faces et saler au dernier moment.', 675, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (501, 'Brochettes de fruits de mer', NULL, 20, 20, 30, 120, NULL, 604, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (502, 'Brochettes de foie et rognon', NULL, 20, 10, 0, 120, NULL, 698, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (503, 'Brochettes chachlyks', NULL, 20, 10, 0, 120, 'Préparation : Mélanger les ingrédients de la marinade et y mettre à macérer, pendant au moins 2 heures, la viande, coupée en dés et les champignons. Remuer fréquemment. Couper les oignons en rondelles et les ébouillanter, pendant 2 minutes. Diviser les tomates en deux, en enlever les graines et les assaisonner. Enfiler sur les brochettes les dés de viande égouttés, en alternant avec rondelles d''oignon, demi-tomates et champignons. Saler la viande, badigeonner d''huile et faire griller. Faire chauffer l''alcool, sans bouillir, dans une petite casserole. En arroser les brochettes et faire flamber.', 565, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (504, 'Brochettes de cervelas et bacon', NULL, 15, 10, 0, 0, 'Préparation : Fendre les cervelas horizontalement, sans les couper en deux et en tartiner l''intérieur avec de la moutarde. Rouler au centre de chaque cervelas une 1/2 tranche de fromage et insérer une brochette dans la fente, entre les couches de fromage. Enrouler tout autour des cervelas les tranches de lard, en serrant bien pour maintenir les saucisses bien fermées, et en piquant un bâtonnet à chaque pliure. Couper les tomates en deux, les épépiner, les assaisonner et les parsemer d''origan en poudre. Enfiler ces demi-tomates aux extrémités de chaque brochette. Arroser d''huile et faire griller.', 913, NULL);
