INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (526, 'Beignets aux fruits', NULL, 30, 10, 0, 70, 'Préparation : Faire macérer les tranches d''ananas et les bananes coupées en 4 tronçons dans le rhum. Eplucher les pommes, ôter le trognon avec un vide-pommes et les couper en rondelles d''un 1/2 cm d''épaisseur. Les citronner pour qu''elles ne noircissent pas et les saupoudrer légèrement de sucre. Préparer la pâte dans une terrine : Mélanger la farine avec un œuf, l''huile ou le beurre fondu et le sel. Ajouter la bière petit à petit: vous devez obtenir une pâte plus épaisse que celle des crêpes. Laisser reposer 1 heure environ. Juste avant utilisation, incorporer délicatement les blancs battus en neige des 2 œufs restants. Plonger les fruits dans la pâte à beignets et, au fur et à mesure, les jeter dans la friture chaude. Quand ils sont bien dorés, les égoutter sur du papier absorbant, puis servir, saupoudrés de sucre.', 547, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (527, 'Beignets de fraises', NULL, 20, 10, 0, 180, 'Préparation : Laver et équeuter les fraises. Les mettre dans un bol et les couvrir avec le sucre. Laisser macérer quelques heures et bien mélanger, sans abîmer les fruits. Préparer la pâte à beignets. Egoutter les fraises et ajouter leur jus éventuel dans la pâte à beignets. Tremper les fraises dans la pâte, elles doivent en être complètement enrobées. Faire frire les beignets, les éponger avec du papier absorbant et les saupoudrer de sucre.', 1650, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (528, 'Beignets de chou-fleur', NULL, 20, 20, 0, 0, 'Préparation : Préparer la pâte à frire ou à beignets en suivant les données de la recette appropriée. Séparer le chou-fleur en petits bouquets, laver bien ceux-ci, les faire égoutter et les arroser de jus de citron. Les faire cuire dans de l''eau bouillante salée, 10 à 15 minutes, ils doivent rester très fermes. Les assaisonner au sel, poivre et muscade. Les égoutter à fond et les déposer sur du papier absorbant avant de les tremper dans la pâte de votre choix. Les faire frire, les égoutter et les parsemer de persil haché. Séparer le chou en très petits bouquets et, après les avoir frits, les parsemer de fromage râpé. Vous aurez ainsi un très bon accompagnement d''un plat de jambon ou de viande froide. D''autres légumes peuvent être ainsi présentés: blancs de poireaux, salsifis, tranches de courgettes, d''aubergines.', 484, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (529, 'Beignets de champignons', NULL, 20, 10, 0, 0, 'Préparation : Préparer la pâte à frire ou à beignets et la laisser reposer. Laver, égoutter et éponger les champignons. Les faire sauter 5 minutes dans du beurre et du jus de citron, saler et poivrer. Ajouter le madère et le laisser s''évaporer à feu vif. Mettre les champignons à refroidir puis les tremper dans la pâte à frire ou à beignets ou les enfoncer dans une boule de pâte à beignets soufflée non sucrée. Faire frire dans la friture. Servir comme accompagnement d''un plat de viande.', 921, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (530, 'Barquettes de soles', NULL, 20, 45, 0, 0, NULL, 614, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (531, 'Barquettes au riz', NULL, 10, 30, 0, 0, 'Préparation : Faire cuire le riz dans 2 fois son volume d''eau salée (pour qu''il reste bien ferme et croquant). Ajouter l''huile dans l''eau de cuisson. Laisser refroidir le riz. Mélanger le riz avec la mayonnaise bien relevée (moutarde forte, poivre et sel). Déposer le riz dans une douzaine de petites barquettes (en carton, en papier plissé, en aluminium, etc). Garnir les barquettes avec les saucisses découpées en rondelles, les crevettes, des lanières de poivron (lavé et épépiné), des morceaux de saumon fumé, des rondelles d''œufs durs, des olives.', 1441, NULL);
