INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (536, 'Avocats à la crème', NULL, 10, 0, 0, 0, 'Préparation : Couper les avocats en deux et enlever le gros noyau central. Fouetter la crème et, lorsqu''elle est bien ferme, y ajouter le raifort, le tomato ketchup, sel et liqueur anisée. Répartir et parsemer cette crème dans les cavités des demi-avocats et parsemer d''un peu d''amandes effilées. Servir immédiatement.', 933, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (537, 'Avocats aux coeurs de palmiers', NULL, 30, 0, 0, 0, 'Préparation : Couper les avocats en deux, horizontalement, enlever les noyaux puis retirer soigneusement la chair que vous couperez en petits dés. Les arroser de jus de citron ainsi que les écorces vidées. Couper en tronçons les cœurs de palmiers et effeuiller la chair de crabe. Laver la laitue et en ciseler le cœur en fines lanières. Préparer la mayonnaise en utilisant du jus de citron au lieu de vinaigre et y incorporer la crème fouettée. Saler et poivrer. Au moment de servir, tapisser les écorces des demi-avocats avec des lanières de laitue et les remplir avec la chair de l''avocat mélangée aux tronçons de cœurs de palmiers, au crabe et â la mayonnaise additionnée de crème. Garnir de cerneaux de noix et Servir bien frais.', 904, NULL);
