INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (571, 'Pommes soufflées', NULL, 20, 25, 0, 0, 'Préparation : Laver les pommes et les essuyer. Les couper en deux et les évider sans abîmer la peau. Epépiner la chair, la couper en morceaux et la faire cuire dans une casserole, à couvert, avec un filet d''eau. Quand la compote est cuite, ajouter la cannelle et le sucre en poudre. Travailler la compote à la cuillère en bois, jusqu''à ce qu''elle forme une purée lisse. Si elle reste trop liquide, la faire évaporer sur le feu en tournant. Fouetter les blancs d''œufs en neige très ferme avec le sel. Les incorporer délicatement, avec une spatule en bois, à la purée de pommes. Remplir les demi-pommes avec cette préparation. Les ranger dans un plat allant au four, enduit de beurre et mettre 10 minutes à four très chaud. Servir immédiatement.', 758, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (572, 'Pomme à la normande', NULL, 10, 30, 0, 0, 'Préparation : Laver les pommes, les peler, les couper en 4 et enlever les pépins. Si les pommes sont très grosses, les découper en 6. Beurrer un plat allant au four. Disposer les pommes dans ce plat (le côté bombé à l''extérieur). Saupoudrer de sucre fin. Faire cuire à four moyen (thermostat 5), pendant 30 minutes. Servir tiède, napper les pommes de crème fraîche fouettée très fermement.', 857, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (573, 'Pommes meringuées', NULL, 15, 20, 0, 0, 'Préparation : Eplucher les pommes, les couper en 4, enlever les pépins. Faire cuire les pommes avec le sucre, la cannelle et le petit jus de citron, environ 10 minutes. Les pommes ne doivent pas « tomber » en compote, mais fondre en gardant une certaine fermeté. Beurrer légèrement un plat à gratin. Etaler les morceaux de pommes cuites en une couche assez tassée sur elle-même. Faire monter en neige les blancs d''œufs, bien fermement. Verser la neige sur les pommes. Faire quelques dessins avec les pointes d''une fourchette. Faire dorer au grill, pendant 10 minutes.', 358, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (574, 'Pommes au four', NULL, 10, 15, 0, 0, 'Préparation : Entailler la peau des pommes sur toute leur circonférence, aux 2/3 de leur hauteur environ. Couper 6 belles rondelles d''orange en conservant l''écorce et les ranger dans un plat allant au four. Poser une pomme sur chaque rondelle et mettre à four doux (thermostat 4-5 environ). Presser le jus des morceaux d''orange restants. Le sucrer à volonté en mélangeant bien. Dès que les pommes commencent à s''ouvrir, les arroser de jus d''orange. Faire cuire jusqu''à ce que les pommes soient tendres, en arrosant souvent. Saupoudrer abondamment de sucre et servir.', 331, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (575, 'Poires surprise', NULL, 0, 0, 0, 0, 'Préparation : Peler les poires et les couper en morceaux.  Faire fondre le sucre vanillé dans l''eau et y mettre à cuire les poires jusqu''à ce qu''elles deviennent transparentes. Les égoutter et les mettre dans un plat creux ou dans des coupes individuelles.  Laisser refroidir.  Dans une petite casserole, faire fondre, à feu doux, le chocolat avec le café et le sucre. Quand le mélange est onctueux, napper les fruits de façon à les enrober complètement. Saupoudrer avec les amandes effilées.  Servir aussitôt. ', 980, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (576, 'Omelette soufflée sibérienne', NULL, 10, 20, 0, 0, 'Préparation : Mélanger les jaunes d''oeufs avec le sucre et le sucre vanillé, jusqu''à ce que la préparation blanchisse.  Battre les blancs d''oeufs en neige ferme avec le sel et les incorporer délicatement au mélange précédent en soulevant la pâte. Dans un grand plat rectangulaire à gratin, en faïence, porcelaine ou verre à feu, faire chauffer le beurre et, lorsqu''il commence à grésiller, verser rapidement dessus la masse mousseuse et faites cuire environ 15 à 20 minutes à four doux.  Saupoudrer rapidement de sucre glace tamisé et remettre au four plus chaud pour dorer la surface. Retirer le plat du four et, le plus rapidement possible, insérer une palette à mi-hauteur de la masse et soulever celle-ci pour y insérer des cuillerées de crème glacée très dure au centre et sur toute la longueur du plat.  Recouvrir celle-ci avec la partie d''omelette soulevée et servez immédiatement. ', 1079, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (577, 'Omelette soufflée à la confiture', NULL, 10, 5, 0, 0, 'Préparation : Séparer les blancs d''oeufs des jaunes et battre ceux-ci avec les sucres.  Fouetter les blancs en neige avec un peu de sel.  Amalgamer délicatement les jaunes et les blancs, sans casser ceux-ci et en soulevant la masse. Faire chauffer la rampe du gril du four.  Chauffer le beurre dans une grande poêle et verser la mousse d''oeufs.  Laissez cuire quelques secondes à feu vif puis1 à 2 minutes à petit feu. Eparpiller la confiture sur la surface des oeufs et passer immédiatement la poêle dans le four, sous la rampe et à chaleur pas trop forte pour que la mousse puisse prendre sans brûler, 3 à 4 minutes au plus.  Sortir la poêle du four, glisser l''omelette sur un plat et porter immédiatement à table. ', 1184, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (581, 'Poires Belle-Hélène', NULL, 10, 0, 0, 0, 'Préparation : Faire fondre le chocolat à feu très doux avec un filet d''eau. Garnir les coupes de service avec deux ou trois boules de glace. Couvrir avec une ou deux demi-poires. Quand le chocolat fondu est bien lisse, arroser avec les coupes de glace. Décorer avec une pincée d''amandes. Servir immédiatement.', 1099, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (582, 'Poires au beaujolais', NULL, 20, 55, 0, 0, 'Préparation : Faire cuire le vin avec le sucre et les épices, pendant 10 minutes. Ajouter les poires entières, épluchées, mais en conservant leur queue. Laisser cuire à petit feu, pendant 15 minutes. Laisser refroidir. Déposer les poires (queues en l''air) dans un plat à gratin, sans les serrer les unes contre les autres. Verser le jus. Les poires doivent être à moitié recouvertes de jus. Au besoin ajouter un peu d''eau. Laisser cuire à four doux (thermostat 4/5), pendant 30 minutes. Servir chaudes ou froides avec un bol de crème fraîche fouettée et bien ferme. Pour « bonifier » le dessert, le préparer la veille, laisser les poires macérer dans le jus de cuisson. Si vous servez ces poires froides, faire couler par-dessus un peu de chocolat fondu et chaud.', 427, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (583, 'Fraises melba', NULL, 5, 0, 0, 0, 'Préparation : Remplir des coupes individuelles de boules de glace à la vanille. Recouvrir de fraises égouttées et coupées en 2. Faire bouillir de la gelée de groseilles avec un filet d''eau et arroser les fraises. Garnir de crème fraîche battue en chantilly et saupoudrer d''amandes. Servir immédiatement.', 827, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (584, 'Pêches melba', NULL, 5, 0, 0, 0, 'Préparation : Remplir des coupes individuelles de boules de glace à la vanille. Recouvrir d''une ou de deux demi-pêches égouttées. Faire bouillir de la gelée de groseilles avec un filet d''eau et arroser les pêches. Garnir de crème fraîche battue en chantilly et saupoudrer d''amandes. Servir immédiatement.', 897, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (587, 'Parfait glacé à la purée de pêches', NULL, 0, 0, 0, 0, 'Préparation : Laver les pêches et les dénoyauter.  Les passer en purée. Ajouter à cette purée le sucre et quand celui-ci est bien fondu, la crème légèrement battue. Mettre en moule ou en sorbetière et faire glacer.', 992, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (588, 'Parfait glacé à la purée de fraises', NULL, 15, 0, 0, 0, 'Préparation : Laver les fraises, les équeuter. Les passer en purée. Ajouter à cette purée le sucre et quand celui-ci est bien fondu, la crème légèrement battue. Mettre en moule ou en sorbetière et faire glacer. Les parfaits se préparent sans cuisson.', 974, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (589, 'Parfait glacé à la purée de framboises', NULL, 20, 0, 0, 0, 'Préparation : Laver les fruits choisis, les équeuter ou les dénoyauter selon le cas. Les passer en purée. Ajouter à cette purée le sucre et quand celui-ci est bien fondu, la crème légèrement battue. Mettre en moule ou en sorbetière et faire glacer. Les parfaits se préparent sans cuisson.', 1024, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (590, 'Parfait glacé au chocolat', NULL, 15, 0, 0, 0, 'Préparation : Faire fondre le sucre et le chocolat fondu dans le lait à froid puis y incorporer en battant au fouet, 1/3 de crème, celle-ci empêchant la cristallisation des particules d''eau. Mettre à glacer au freezer ou en sorbetière, pendant un minimum de 4 heures, jusqu''au moment de servir. Répartir le parfait dans des coupes préalablement mises à refroidir et garnir avec le reste de crème fouettée avec le sucre vanillé. Parsemer de noisettes grillées', 1576, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (591, 'Parfait glacé aux mandarines', NULL, 10, 0, 0, 0, 'Préparation : Faire fondre le sucre dans le jus de fruits à froid puis y incorporer en battant au fouet, les 3/4 de la crème, celle-ci empêchant la cristallisation des particules d''eau. Mettre à glacer au freezer ou en sorbetière, pendant un minimum de 4 heures, jusqu''au moment de servir. Répartir le parfait dans des coupes préalablement mises à refroidir et garnir avec le reste de crème, fouettée avec le sucre vanillé.', 1239, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (592, 'Parfait glacé au jus de fruits', NULL, 5, 0, 0, 0, 'Préparation : Mélanger le sucre avec le jus de fruits et le laisser fondre complètement. Ajouter alors la crème liquide, mélanger bien et faire prendre au frais, dans le congélateur. La crème empêche la cristallisation des particules d''eau. Si les jus de fruits ont un goût très fort, comme le citron par exemple, les couper d''une partie d''eau. Les parfaits se préparent sans cuisson.', 1009, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (594, 'Pamplemousses grillés', NULL, 10, 10, 0, 0, 'Préparation : Laver soigneusement les pamplemousses. Couper les fruits en deux avec un petit couteau à découper, à lame très fine et bien aiguisée. Saupoudrer largement de sucre fin. Placer les fruits dans un plat, allant au four, bien beurré. Déposer le plat dans un four très chaud (thermostat 8), pendant 10 à 12 minutes, selon la grosseur du fruit. Les pamplemousses sont « à point » lorsqu''ils sont recouverts d''une fine croûte dorée. Les décorer d''une cerise confite, les arroser d''un peu de jus et servir chaud.  ', 667, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (595, 'Melons farcis aux fruits', NULL, 15, 0, 0, 0, 'Préparation : Equeuter et laver les fraises. Egoutter et les sécher. Les mettre dans un bol, les arroser de jus de citron, les saupoudrer de sucre, mélanger délicatement et laisser macérer. Couper les melons en deux et enlever les graines, mais non la chair. Répartir les fraises au centre de chaque melon et napper de crème fouettée sucrée. Servir frais.', 464, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (598, 'Macédoine de fruits flambée', NULL, 25, 0, 0, 0, 'Préparation : Faire tremper les raisins secs dans rhum, eau et sucre. Couper les pommes en quartiers, les peler, éliminer pépins et cœurs et les diviser en petites tranches verticales. Les mettre dans un saladier et arroser de la moitié du jus de citron. Procéder de même pour la poire. Eplucher les oranges à vif, en coupant l''écorce à même la chair avec un couteau tranchant, avec lequel vous enlèverez ensuite les segments d''orange entre les peaux. Mettre le tout dans le saladier. Y ajouter les raisins frais, pelés et épépinés, puis les bananes coupées en rondelles et arrosées du jus de citron restant, le sucre, les raisins secs et leur macération. Bien mélanger. Au moment de servir, chauffer le rhum avec le sucre, sans laisser bouillir, le verser dans une louche chauffée, y mettre le feu et verser flambant sur les fruits. Garnir d''amandes et noix. Accompagner d''un ravier de crème fouettée sucrée.', 785, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (599, 'îles flottantes', NULL, 10, 15, 20, 0, 'Préparation : Verser le lait dans une casserole, y ajouter le bâton de vanille coupé en deux et amener à ébullition. Battre les blancs d''œufs en neige ferme, avec la moitié du sucre. Avec une cuillère à soupe, les déposer, par petits tas, dans le lait bouillant. Après 30 à 40 secondes de cuisson, les enlever à l''écumoire. Mélanger les jaunes d''œufs avec le reste du sucre et verser dessus quelques cuillerées de lait chaud. Incorporer les jaunes d''œufs sucrés au restant de lait, dont vous aurez ôté la vanille, et faire épaissir en évitant l''ébullition. Verser cette crème dans un plat creux et la laisser tiédir. Disposer alors dessus les petits monticules de blancs d''œufs et mettre au frais.', 526, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (600, 'Mousse aux bananes', NULL, 10, 10, 30, 0, 'Préparation : Ecraser les bananes en purée. Les verser dans une petite casserole et ajouter les jaunes des œufs et le sucre. Mélanger bien, puis faire cuire à feu doux, sans cesser de tourner, avec une cuillère en bois, jusqu''à ce que le mélange commence à épaissir. Retirer du feu et laisser refroidir. Battre les blancs d''œufs en neige très fermes et les incorporer délicatement, en soulevant le mélange, aux bananes. Verser dans des coupes individuelles et mettre au réfrigérateur pendant 30 minutes environ. Servir décoré d''un biscuit ou d''une petite meringue.', 713, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (601, 'Mousse au café', NULL, 10, 0, 120, 0, 'Préparation : Dans le grand bol du mixer, verser le fromage blanc, le café soluble non délayé et le sucre. Fouetter énergiquement. Battre les blancs d''œufs en neige bien ferme. L''ajouter au fromage blanc. Verser le mélange dans des coupes individuelles. Les garnir d''un grain de café et déposer 2 heures au réfrigérateur.', 685, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (602, 'Mousse au chocolat', NULL, 15, 10, 180, 0, 'Préparation : Faire fondre le chocolat avec un filet d''eau, dans une casserole, à feu très doux ou au bain-marie. Laisser tiédir. Ajouter le beurre en parcelles et tourner,jusqu''à ce que la sauce soit lisse. Ajouter les jaunes des œufs, un à un, en tournant bien. Battre les blancs des œufs en neige très ferme, avec une pincée de sel. Battre la crème fraîche en chantilly, avec un peu de sucre. Incorporer d''abord la crème chantilly au mélange œufs chocolat, puis les œufs en neige, délicatement, en soulevant le mélange avec une spatule en bois. Verser dans des coupes individuelles, ou dans un plat de service et mettre au réfrigérateur. Servir frais, garni d''un biscuit.', 1765, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (603, 'Mousse au citron', NULL, 20, 5, 120, 0, 'Préparation : Frotter les morceaux de sucre à l''écorce d''un citron pour bien le parfumer. Presser les citrons et mettre le jus dans une casserole. Faire fondre les morceaux de sucre dans le jus. Mélanger 1/3 du sucre aux  jaunes d''œufs. Ajouter le jus de citron parfumé et sucré. Battre la crème de lait (elle doit devenir bien épaisse et ferme). Ajouter le reste du sucre fin. Verser la crème dans la casserole. Mélanger le tout pour en faire une crème bien homogène. Verser dans des coupes individuelles. Garnir de cerises confites. Servir immédiatement ou placer les coupes dans le réfrigérateur, mais pas plus de 2 heures.', 940, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (604, 'Mousse aux fraises', NULL, 15, 0, 120, 0, 'Préparation : Equeuter, laver les fraises et les égoutter. Les passer en purée, y ajouter le jus pressé du citron, le sucre et bien mélanger. Battre les blancs d''œufs en neige avec une pincée de sel. Fouetter la crème. Incorporer à la purée de fraises, les œufs en neige et crème fouettée, doucement et sans trop mélanger, puis verser le tout dans un grand moule ou des moules individuels, préalablement humectés d''eau froide. Mettre au frais.', 596, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (605, 'Mousse de fraises au yaourt', NULL, 10, 0, 120, 0, 'Préparation : Laver les fraises, les équeuter et les essuyer délicatement avec du papier de cuisine. Les déposer dans le bol du mixer avec le yaourt, le sucre et la crème fraîche. Passer le tout au mixer, pendant 30 secondes. Verser dans des petites coupes individuelles. Déposer au réfrigérateur, pendant au moins 2 heures. Servir avec des langues de chat', 469, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (607, 'Mousse glacée aux framboises', NULL, 10, 5, 180, 0, 'Préparation : Equeuter et laver les framboises, en réserver pour la décoration et passer le reste à l''étamine, pour obtenir du jus de framboise. Mettre ce jus dans une petite casserole, avec le sucre et faire fondre ce dernier sur feu doux, puis donnez un ou deux bouillons. Hors du feu, incorporer les jaunes d''œufs, remettre sur le feu, sans bouillir, pour épaissir le sirop. Laisser refroidir. Fouetter la crème avec la pincée de sel et l''incorporer, en battant bien à la préparation précédente. Ajouter enfin les blancs d''œufs battus en neige ferme, en soulevant la pâte pour ne pas enlever aux blancs d''œufs leur légèreté. Verser dans un moule passé à l''eau froide et faire prendre au congélateur pendant quelques heures, jusqu''à durcissement. Démouler et servir immédiatement après avoir décoré avec les framboises réservées.', 672, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (608, 'Mousse de groseilles vertes', NULL, 10, 10, 0, 0, 'Préparation : Laver et équeuter les groseilles, puis les plonger 2 minutes dans de l''eau bouillante. Les égoutter. Mettre les groseilles dans le tamis ou le moulin pour les passer et faire cuire la purée obtenue et le jus avec le sucre, pendant 10 minutes, en tournant. Laisser refroidir. Battre les blancs d''œufs en neige, avec le sel, et les incorporer à la purée de fruits. Mettre au frais.', 679, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (609, 'Clafoutis aux prunes', NULL, 25, 60, 0, 0, 'Préparation : Mettre la farine dans un grand bol et la mélanger avec le sel et le sucre. Creuser un trou au centre, y casser les œufs et y incorporer la farine, en alternant avec le lait, jusqu''à ce que vous obteniez une pâte à crêpes épaisse. Terminer en ajoutant la levure et mélanger bien. Laver les prunes, les couper en deux et les dénoyauter. Beurrer un plat à gratin. Y verser un peu de pâte et passer le plat immédiatement au four très chaud, préchauffé, pour que cette mince couche durcisse. Poser dessus les demi-prunes et le reste de la pâte. Faire cuire environ 50 minutes à four moyen.  ', 667, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (610, 'Clafoutis aux cerises', NULL, 20, 50, 0, 0, 'Préparation : Laver les cerises, les équeuter et, éventuellement, les dénoyauter. Mélanger la farine avec sucre et sel, y incorporer les œufs entiers battus, mouiller avec le lait et la crème. Prener un moule à bords élevés, et le mettre 5 minutes dans le four très chaud. Pendant ce temps, mélanger les fruits avec la pâte liquide et verser le tout dans le moule chaud, retiré du four. Faire cuire 45 minutes à four moyen, en couvrant d''un papier la surface du clafouti s''il a tendance à trop dorer. Les cerises non dénoyautées donnent un meilleur goût à la préparation, mais la dégustation en est moins aisée.  ', 543, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (611, 'Chocolat glacé', NULL, 15, 0, 0, 0, 'Préparation : Faire fondre le chocolat à croquer dans une petite casserole, avec un filet d''eau. Tourner jusqu''à ce qu''il soit lisse. Il doit être assez liquide: ajouter de l''eau au besoin. Verser un peu de sauce au chocolat dans le fond de chaque coupe. Les remplir à moitié de glace au chocolat. Arroser d''un peu de sauce au chocolat, puis terminer de remplir les coupes. Arroser à nouveau de glace au chocolat. Surmonter de crème fraîche fouettée en chantilly. Décorer avec des copeaux de chocolat. Servir immédiatement: la glace fond très vite, à cause du chocolat chaud.  ', 1406, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (612, 'Chocolatine', NULL, 25, 10, 20, 0, 'Préparation : Préparer la génoise en incorporant à la farine le cacao en poudre. Faire cuire le gâteau dans un moule à gâteau. Laisser refroidir. Pendant ce temps, préparer la garniture. Faire fondre le chocolat, sans lui ajouter d''eau, dans une casserole au bain-marie. Amener la crème à ébullition et la verser sur le chocolat fondu. Délayer bien le mélange. Laisser tiédir quelques instants. Ajouter la moitié du rhum et le beurre en parcelles, fouetter jusqu''à complet refroidissement. La préparation doit être épaisse. Au besoin, la mettre quelque temps au réfrigérateur pour la raffermir. Couper la génoise en deux. Mélanger le sucre et le reste de rhum avec un peu d''eau. Y tremper les deux parties du gâteau. Poser une moitié du gâteau sur le plat de service. Le recouvrir avec la moitié de la crème au chocolat. Poser dessus la seconde partie du gâteau. Tartiner entièrement avec le reste de crème. Lisser bien la circonférence de la génoise et saupoudrer le dessus avec le chocolat au lait râpé au couteau ou simplement avec une râpe à gros trous. Mettre au réfrigérateur. Servir très frais.  ', 1949, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (614, 'Charlotte glacée aux fraises', NULL, 15, 0, 300, 0, 'Préparation : Passer les fraises au mixer avec le jus du citron et du sucre. Vous devez obtenir une purée assez liquide. Ajouter la crème fraîche et bien mélanger. Goûter et rajouter du sucre au besoin. Verser dans une sorbetière et faire prendre au réfrigérateur (dans le freezer) ou au congélateur. Tapisser un moule à charlotte avec des biscuits à la cuillère rapidement trempés dans de l''eau sucrée. Quand la glace est prise, sans avoir trop durci, la verser dans le moule à charlotte et couvrir avec une dernière rangée de biscuits. Poser par dessus une petite assiette avec un poids et remettre au froid, jusqu''à durcissement complet. Avant de servir, démouler la charlotte sur un plat, en trempant le moule quelques instants dans l''eau chaude, puis en le retournant sur le plat de service. Servir tel quel, ou décorer avec de la crème chantilly.  ', 1136, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (615, 'Charlotte aux bananes', NULL, 20, 5, 300, 0, 'Préparation : Eplucher les bananes. Citronner les bananes pour qu''elles ne noircissent pas. Ecraser les bananes et les laisser cuire quelques instants seulement, avec du sucre. Garnir un moule à charlotte légèrement beurré ou huilé, avec les biscuits à la cuillère. Battre la moitié de la crème en chantilly très ferme et l''incorporer à la compote refroidie. Verser le mélange dans le moule à charlotte, recouvrir d''une dernière rangée de biscuits. Poser une petite assiette avec un poids sur le moule et mettre au réfrigérateur, pendant 5 à 6 heures. Avant de servir, démouler la charlotte, en trempant le moule quelques instants dans de l''eau chaude, puis en le retournant sur le plat de service. Décorer avec le reste de la crème battue en chantilly avec un peu de sucre. Utiliser une poche à douille munie d''une douille cannelée.  ', 931, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (616, 'Charlotte aux pommes', NULL, 25, 20, 300, 0, 'Préparation : Eplucher les pommes, les couper en quatre et ôter les pépins. Les couper en grosses lamelles. Faire fondre les pommes à feu doux, dans une casserole avec le beurre, le sucre et la gousse de vanille. Commencer la cuisson à couvert, pendant 5 minutes environ, puis terminer à découvert, jusqu''à ce que les pommes soient tout à fait réduites en compote. Oter la gousse de vanille. Garnir un moule à charlotte légèrement beurré ou huilé, avec les biscuits à la cuillère. Battre la moitié de la crème en chantilly très ferme et l''incorporer à la compote refroidie. Verser le mélange dans le moule à charlotte, recouvrir d''une dernière rangée de biscuits. Poser une petite assiette avec un poids sur le moule et mettre au réfrigérateur, pendant 5 à 6 heures. Avant de servir, démouler la charlotte, en trempant le moule quelques instants dans de l''eau chaude, puis en le retournant sur le plat de service. Décorer avec le reste de la crème battue en chantilly avec un peu de sucre. Utiliser une poche à douille munie d''une douille cannelée.  ', 835, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (620, 'Charlotte aux amandes', NULL, 20, 0, 240, 0, 'Préparation : Fouetter la moité de crème en chantilly. Mélanger le sucre et le beurre ramolli, jusqu''à ce que le sucre soit incorporé. Ajouter les amandes et le rhum. Tapisser le fond et les parois d''un moule à charlotte, légèrement beurré, avec les biscuits bien serrés. Incorporer la crème chantilly au mélange aux amandes et verser dans le moule préparé. Frapper le moule sur la table pour bien tasser. Mettre au freezer ou au congélateur, pendant 4 heures au moins. Avant de servir, démouler la charlotte, en trempant le moule rapidement dans de l''eau chaude, puis en le retournant sur le plat de service. Battre le reste de crème fraîche en chantilly, avec le sucre vanillé, vider dans une poche à douille cannelée et décorer le gâteau. Servir très frais.  ', 2058, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (621, 'Cerises flambées au kirsch', NULL, 30, 15, 0, 0, 'Préparation : Nettoyer les cerises, les dénoyauter. Faire un sirop avec le sucre et un peu d''eau, le laisser cuire quelques minutes. Faire chauffer les cerises avec une partie du sirop, à couvert sur feu très doux. Verser les cerises dans une poêle, saupoudrer de cannelle et faire chauffer en remuant sans arrêt, jusqu''à ce qu''il ne reste plus de sirop. Faire chauffer le kirsch et le flamber, verser sur les cerises. Servir avec des gâteaux secs.  ', 389, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (623, 'Granité de café au chocolat', NULL, 20, 15, 0, 0, 'Préparation : Râper le chocolat ou le passer à la moulinette. Le mettre dans le café, en casserole, avec le sucre et le sucre vanillé et le laisser fondre, sans jamais atteindre l''ébullition. Retirer du feu, ajouter le rhum et faire refroidir. Verser alors le liquide dans un récipient, à 3 ou 4 cm de hauteur, et mettre à glacer au freezer au moins 2 h. Ranger dans le réfrigérateur les verres ou coupes de service. Laver et ébouillanter le zeste d''orange et le couper en fine julienne. Faire fondre le sucre dans le jus d''orange, porter à ébullition et, quand le sirop commence à s''épaissir un peu, y jeter les zestes et les laisser confire 4 à 5 minutes. Les retirer pour qu''ils refroidissent. Lorsque la glace a bien pris (elle a un aspect grenelé qui justifie son appellation de granité), la racler vivement avec une fourchette et remplir de ces paillettes un verre sorti du réfrigérateur. Le remettre au frais et procéder ainsi pour les autres verres. Garnir des zestes d''orange et du sirop refroidi et servir aussitôt.  ', 509, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (625, 'Glace au pamplemousse', NULL, 10, 0, 0, 0, 'Préparation : Mélanger le fromage, la crème fraîche et le jus de pamplemousse. Battre les blancs d''œufs en neige très ferme. Ajouter le sucre aux blancs d''œufs et les ajouter au fromage, très délicatement. Mettre le tout dans une sorbetière et laisser prendre la glace pendant 45 minutes environ. Servir dans des coupes bien fraîches.  ', 688, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (626, 'Glace au caramel', NULL, 10, 10, 0, 0, 'Préparation : Faire la crème anglaise. Ajouter simplement au lait bouilli du caramel préparé avec 250 g de sucre humecté d''eau et chauffé, jusqu''à ce qu''il blondisse. Ajouter à la crème terminée la crème fraîche encore liquide et battre énergiquement au fouet pour alléger le mélange. Verser dans une sorbetière et faire prendre au congélateur. Servir tel quel ou en coupes plus élaborées. Si vous n''avez pas de sorbetière, battre le mélange à 3 reprises à 20 minutes d''intervalle.  ', 980, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (627, 'Glace au chocolat', NULL, 10, 10, 0, 0, 'Préparation : Faire la crème anglaise. Ajouter simplement au lait bouilli du chocolat fondu. Ajouter à la crème terminée la crème fraîche encore liquide et battre énergiquement au fouet pour alléger le mélange. Verser dans une sorbetière et faire prendre au congélateur. Servir tel quel ou en coupes plus élaborées. Si vous n''avez pas de sorbetière, battre le mélange à 3 reprises à 20 minutes d''intervalle.  ', 1085, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (628, 'Glace au café', NULL, 10, 10, 0, 0, 'Préparation : Faire tiédir ensemble le lait et la crème. Dans une casserole, mélanger les jaunes d''œufs, le sucre et le sucre vanillé. Verser lentement le lait tiède sur les œufs et le sucre, en remuant sans cesse. Faire cuire à petit feu, en remuant constamment, mais couper la source de chaleur au premier frémissement. Laisser refroidir. Ajouter le café fort, bien mélanger puis verser le liquide dans la sorbetière. Servir en boules dans des coupes individuelles et garnir chaque boule avec un grain de café entier.  ', 834, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (629, 'Gâteau aux noix', NULL, 25, 55, 0, 0, 'Préparation : Mélanger la farine avec le sel et la levure. Tourner le beurre en crème, y ajouter le sucre et, quand ce dernier est fondu et que le mélange est blanchâtre, les jaunes des œufs puis le mélange farine et levure, les noix hachées et, pour terminer, les blancs des œufs battus en neige. Verser cette pâte dans un moule à manqué, beurré et fariné, et faire cuire à four modéré pendant 55 minutes environ. Démouler le gâteau, le laisser refroidir puis le couper en deux, horizontalement. Pour la crème : Faire fondre, dans une casserole placée dans un bain-marie, le chocolat en morceaux, avec le café et en y ajoutant le sucre glace, jusqu''à obtenir une pâte homogène. Hors du feu, incorporer le jaune d''œuf, les noix hachées et le beurre. Laisser refroidir. Tartiner de cette crème la base du gâteau et reformer celui-ci. Faire fondre la confiture d''abricots, la passer au broyeur et en enrober le gâteau. Parsemer de noix hachées et de quelques cerneaux entiers.  ', 2156, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (631, 'Gâteau au chocolat', NULL, 20, 55, 0, 0, 'Préparation : Battre les blancs en neige avec un peu de sel. Dans une petite casserole placée dans un bain-marie, faire fondre le chocolat coupé en morceau, mouillé d''une cuillerée de lait. Sortir le récipient du bain-marie et y ajouter immédiatement les jaunes d''œufs, le beurre, sucre en poudre et sucre vanillé, puis la farine, la poudre d''amandes et, pour terminer, les blancs d''œufs battus en neige. Beurrer et fariner un moule à manqué, y verser la pâte et laisser cuire environ 55 minutes à four modéré. Démouler le gâteau et le laisser refroidir complètement. Pour la crème : Faire fondre le chocolat dans le café ; y ajouter le sucre glace, le beurre et la marmelade. Tourner sur feu doux jusqu''à épaississement et laisser refroidir. Couper le gâteau en deux, horizontalement, et le tartiner de la crème précédente. Reformer le gâteau et le garnir de rosaces de crème fouettée sucrée et de cerneaux de noix.  ', 2001, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (641, 'Crêpes fourrées aux fraises', NULL, 10, 15, 0, 60, 'Préparation : Préparer les crêpes: tamiser la farine au-dessus du lait froid et battre au fouet. Ajouter les œufs entiers, sel, sucre, beurre fondu et liqueur. Laisser reposer. Laver et équeuter les fraises. Les écraser en purée et les mélanger avec le sucre et 1 cuillère à soupe de jus de citron. Mettre au frais. Faire cuire les crêpes et les garder au chaud. Les tartiner de purée de fraises, poser dessus une portion de crème glacée et plier les crêpes en quatre. Les disposer sur un plat en métal chauffé, les parsemer de sucre puis arroser de l''alcool chauffé. Faire flamber.', 822, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (634, 'Fromage blanc aux fruits secs', NULL, 5, 0, 15, 60, 'Préparation : 1 heure à l''avance, faire macérer les raisins secs dans le rhum. Dans une terrine, fouetter énergiquement le fromage blanc et le lait, pendant quelques instants. Ensuite, incorporer les raisins, les amandes, le rhum et le sucre. Bien mélanger, puis verser dans de petites coupes individuelles. Mettre au réfrigérateur. Servir très frais.  ', 1053, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (636, 'Diplomate aux bananes', NULL, 30, 45, 0, 10, 'Préparation : Faire macérer les raisins avec 1/3 du rhum. Eplucher les bananes, les détailler en rondelles et les arroser avec 1/3 du rhum et le jus de citron. Faire bouillir le lait avec la gousse de vanille. Laisser infuser jusqu''à ce que le lait soit tiède. Enlever la gousse. Mélanger un œuf entier, deux jaunes, le sucre et la fécule. Travailler jusqu''à ce que le tout soit lisse. Ensuite, verser le lait tiède par dessus, en battant au fouet. Beurrer légèrement un plat rond (20 cm de diamètre environ), à bords hauts. En tapisser le fond et les parois de biscuits à la cuillère. Mettre au fond une couche de rondelles de bananes, répartir une cuillère à soupe de raisins de Corinthe, arroser avec 4 cuillères à soupe du mélange œufs-lait. Recommencer jusqu''à ce que le moule soit rempli. Terminer par une couche de biscuits. Faire bouillir un grand récipient d''eau dans lequel vous pourrez immerger le moule aux 3/4. Le laisser refroidir quelques minutes avant d''y déposer le plat rempli. Mettre à four modéré (thermostat 4-5), pendant 45 mn environ. Pendant ce temps, réduire la confiture d''abricots en purée et l''allonger du reste de rhum. Quand le gâteau est cuit, le démouler sur le plat de service. Découper la banane qui reste en rondelles et en garnir le sommet du gâteau. Terminer en enduisant le diplomate de purée d''abricots. Servir tiède ou froid.', 766, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (637, 'Crêpes suzette', NULL, 20, 20, 0, 120, 'Préparation : Préparer les crêpes à l''avance. Ajouter simplement une cuillère à soupe de liqueur d''orange à la pâte. Deux heures au moins avant de servir, laver, en frottant bien, l''écorce de l''orange et la râper. Incorporer le zeste râpé au beurre ramolli. Réchauffer les crêpes une à une, dans une poêle où vous ferez fondre, avant chacune d''elle, une noisette de beurre à l''orange. Réserver les crêpes au chaud, en les déposant au fur et à mesure sur une assiette chaude, à l''entrée du four. Les sucrer légèrement. Quand elles sont toutes réchauffées, les plier en quatre et les remettre dans la poêle. Les arroser avec les alcools mélangés au jus de l''orange pressée. Saupoudrer de sucre et faire chauffer quelques instants. Enflammer l''alcool avec une allumette et présenter à table en train de flamber. Servir aussitôt. On peut également chauffer le mélange d''alcool, de liqueur et de jus d''orange, dans une petite casserole. L''enflammer et le verser devant vos convives sur les crêpes chaudes.', 959, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (638, 'Crêpes sucrées', NULL, 20, 10, 0, 120, 'Préparation : Mettre la farine, les oeufs et le sel dans une terrine. Mélanger jusqu''à ce que la pâte soit lisse. Délayer, petit à petit, avec le lait froid. Ajouter l''huile ou le beurre fondu, le sucre et le sucre vanillé et laisser reposer 2 heures environ. Faire cuire les crêpes à feu vif, à la poêle graissée avec un peu de beurre. Verser juste assez de pâte pour en couvrir le fond. Dès que la crêpe commence à dorer d''un côté, la retourner avec un spatule en bois. Servir les crêpes au fur et à mesure, ou les faire à l''avance et les réserver au chaud, couvertes d''une feuille d''aluminium, jusqu''au moment de servir. Les crêpes peuvent être simplement saupoudrées de sucre, couvertes de sucre et arrosées de jus de citron; arrosées de crème fraîche, de chocolat fondu chaud, ou simplement de beurre, fourrées de toutes les variétés de confitures, de rondelles de fruits, de glace, flambées au rhum, au kirsch, au Grand-Marnier, etc.', 852, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (640, 'Crêpes mikado', NULL, 20, 20, 0, 120, 'Préparation : Préparer la pâte en tamisant farine, sel et sucre dans le lait. Mélanger au fouet, pour obtenir une pâte onctueuse. Y ajouter, les œufs entiers, le beurre fondu et la liqueur. Laisser reposer au moins 2 h. Faire cuire les crêpes dans une poêle, la pâte étant très grasse il ne faut pratiquement pas de beurre pour la cuisson ou très peu. Garder les crêpes au chaud, dans du papier aluminium, à four très doux. Faire fondre le chocolat avec la crème et les chauffer sans laisser bouillir. Au moment de servir, sortir du freezer la crème glacée qui doit être très dure et en mettre une grande cuillerée au centre de chaque crêpe. Plier les crêpes en quatre et les napper d''un peu de crème au chocolat. Servir immédiatement. Si les crêpes ont refroidi, les réchauffer rapidement dans la poêle ou dans le four.', 1415, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (643, 'Crème renversée au caramel', NULL, 15, 10, 60, 0, 'Préparation : Préparer le caramel directement dans le moule (style moule à charlotte, d''une contenance de 1 lit. environ), dans lequel la crème prendra. Faire fondre le sucre humecté d''eau et de quelques gouttes de jus de citron, jusqu''à ce qu''il prenne la couleur désirée. Arrêter l''ébullition en ajoutant 1 cuillère à café d''eau froide. Enduire les parois du moule de caramel, en le faisant tourner en tous sens. Quand le caramel est uniformément réparti, tremper le moule dans l''eau froide, pour le durcir. Faire bouillir le lait avec la gousse de vanille et le sucre. Battre les œufs en omelette dans une terrine et y verser le lait, légèrement tiédi, en tournant sans arrêt. Verser le mélange dans le moule et mettre à four doux au bain-marie, dans un plat rempli d''eau. Ni l''eau, ni la crème ne doivent bouillir. La crème est cuite quand une lame de couteau qu''on y enfonce ressort propre. Faire refroidir et mettre au réfrigérateur. Avant de servir, démouler en trempant le moule quelques instants dans de l''eau très chaude, puis en le retournant sur le plat de service.', 640, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (644, 'Crème patissière', NULL, 10, 10, 0, 0, 'Préparation : Faire bouillir le lait avec la vanille fendue. Dans une casserole à fond épais, mélanger jaunes d''œufs, œuf entier, farine, sucre, sel et beurre ramolli. Verser dessus, peu à peu, le lait vanillé bouillant, en remuant à la cuillère en bois et faire chauffer sur feu doux, sans cesser de remuer, jusqu''à ébullition. Bien surveiller la cuisson de la crème qui doit avoir épaissi avant d''atteindre l''ébullition. Celle-ci doit immédiatement être interrompue pour éviter la coagulation des œufs. Retirer la casserole du feu aux premiers bouillons. Transvaser cette crème dans une terrine et la remuer de temps à autre jusqu''à refroidissement complet, ceci pour éviter la formation de peaux. Cette crème peut être aromatisée avec une liqueur au choix, de la cannelle peut remplacer la vanille ou encore du gingembre.', 960, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (646, 'Crème glacée à la vanille', NULL, 10, 10, 120, 0, 'Préparation : Faire bouillir le lait avec le bâton de vanille coupé en deux. Mélanger les jaunes d''œufs avec sel et sucre et verser dessus, peu à peu, le lait vanillé très chaud. Bien remuer. Mettre la préparation dans une casserole, sur feu doux, et laisser épaissir la crème, sans atteindre l''ébullition. Verser cette crème dans un moule ou des bacs à glacer, la laisser refroidir puis la mettre à prendre au congélateur. Dès que la glace commence à se former, y ajouter la crème fouettée bien ferme et remettre à glacer. Cette recette constitue la base des crèmes glacées auxquelles on ajoute le parfum de son choix. Si vous possédez une sorbetière, suivre les consignes données par le fabricant.', 1016, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (647, 'Crème frangipane', NULL, 0, 0, 0, 0, 'Préparation : Préparer la crème pâtissière Piler finement amandes et macarons et les ajouter à la crème bouillante sortant du feu. Laisser refroidir.', 1120, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (648, 'Crème au citron', NULL, 10, 20, 0, 0, 'Préparation : Laver les citrons et les essuyer. Râper le zeste d''un citron. Presser les citron pour en extraire tout leur jus. Le verser dans un grand bol. Dans un poêlon, faire fondre le beurre. Ajouter le sucre et le jus de citron. Bien mélanger. Amener à ébullition. Dans un autre bol, battre les œufs entiers en omelette. Verser le jus bouillant sur les œufs battus. Passer au mixer (avec les fouets) pour rendre le mélange bien homogène. Remettre le tout dans la casserole, avec le zeste de citron. Amener à ébullition, en remuant sans arrêt. Couper la source de chaleur dès le premier bouillon et verser dans des coupes individuelles. Laisser refroidir, mais ne pas déposer les coupelles dans le réfrigérateur.', 1340, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (649, 'Crème de bananes', NULL, 15, 0, 0, 0, 'Préparation : Eplucher les bananes et les écraser en purée fine. Ajouter le sucre et le jus des citrons. Incorporer les petits suisses en mélangeant au fouet, jusqu''à ce que l''amalgame soit parfait. Battre la crème fraîche en chantilly et l''ajouter à la préparation Verser dans des coupes individuelles et mettre au réfrigérateur, Avant de servir, s''il vous reste une banane, la couper en rondelles et en décorer les coupes. Servir très frais.', 787, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (650, 'Crème anglaise custard', NULL, 10, 15, 0, 0, 'Préparation : Faire bouillir le lait avec la vanille, le retirer du feu, laisser infuser 5 minutes puis enlever la vanille. Battre les jaunes d''œufs avec l''œuf entier, sel, sucre, pour former une mousse homogène. Verser dessus un peu de lait chaud, en tournant vivement avec le fouet, puis mettre cette préparation dans le reste du lait en continuant à fouetter le mélange. Porter sur feu doux et, avec une cuillère en bois, laisser épaissir, en ajoutant la fécule délayée dans très peu de lait. Enlever la casserole du feu avant que l''ébullition ne se produise, et laisser refroidir en continuant de tourner avec la cuillère en bois pour empêcher la formation de croûtes à la surface.', 520, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (651, 'Crème anglaise', NULL, 10, 15, 0, 0, 'Préparation : Faire bouillir le lait avec la gousse de vanille. Mélanger les jaunes des œufs, le sucre et la fécule, jusqu''à ce que le sucre soit fondu. Oter la gousse de vanille et verser le lait bouillant, petit à petit, en ne cessant pas de tourner avec un fouet. Reverser dans la casserole et faire épaissir, à feu très doux, sans laisser bouillir, en tournant sans arrêt. Verser dans des petits coupes individuelles, recouvrir d''un petit morceau de papier aluminium beurré, pour éviter la formation d''une peau en surface. Mettre au réfrigérateur après refroidissement. Servir très frais, décoré d''un biscuit ou d''une cuillère de confiture.', 543, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (652, 'Couronne de semoule aux fruits', NULL, 10, 15, 0, 0, 'Préparation : Dans une casserole, mettre le lait, la vanille, la crème, le zeste de citron et la semoule. Pour éviter que la semoule ne soit grumeleuse: il faut la verser en pluie en tournant sans arrêt. En fin de cuisson, ajouter le sucre et laisser épaissir la semoule qui doit être très épaisse. Battre les blancs en neige ferme, incorporer l''alcool à la semoule puis les blancs battus. Beurrer légèrement un moule à couronne, y verser la préparation et mettre au frais. Lorsque le tout est refroidi, retourner la préparation sur un plat. Remplir le centre de cerises. Décorer de crème chantilly. Ce dessert peut se faire également avec des fraises ou des framboises.', 597, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (653, 'Biscuits fondants', NULL, 10, 20, 0, 0, NULL, 2290, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (654, 'Biscuits à la crème et aux fruits', NULL, 20, 0, 0, 10, 'Préparation : Préparer la crème et la laisser refroidir. Tremper les biscuits dans le mélange vin et liqueur et en poser une couche dans le fond d''une grande coupe. Les napper ensuite de la confiture de fraises liquéfiée à la chaleur, mais refroidie. Fouetter la crème avec 1/3 du sucre. Laver et équeuter les fruits, laisser-les égoutter, puis en réserver quelques uns pour la décoration. Les faire macérer dans le reste du sucre. Disposer les framboises sur la couche de biscuits, couvrir de la moitié de la crème anglaise, remettre une couche de biscuits imbibée de confiture fondue, les fraises et le reste de la crème anglaise. Terminer par la crème fouettée. Décorer en saupoudrant les amandes effilées et en alternant fraises et framboises réservées, préalablement macérées dans le sucre.', 807, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (655, 'Bavarois aux framboises', NULL, 15, 20, 180, 0, NULL, 787, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (656, 'Bananes à la martiniquaise', NULL, 10, 5, 0, 0, NULL, 764, NULL);
