INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (659, 'Bananes flambées', NULL, 10, 10, 0, 0, NULL, 682, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (661, 'Alaska aux framboises', NULL, 20, 4, 0, 0, 'Préparation : Sortez les fruits surgelés du freezer, ou du congélateur si vous n''utilisez pas des framboises fraiches. Faites cuire la pâte à génoise dans un moule de 18 cm de diamètre environ, beurré et fariné. Laissez-la refroidir complètement, puis creusez-en le centre pour pouvoir y mettre le bloc de glace. Déposez-la sur une platine. Battez les blancs des œufs en neige très ferme, avec une pincée de sel et le jus de citron. Incorporez ensuite la moitié du sucre, délicatement, sans briser les œufs. Remplissez le puit creusé dans la génoise avec la glace à peine sortie du freezer. Recouvrez la glace avec les framboises dégelées ou fraiches. Saupoudrez avec le reste du sucre. Recouvrez le gâteau entièrement de blancs d''œufs battus en neige. Saupoudrez de sucre glace. Déposez la platine avec le gâteau sur une planchette de bois, puis mettez au four chauffé au maximum, jusqu''à ce que la meringue prenne couleur. Servez aussitôt.', 1091, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (664, 'Chair à saucisse', NULL, 0, 0, 0, 0, 'Préparation : Mélanger soigneusement les viandes hachées. Peut être utilisée pour farcir des légumes, des viandes ou faire des saucisses.', 1939, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (666, 'Pâte sablée pour tartes', NULL, 20, 20, 0, 0, 'Préparation : Faire chauffer le beurre dans une petite casserole, sur feu doux et ajouter le sucre. Mélanger avec la cuillère en bois, jusqu''à ce qu''il soit fondu mais sans le laisser prendre couleur. Déposer la farine sur la table ou planche farinée, creuser une fontaine au centre et y mettre le jaune d''oeuf battu avec le sel, puis le beurre sucré refroidi. Mélanger et travailler vivement du bout des doigts pour obtenir une pâte souple mais friable. Etaler cette pâte au rouleau et foncer un moule beurré, si possible avec fond amovible. Couvrir le fond de pâte avec un disque de papier sulfurisé et poser dessus des haricots secs qui empêcheront la pâte de se boursoufler. Faire cuire 20 minutes à four modéré plutôt doux, la pâte doit rester blonde. Pour confectionner des biscuits sablés, rouler la pâte en boudins plus ou moins minces, selon la dimension voulue des sablés, et couper en rondelles de 1 cm. Cuisson: 10 minutes.', 2369, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (668, 'Pâte à frire sucrée', NULL, 15, 5, 0, 120, 'Préparation : Mettre le lait dans une petite casserole et laisser fondre le beurre sans trop chauffer. Hors du feu, ajouter le sucre et le sel. Verser le liquide tiédi au centre de la farine que vous y incorporerez peu à peu. Ajouter le jaune d''oeuf et laisser reposer 1 à 2 heures. Au dernier moment, ajouter la liqueur ou le parfum au choix et la levure. La pâte, tout en étant coulante, doit parfaitement enrober.', 1074, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (673, 'Pâte brisée minute', NULL, 15, 0, 0, 0, 'Préparation : Mélanger et tamiser la farine avec le sel. La verser sur la table ou la planche farinée et poser dessus le beurre fortement ramolli. Travailler de façon à incorporer la farine au beurre et ajouter par cuillère à soupe à la fois, un peu d''eau dans laquelle vous aurez délayé la levure. Pétrir légèrement, vous devez obtenir une pâte assez ferme tout en étant souple. L’abaisser au rouleau immédiatement et foncer le ou les moules. Cette pâte n''a pas besoin de reposer. .', 2293, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (674, 'Pâte brisée parisienne', NULL, 15, 0, 120, 0, 'Préparation : Tamiser la farine avec le sel, la rassembler en tas et verser dessus le beurre divisé en parcelles. Incorporer le beurre à la farine, en travaillant du bout des doigts, jusqu''à l’obtention d''une texture sableuse, ressemblant à une chapelure grossière. Ajouter à cette pâte le jaune d''oeuf. Ajouter une cuillère à soupe d''eau et pétrir en remettant une ou deux cuillères d''eau si nécessaire, afin d''obtenir une pâte ferme, mais souple. La rouler en boule et la laisser reposer au frais pendant au moins 2 heures.', 2323, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (675, 'Pâte brisée', NULL, 15, 0, 120, 0, 'Préparation : Tamiser la farine avec le sel, la rassembler en tas et verser dessus le beurre divisé en parcelles. Incorporer le beurre à la farine, en travaillant du bout des doigts, jusqu''à l’obtention d''une texture sableuse, ressemblant à une chapelure grossière. Ajouter une cuillère à soupe d''eau et pétrir en remettant une ou deux cuillères d''eau si nécessaire, afin d''obtenir une pâte ferme, mais souple. La rouler en boule et la laisser reposer au frais pendant au moins 2 heures.', 2345, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (678, 'Pâte à beignets sucrée', NULL, 15, 0, 0, 0, 'Préparation : Délayer la levure dans un peu de lait tiédi et verser au centre de la farine tamisée, avec le sucre.  Couvrir la pâte d''un peu de farine et la laisser lever et gonfler. Ajouter au levain les jaunes d''oeufs et suffisamment de lait pour, en y incorporant la farine, obtenir une pâte bien enrobante, pas trop liquide surtout. Laisser reposer et gonfler dans un endroit chaud, ajouter les blancs d''oeufs en neige, en battant bien, et tremper dans cette pâte les aliments que vous ferez frire.', 901, NULL);
INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (679, 'Pâte à beignets salée', NULL, 15, 0, 0, 0, 'Préparation : Délayer la levure dans un peu de lait tiédi et verser au centre de la farine tamisée, avec le sel et le poivre. Couvrir la pâte d''un peu de farine et la laisser lever et gonfler. Ajouter au levain les jaunes d''oeufs et suffisamment de lait pour, en y incorporant la farine, obtenir une pâte bien enrobante, pas trop liquide surtout. Laisser reposer et gonfler dans un endroit chaud, ajouter les blancs d''oeufs en neige, en battant bien, et tremper dans cette pâte les aliments que vous ferez frire.', 833, NULL);
