INSERT INTO recette (rec_id, rec_nom, rec_note, rec_tmp_preparation, rec_tmp_cuisson, rec_tmp_refrigeration, rec_tmp_maceration, rec_preparation, rec_calories, rec_href) VALUES (683, 'Coulis de tomate', NULL, 10, 30, 0, 0, 'Préparation : Hacher finement l''oignon et le faire doucement blondir dans l''huile chauffée. Y ajouter l''ail haché, les tomates préalablement pelées, épépinées et coupées en morceaux, le sucre, la marjolaine et assaisonner. Laisser cuire à feu doux, jusqu''à ce que vous obteniez une purée homogène. Servir ce coulis tel quel ou passé.', 234, NULL);
